@RequestMapping(method = RequestMethod.POST)
public String download(
    ModelMap model,
    @RequestParam("id") Integer idCV,
    HttpServletResponse response,
    HttpSession session) {
  if (idCV == null) {
    return "acceuil";
  } else {
    CV cv = HECV.get(idCV);

    String cvUrl = cv.getCvUrl();
    String splitUrl[] = cvUrl.split("/");
    String cvName = splitUrl[splitUrl.length - 1];
    String cvPath = session.getServletContext().getRealPath("/") + cvUrl.substring(1);

    response.setContentType("application/octet-stream");
    response.setHeader("Content-Disposition", "attachment;filename=" + cvName);

    File file = new File(cvPath);
    FileInputStream fileIn;
    try {
      fileIn = new FileInputStream(file);
      ServletOutputStream out = response.getOutputStream();

      byte[] outputByte = new byte[4096];

      while (fileIn.read(outputByte, 0, 4096) != -1) {
        out.write(outputByte, 0, 4096);
      }

      fileIn.close();
      out.flush();
      out.close();
    } catch (FileNotFoundException e) {
      e.printStackTrace();
    } catch (IOException e) {
      e.printStackTrace();
    }

    return null;
  }
}

private static void copyFile(File src, File dst) throws IOException {
  InputStream in = new FileInputStream(src);
  OutputStream out = new FileOutputStream(dst);

  // Transfer bytes from in to out
  byte[] buf = new byte[1024];
  int len;
  while ((len = in.read(buf)) > 0) {
    out.write(buf, 0, len);
  }
  in.close();
  out.close();
}

