/**
 * Gets the standard properties class.
 *
 * @return properties.
 *     <p>The default property file is named openemrconnect.properties.
 */
public static Properties getProperties() {
  if (properties == null) {
    properties = new Properties();

    // If a system property defining the configuration directory is
    // available, use it.
    String configDirectory = System.getProperty("configDirectory");
    if (configDirectory == null) {
      configDirectory = runtimeDirectory;
    }

    // First attempt to load from the filesystem, which will only work
    // in a dev environment (i.e. from within the IDE), and for many,
    // only for runtime2 or better.

    // On deployments, the properties file should live in the jar, and
    // thus require loading as a resource (unless the system property
    // for "configDirectory" is supplied).

    String propFileName = "openemrconnect.properties";
    try {
      String propPathName = configDirectory + propFileName;
      Logger.getLogger(Mediator.class.getName())
          .log(Level.INFO, "Attempt property load from file ''{0}''", propPathName);
      try {
        FileInputStream fis = new FileInputStream(propPathName);
        properties.load(fis);
        fis.close();
      } catch (IOException ex) {
        Logger.getLogger(Mediator.class.getName())
            .log(Level.INFO, "Attempt property load as resource ''{0}''", propFileName);
        InputStream in = (Mediator.class.getResourceAsStream(propFileName));
        properties.load(in);
        in.close();
      }
    } catch (Exception ex) {
      Logger.getLogger(Mediator.class.getName())
          .log(
              Level.SEVERE,
              "getProperty() Can''t open ''{0}'' -- Please create the properties file if it doesn''t exist and then restart the app",
              propFileName);
      System.exit(1);
    }
  }
  return properties;
}

String uploadToAs(String deploymentPath) throws IOException {
  ASUploadConnection conn = new ASUploadConnection(DC_HOST, DC_HTTP_PORT, DC_USER, DC_PASS);
  String fileName = new File(deploymentPath).getName();
  OutputStream os = conn.getOutputStream(fileName);

  // URL url = getClass().getClassLoader().getResource(".");
  // System.out.println(url);

  InputStream fis = getClass().getClassLoader().getResourceAsStream(deploymentPath);
  if (fis == null) {
    File inputFile = new File(deploymentPath);
    if (!inputFile.canRead()) {
      throw new FileNotFoundException(
          "Input stream for path ["
              + deploymentPath
              + "] could not be opened - does the file exist either in the test classpath or on the filesystem?");
    }
    fis = new FileInputStream(inputFile);
  }

  StreamUtil.copy(fis, os, false);
  try {
    fis.close();
  } catch (IOException e) {
    // ignore
  }

  JsonNode node = conn.finishUpload();
  // System.out.println(node);
  assert node != null : "No result from upload - node was null";
  assert node.has("outcome") : "No outcome from upload";
  String outcome = node.get("outcome").getTextValue();
  assert outcome.equals("success") : "Upload was no success" + outcome;

  JsonNode resultNode = node.get("result");
  return resultNode.get("BYTES_VALUE").getTextValue();
}

