/**
 * Returns a map of ids to DateTimeZones.
 *
 * @param outputDir optional directory to write compiled data files to
 * @param sources optional list of source files to parse
 */
public Map<String, DateTimeZone> compile(File outputDir, File[] sources) throws IOException {
  if (sources != null) {
    for (int i = 0; i < sources.length; i++) {
      BufferedReader in = new BufferedReader(new FileReader(sources[i]));
      parseDataFile(in);
      in.close();
    }
  }

  if (outputDir != null) {
    if (!outputDir.exists()) {
      throw new IOException("Destination directory doesn't exist: " + outputDir);
    }
    if (!outputDir.isDirectory()) {
      throw new IOException("Destination is not a directory: " + outputDir);
    }
  }

  Map<String, DateTimeZone> map = new TreeMap<String, DateTimeZone>();

  System.out.println("Writing zoneinfo files");
  for (int i = 0; i < iZones.size(); i++) {
    Zone zone = iZones.get(i);
    DateTimeZoneBuilder builder = new DateTimeZoneBuilder();
    zone.addToBuilder(builder, iRuleSets);
    final DateTimeZone original = builder.toDateTimeZone(zone.iName, true);
    DateTimeZone tz = original;
    if (test(tz.getID(), tz)) {
      map.put(tz.getID(), tz);
      if (outputDir != null) {
        if (ZoneInfoCompiler.verbose()) {
          System.out.println("Writing " + tz.getID());
        }
        File file = new File(outputDir, tz.getID());
        if (!file.getParentFile().exists()) {
          file.getParentFile().mkdirs();
        }
        OutputStream out = new FileOutputStream(file);
        try {
          builder.writeTo(zone.iName, out);
        } finally {
          out.close();
        }

        // Test if it can be read back.
        InputStream in = new FileInputStream(file);
        DateTimeZone tz2 = DateTimeZoneBuilder.readFrom(in, tz.getID());
        in.close();

        if (!original.equals(tz2)) {
          System.out.println("*e* Error in " + tz.getID() + ": Didn't read properly from file");
        }
      }
    }
  }

  for (int pass = 0; pass < 2; pass++) {
    for (int i = 0; i < iLinks.size(); i += 2) {
      String id = iLinks.get(i);
      String alias = iLinks.get(i + 1);
      DateTimeZone tz = map.get(id);
      if (tz == null) {
        if (pass > 0) {
          System.out.println(
              "Cannot find time zone '" + id + "' to link alias '" + alias + "' to");
        }
      } else {
        map.put(alias, tz);
      }
    }
  }

  if (outputDir != null) {
    System.out.println("Writing ZoneInfoMap");
    File file = new File(outputDir, "ZoneInfoMap");
    if (!file.getParentFile().exists()) {
      file.getParentFile().mkdirs();
    }

    OutputStream out = new FileOutputStream(file);
    DataOutputStream dout = new DataOutputStream(out);
    try {
      // Sort and filter out any duplicates that match case.
      Map<String, DateTimeZone> zimap =
          new TreeMap<String, DateTimeZone>(String.CASE_INSENSITIVE_ORDER);
      zimap.putAll(map);
      writeZoneInfoMap(dout, zimap);
    } finally {
      dout.close();
    }
  }

  return map;
}

/**
 * Upload a local file to this file on the distributed file system
 *
 * @param monitor
 * @param file
 */
public void upload(IProgressMonitor monitor, File file) {

  final int taskSize = 1024;

  monitor.setTaskName("Upload file " + this.path);

  BufferedInputStream istream = null;
  DataOutputStream ostream = null;

  try {
    istream = new BufferedInputStream(new FileInputStream(file));
    ostream = getDFS().create(this.path);

    int bytes;
    byte[] buffer = new byte[taskSize];

    while ((bytes = istream.read(buffer)) >= 0) {
      if (monitor.isCanceled()) return;
      ostream.write(buffer, 0, bytes);
      monitor.worked(1);
    }

  } catch (Exception e) {
    ErrorMessageDialog.display(
        String.format("Unable to uploade file %s to %s", file, this.path),
        e.getLocalizedMessage());

  } finally {
    try {
      if (istream != null) istream.close();
    } catch (IOException e) {
      e.printStackTrace();
      // nothing we can do here
    }
    try {
      if (ostream != null) ostream.close();
    } catch (IOException e) {
      e.printStackTrace();
      // nothing we can do here
    }
  }
}

/**
 * This method stores a File as a DigitalObject into the specified Storage. The File can be stored
 * as a linked Payload if specified.
 *
 * @param storage a Storage instance
 * @param file the File to store
 * @param linked set true to link to the original file, false to copy
 * @return a DigitalObject
 * @throws StorageException if there was an error storing the file
 */
public static DigitalObject storeFile(Storage storage, File file, boolean linked)
    throws StorageException {
  DigitalObject object = null;
  Payload payload = null;
  String oid = generateOid(file);
  String pid = generatePid(file);
  try {
    try {
      object = getDigitalObject(storage, oid);
      if (linked) {
        try {
          String path = FilenameUtils.separatorsToUnix(file.getAbsolutePath());
          payload = createLinkedPayload(object, pid, path);
        } catch (StorageException se) {
          payload = object.getPayload(pid);
        }
      } else {
        log.debug("Attempting to create or update payload...");
        payload = createOrUpdatePayload(object, pid, new FileInputStream(file));
      }
    } catch (StorageException se) {
      throw se;
    }
  } catch (FileNotFoundException fnfe) {
    throw new StorageException("File not found '" + oid + "'");
  } finally {
    if (payload != null) {
      payload.close();
    }
  }
  return object;
}

