/**
 * Load sound data from a file into a buffer. Returns a handle that is passed to makeSoundSource()
 * to create a sound in the environment. Multiple sound sources can be bound to one data buffer.
 *
 * <p>
 *
 * @param soundFilename name of file to load (.wav)
 * @return numeric handle to the sound data buffer
 */
public static int loadSoundData(String soundFilename) // , int
      // soundDataBufferHandle)
    {
  int soundDataBufferHandle = allocateSoundData();
  if (soundFilename.endsWith(".ogg")) {
    if (haveVorbis) {
      ByteBuffer filebuffer = getData(soundFilename);
      AL10.alBufferData(soundDataBufferHandle, AL10.AL_FORMAT_VORBIS_EXT, filebuffer, -1);
      filebuffer.clear();
    } else {

      try {
        OggDecoder decoder = new OggDecoder();
        FileInputStream fis = new FileInputStream(soundFilename);

        OggData ogg = decoder.getData(fis);

        AL10.alBufferData(
            soundDataBufferHandle,
            ogg.channels > 1 ? AL10.AL_FORMAT_STEREO16 : AL10.AL_FORMAT_MONO16,
            ogg.data,
            ogg.rate);

        fis.close();
      } catch (Exception e) {
        e.printStackTrace();
      }
    }
  } else {
    // load wave data into buffer (manire de l'auteur mais j'y crois plus)
    // WaveData wavefile = WaveData.create(soundFilename);

    // FAIT MAISON BEURK PAS BEAU

    File fin = new File(soundFilename);
    AudioInputStream aud = null;

    try {
      aud = AudioSystem.getAudioInputStream(fin);
    } catch (UnsupportedAudioFileException e) {
      System.out.println("Format non supporte");
      e.printStackTrace();
    } catch (IOException e) {
      e.printStackTrace();
    }
    WaveData wavefile = WaveData.create(aud);

    AL10.alBufferData(soundDataBufferHandle, wavefile.format, wavefile.data, wavefile.samplerate);
    wavefile.dispose();
  }
  if ((lastError = AL10.alGetError()) != AL10.AL_NO_ERROR) {
    exit(lastError);
  }
  return soundDataBufferHandle;
}

