private void convertResultsFile(String folderPath, String h2Name) {
  String resultsFile = folderPath + "results";
  BufferedReader brResult = null;
  try {
    Connection conn = getConnection(dbNameSource);
    brResult = new BufferedReader(new InputStreamReader(new FileInputStream(resultsFile)));
    String data = null;
    String[] split;
    int position = 1;
    PreparedStatement prepItem = conn.prepareStatement(sqlPrepInsertItem);
    PreparedStatement prepCategory = conn.prepareStatement(sqlPrepInsertCategory);
    PreparedStatement prepRelationship = conn.prepareStatement(sqlPrepInsertRelationship);
    while ((data = brResult.readLine()) != null) {
      try {
        data = data.trim();
        split = data.split(";");
        int queryID = parseID(split[0]);
        int itemID = Integer.parseInt(split[1]);
        String title = split[2];
        // for Dominos#39;s
        if (title.equals("Dominos#39")) {
          title = "Dominos#39;s";
          String city = split[4];
          String state = split[5];
          double latitude = Double.parseDouble(split[6]);
          double longitude = Double.parseDouble(split[7]);
          double distance = Double.parseDouble(split[8]);
          List<Category> categories = new ArrayList<Category>();
          for (int i = 9; i < split.length; i = i + 2) {
            // prepare category
            Category category = new Category(Integer.parseInt(split[i]), split[i + 1]);
            categories.add(category);

            setPrepCategory(itemID, category, prepCategory);
            prepCategory.addBatch();
          }
          APOI result =
              new APOI(
                  itemID, title, city, state, longitude, latitude, null, distance, categories, 0);

          setPrepItem(result, prepItem);
          prepItem.addBatch();
        } else {
          String city = split[3];
          String state = split[4];
          double latitude = Double.parseDouble(split[5]);
          double longitude = Double.parseDouble(split[6]);
          double distance = Double.parseDouble(split[7]);
          List<Category> categories = new ArrayList<Category>();
          for (int i = 8; i < split.length; i = i + 2) {
            // prepare category
            Category category = new Category(Integer.parseInt(split[i]), split[i + 1]);
            categories.add(category);

            setPrepCategory(itemID, category, prepCategory);
            prepCategory.addBatch();
          }
          APOI result =
              new APOI(
                  itemID, title, city, state, longitude, latitude, null, distance, categories, 0);
          setPrepItem(result, prepItem);
          prepItem.addBatch();
        }
        //
        setPrepRelationship(itemID, queryID, position++, prepRelationship);
        prepRelationship.addBatch();
      } catch (Exception e) {
        System.out.println(data);
      }
    }
    // execute prepare statements...
    prepCategory.executeBatch();
    prepItem.executeBatch();
    prepRelationship.executeBatch();

    brResult.close();
  } catch (FileNotFoundException e) {
    e.printStackTrace();
  } catch (IOException e) {
    e.printStackTrace();
  } catch (SQLException e1) {
    e1.printStackTrace();
  }
}

/**
 * locateClassFile returns a DataInputStream with mark/reset capability that can be used to read
 * the requested class file. The CLASSPATH is used to locate the class.
 *
 * @param classFileName The name of the class to locate. The class name should be given in
 *     fully-qualified form, for example:
 *     <pre>
 * java.lang.Object
 * java.io.DataInputStream
 * </pre>
 *
 * @exception java.io.FileNotFoundException The requested class file could not be found.
 * @exception java.io.IOException The requested class file could not be opened.
 */
public static DataInputStream locateClassFile(String classFileName)
    throws FileNotFoundException, IOException {

  boolean notFound = true;
  StringTokenizer st;
  String path = "";
  String pathNameForm;
  File cf = null;
  NamedDataInputStream result;

  st = new StringTokenizer(classPath, pathSeparator, false);
  pathNameForm = classFileName.replace('.', File.separatorChar) + ".class";

  while (st.hasMoreTokens() && notFound) {

    try {
      path = st.nextToken();
    } catch (NoSuchElementException nse) {
      break;
    }
    int pLen = path.length();
    String pathLast4 = pLen > 3 ? path.substring(pLen - 4) : "";
    if (pathLast4.equalsIgnoreCase(".zip") || pathLast4.equalsIgnoreCase(".jar")) {

      try {

        result = locateInZipFile(path, classFileName, true, true);
        if (result == null) continue;
        return (DataInputStream) result;

      } catch (ZipException zfe) {
        continue;
      } catch (IOException ioe) {
        continue;
      }

    } else {
      try {
        cf = new File(path + File.separator + pathNameForm);
      } catch (NullPointerException npe) {
        continue;
      }
      if ((cf != null) && cf.exists()) notFound = false;
    }
  }

  if (notFound) {

    /* Make one last attempt to find the file in the current
     * directory
     */

    int lastdot = classFileName.lastIndexOf('.');
    String simpleName = (lastdot >= 0) ? classFileName.substring(lastdot + 1) : classFileName;

    result =
        new NamedDataInputStream(
            new BufferedInputStream(new FileInputStream(simpleName + ".class")),
            simpleName + ".class",
            false);
    return (DataInputStream) result;
  }

  result =
      new NamedDataInputStream(
          new BufferedInputStream(new FileInputStream(cf)),
          path + File.separator + pathNameForm,
          false);
  return (DataInputStream) result;
}

