public static void main(String[] args) {
  String dbname;
  Properties props = new Properties();
  Properties fileprops = new Properties();
  boolean dotransactions = true;
  int threadcount = 1;
  int target = 0;
  boolean status = false;
  String label = "";

  // parse arguments
  int argindex = 0;

  if (args.length == 0) {
    usageMessage();
    System.exit(0);
  }

  while (args[argindex].startsWith("-")) {
    if (args[argindex].compareTo("-threads") == 0) {
      argindex++;
      if (argindex >= args.length) {
        usageMessage();
        System.exit(0);
      }
      int tcount = Integer.parseInt(args[argindex]);
      props.setProperty("threadcount", tcount + "");
      argindex++;
    } else if (args[argindex].compareTo("-target") == 0) {
      argindex++;
      if (argindex >= args.length) {
        usageMessage();
        System.exit(0);
      }
      int ttarget = Integer.parseInt(args[argindex]);
      props.setProperty("target", ttarget + "");
      argindex++;
    } else if (args[argindex].compareTo("-load") == 0) {
      dotransactions = false;
      argindex++;
    } else if (args[argindex].compareTo("-t") == 0) {
      dotransactions = true;
      argindex++;
    } else if (args[argindex].compareTo("-s") == 0) {
      status = true;
      argindex++;
    } else if (args[argindex].compareTo("-db") == 0) {
      argindex++;
      if (argindex >= args.length) {
        usageMessage();
        System.exit(0);
      }
      props.setProperty("db", args[argindex]);
      argindex++;
    } else if (args[argindex].compareTo("-l") == 0) {
      argindex++;
      if (argindex >= args.length) {
        usageMessage();
        System.exit(0);
      }
      label = args[argindex];
      argindex++;
    } else if (args[argindex].compareTo("-P") == 0) {
      argindex++;
      if (argindex >= args.length) {
        usageMessage();
        System.exit(0);
      }
      String propfile = args[argindex];
      argindex++;

      Properties myfileprops = new Properties();
      try {
        myfileprops.load(new FileInputStream(propfile));
      } catch (IOException e) {
        System.out.println(e.getMessage());
        System.exit(0);
      }

      // Issue #5 - remove call to stringPropertyNames to make compilable under Java 1.5
      for (Enumeration<?> e = myfileprops.propertyNames(); e.hasMoreElements(); ) {
        String prop = (String) e.nextElement();

        fileprops.setProperty(prop, myfileprops.getProperty(prop));
      }

    } else if (args[argindex].compareTo("-p") == 0) {
      argindex++;
      if (argindex >= args.length) {
        usageMessage();
        System.exit(0);
      }
      int eq = args[argindex].indexOf('=');
      if (eq < 0) {
        usageMessage();
        System.exit(0);
      }

      String name = args[argindex].substring(0, eq);
      String value = args[argindex].substring(eq + 1);
      props.put(name, value);
      // System.out.println("["+name+"]=["+value+"]");
      argindex++;
    } else {
      System.out.println("Unknown option " + args[argindex]);
      usageMessage();
      System.exit(0);
    }

    if (argindex >= args.length) {
      break;
    }
  }

  if (argindex != args.length) {
    usageMessage();
    System.exit(0);
  }

  // set up logging
  // BasicConfigurator.configure();

  // overwrite file properties with properties from the command line

  // Issue #5 - remove call to stringPropertyNames to make compilable under Java 1.5
  for (Enumeration<?> e = props.propertyNames(); e.hasMoreElements(); ) {
    String prop = (String) e.nextElement();

    fileprops.setProperty(prop, props.getProperty(prop));
  }

  props = fileprops;

  if (!checkRequiredProperties(props)) {
    System.exit(0);
  }

  // get number of threads, target and db
  threadcount = Integer.parseInt(props.getProperty("threadcount", "1"));
  dbname = props.getProperty("db", "com.yahoo.ycsb.BasicDB");
  target = Integer.parseInt(props.getProperty("target", "0"));

  // compute the target throughput
  double targetperthreadperms = -1;
  if (target > 0) {
    double targetperthread = ((double) target) / ((double) threadcount);
    targetperthreadperms = targetperthread / 1000.0;
  }

  System.out.println("YCSB Client 0.1");
  System.out.print("Command line:");
  for (int i = 0; i < args.length; i++) {
    System.out.print(" " + args[i]);
  }
  System.out.println();
  System.err.println("Loading workload...");

  // show a warning message that creating the workload is taking a while
  // but only do so if it is taking longer than 2 seconds
  // (showing the message right away if the setup wasn't taking very long was confusing people)
  Thread warningthread =
      new Thread() {
        public void run() {
          try {
            sleep(2000);
          } catch (InterruptedException e) {
            return;
          }
          System.err.println(" (might take a few minutes for large data sets)");
        }
      };

  warningthread.start();

  // set up measurements
  Measurements.setProperties(props);

  // load the workload
  ClassLoader classLoader = Client.class.getClassLoader();

  Workload workload = null;

  try {
    Class<?> workloadclass = classLoader.loadClass(props.getProperty(WORKLOAD_PROPERTY));

    workload = (Workload) workloadclass.newInstance();
  } catch (Exception e) {
    e.printStackTrace();
    e.printStackTrace(System.out);
    System.exit(0);
  }

  try {
    workload.init(props);
  } catch (WorkloadException e) {
    e.printStackTrace();
    e.printStackTrace(System.out);
    System.exit(0);
  }

  warningthread.interrupt();

  // run the workload

  System.err.println("Starting test.");

  int opcount;
  if (dotransactions) {
    opcount = Integer.parseInt(props.getProperty(OPERATION_COUNT_PROPERTY, "0"));
  } else {
    if (props.containsKey(INSERT_COUNT_PROPERTY)) {
      opcount = Integer.parseInt(props.getProperty(INSERT_COUNT_PROPERTY, "0"));
    } else {
      opcount = Integer.parseInt(props.getProperty(RECORD_COUNT_PROPERTY, "0"));
    }
  }

  Vector<Thread> threads = new Vector<Thread>();

  for (int threadid = 0; threadid < threadcount; threadid++) {
    DB db = null;
    try {
      db = DBFactory.newDB(dbname, props);
    } catch (UnknownDBException e) {
      System.out.println("Unknown DB " + dbname);
      System.exit(0);
    }

    Thread t =
        new ClientThread1(
            db,
            dotransactions,
            workload,
            threadid,
            threadcount,
            props,
            opcount / threadcount,
            targetperthreadperms);

    threads.add(t);
  }

  StatusThread1 statusthread = null;

  if (status) {
    boolean standardstatus = false;
    if (props.getProperty("measurementtype", "").compareTo("timeseries") == 0) {
      standardstatus = true;
    }
    statusthread = new StatusThread1(threads, label, standardstatus);
    statusthread.start();
  }

  long st = System.currentTimeMillis();

  for (Thread t : threads) {
    t.start();
  }

  for (Thread t : threads) {
    try {
      t.join();
    } catch (InterruptedException e) {
    }
  }

  long en = System.currentTimeMillis();

  if (status) {
    statusthread.interrupt();
  }

  try {
    workload.cleanup();
  } catch (WorkloadException e) {
    e.printStackTrace();
    e.printStackTrace(System.out);
    System.exit(0);
  }

  try {
    exportMeasurements(props, opcount, en - st);
  } catch (IOException e) {
    System.err.println("Could not export measurements, error: " + e.getMessage());
    e.printStackTrace();
    System.exit(-1);
  }

  System.exit(0);
}

public void run(String arg) {
  OpenDialog od = new OpenDialog("Open BMP...", arg);
  String directory = od.getDirectory();
  String name = od.getFileName();
  if (name == null) return;
  String path = directory + name;

  // IJ.showStatus("Opening: " + path);
  BMPDecoder bmp = new BMPDecoder();
  FileInputStream is = null;
  try {
    is = new FileInputStream(path);
    bmp.read(is);
  } catch (Exception e) {
    String msg = e.getMessage();
    if (msg == null || msg.equals("")) msg = "" + e;
    IJ.error("BMP Decoder", msg);
    return;
  } finally {
    if (is != null) {
      try {
        is.close();
      } catch (IOException e) {
      }
    }
  }

  MemoryImageSource mis = bmp.makeImageSource();
  if (mis == null) IJ.write("mis=null");
  Image img = Toolkit.getDefaultToolkit().createImage(mis);
  FileInfo fi = new FileInfo();
  fi.fileFormat = FileInfo.BMP;
  fi.fileName = name;
  fi.directory = directory;
  setImage(img);
  setTitle(name);
  setFileInfo(fi);
  if (bmp.topDown) getProcessor().flipVertical();
  if (arg.equals("")) show();
}

public static String readStringFile(Context context, String filename, Boolean external) {
  String content = "";
  try {
    File file;
    FileInputStream fin;
    if (external) {
      file = new File(context.getExternalFilesDir(null), filename);
      fin = new FileInputStream(file);
    } else {
      file = new File(filename);
      fin = context.openFileInput(filename);
    }
    BufferedInputStream bin = new BufferedInputStream(fin);
    byte[] contentBytes = new byte[1024];
    int bytesRead = 0;
    StringBuffer contentBuffer = new StringBuffer();

    while ((bytesRead = bin.read(contentBytes)) != -1) {
      content = new String(contentBytes, 0, bytesRead);
      contentBuffer.append(content);
    }
    content = contentBuffer.toString();
    fin.close();
  } catch (FileNotFoundException e) {
    Log.e("READ ERROR", "FILE NOT FOUND " + filename);
  } catch (IOException e) {
    Log.e("READ ERROR", "I/O ERROR");
  }
  return content;
}

/** Calls winipcfg and parses the result to find servers and a search path. */
private static void find95() {
  String s = "winipcfg.out";
  try {
    Process p;
    p = Runtime.getRuntime().exec("winipcfg /all /batch " + s);
    p.waitFor();
    File f = new File(s);
    findWin(new FileInputStream(f));
    new File(s).delete();
  } catch (Exception e) {
    return;
  }
}

public void createNewJavaFile() {
  String packageName = null;

  if (getActiveWindow() != null) {
    packageName = getActiveWindow().getPackage();
  }
  if (packageName == null) {
    packageName = "mypackage";
  }

  EditWindow editWindow = new EditWindow(repositoryManager, this, robotsDirectory);

  String templateName = "templates" + File.separatorChar + "newjavafile.tpt";

  String template = "";

  File f = new File(FileUtil.getCwd(), templateName);
  int size = (int) (f.length());
  byte buff[] = new byte[size];

  FileInputStream fis = null;
  DataInputStream dis = null;

  try {
    fis = new FileInputStream(f);
    dis = new DataInputStream(fis);

    dis.readFully(buff);
    template = new String(buff);
  } catch (IOException e) {
    template =
        "Unable to read template file: " + FileUtil.getCwd() + File.separatorChar + templateName;
  } finally {
    FileUtil.cleanupStream(fis);
    FileUtil.cleanupStream(dis);
  }

  String name = "MyClass";

  int index = template.indexOf("$");

  while (index >= 0) {
    if (template.substring(index, index + 10).equals("$CLASSNAME")) {
      template = template.substring(0, index) + name + template.substring(index + 10);
      index += name.length();
    } else if (template.substring(index, index + 8).equals("$PACKAGE")) {
      template = template.substring(0, index) + packageName + template.substring(index + 8);
      index += packageName.length();
    } else {
      index++;
    }
    index = template.indexOf("$", index);
  }

  EditorPane editorPane = editWindow.getEditorPane();

  editorPane.setText(template);
  editorPane.setCaretPosition(0);

  addPlaceShowFocus(editWindow);
}

