        // Se sono diversi incremento la variabile contatore
        if (!newStrTemp.equals(oldStrTemp)) {
          count++;
        }
      }
      oldfileTemp.close();
      newfileTemp.close();

      /*Se sono sicura che sono diversi creo il percorso nella cartella master e
       *creo il file che contiene la differenza rispetto il file della nuova versione
       */
      if (count != 0) {
        String name = CreateDir.getName(fileName);
        String diff = master + subPath;
        File f1 = new File(diff);
        f1.mkdirs();
        File fileFirst = new File(master + subPath + "/" + name);
        fileFirst.createNewFile();

        BufferedReader oldfile = new BufferedReader(new FileReader(path));
        BufferedReader newfile = new BufferedReader(new FileReader(newPath + "/" + fileName));
        BufferedWriter outfile =
            new BufferedWriter(new FileWriter(master + subPath + "/" + name));
        String oldStr;
        String newStr;
        while ((newStr = newfile.readLine()) != null) {
          oldStr = oldfile.readLine();
          if (!newStr.equals(oldStr)) {
            count++;
            try {
              // Scrivo il nuovo file
              outfile.write(newStr);
              outfile.write("\n");

            } catch (IOException e) {
            }
          }
        }
        oldfile.close();
        newfile.close();
        outfile.close();

        if (count == 0) {
          File file = new File(master + subPath + "/" + name);
          file.delete();
        }
      }

      File allDir = new File(master + subPath);
      allDir.delete();

    } catch (IOException e) {
      // Exceptions ignored.
    }
    File MastDir = new File(master);
    String[] lista = MastDir.list();
    for (int j = 0; j < lista.length; j++) {
      File temp = new File(master + "/" + lista[j]);
      temp.delete();
    }
  } else {
    // Viene eseguito nel caso in cui il file  presente solo nella nuova versione
    String diff = master + subPath;
    File f1 = new File(diff);
    f1.mkdirs();
    File inputFile = new File(home + subPath + "/" + fileName);
    File outputFile = new File(diff + "/" + fileName);
    InputStream finput;
    finput = new BufferedInputStream(new FileInputStream(inputFile));
    OutputStream foutput;
    foutput = new BufferedOutputStream(new FileOutputStream(outputFile));
    byte[] buffer = new byte[1024 * 500];
    int bytes_letti = 0;
    while ((bytes_letti = finput.read(buffer)) > 0) foutput.write(buffer, 0, bytes_letti);
    finput.close();
    foutput.close();
  }
  File allDir = new File(master + subPath);
  allDir.delete();
}

/**
 * Make it easy to target the local database. Data source properties are searched in the following
 * order, with the first match winning:
 *
 * <ul>
 *   <li>A Java system property named "sakai.properties" which will be used to load a properties
 *       file containing property names such as "url@javax.sql.BaseDataSource".
 *   <li>Input configProperties named "dbDriver", "dbURL", "dbUser", and "dbPass".
 * </ul>
 *
 * (Side note: this configuration logic would be easy to externalize with Spring.)
 *
 * @param configProperties
 */
private DriverAdapterCPDS getDataSource(Properties configProperties)
    throws ClassNotFoundException {
  String dbDriver = null;
  String dbUrl = null;
  String dbUser = null;
  String dbPassword = null;
  String sakaiPropertiesPath = System.getProperty("sakai.properties");
  if (sakaiPropertiesPath != null) {
    File sakaiPropertiesFile = new File(sakaiPropertiesPath);
    if (sakaiPropertiesFile.exists()) {
      FileInputStream sakaiPropertiesInput = null;
      try {
        sakaiPropertiesInput = new FileInputStream(sakaiPropertiesFile);
        Properties sakaiProperties = new Properties();
        sakaiProperties.load(sakaiPropertiesInput);
        sakaiPropertiesInput.close();
        dbDriver = sakaiProperties.getProperty("driverClassName@javax.sql.BaseDataSource");
        dbUrl = sakaiProperties.getProperty("url@javax.sql.BaseDataSource");
        dbUser = sakaiProperties.getProperty("username@javax.sql.BaseDataSource");
        dbPassword = sakaiProperties.getProperty("password@javax.sql.BaseDataSource");
      } catch (IOException e) {
        log.info("Error loading properties from " + sakaiPropertiesFile.getAbsolutePath());
      } finally {
        if (sakaiPropertiesInput != null) {
          try {
            sakaiPropertiesInput.close();
          } catch (IOException e) {
          }
        }
      }
    }
  }
  if (dbDriver == null) dbDriver = configProperties.getProperty("dbDriver");
  if (dbUrl == null) dbUrl = configProperties.getProperty("dbURL");
  if (dbUser == null) dbUser = configProperties.getProperty("dbUser");
  if (dbPassword == null) dbPassword = configProperties.getProperty("dbPass");

  DriverAdapterCPDS cpds = new DriverAdapterCPDS();
  try {
    cpds.setDriver(dbDriver);
  } catch (ClassNotFoundException e) {
    throw new ClassNotFoundException();
  }
  cpds.setUrl(dbUrl);
  cpds.setUser(dbUser);
  cpds.setPassword(dbPassword);
  return cpds;
}

