/**
 * Reads the given file, translating {@link IOException} to a {@link RuntimeException} of some
 * sort.
 *
 * @param file non-null; the file to read
 * @return non-null; contents of the file
 */
public static byte[] readFile(File file) {
  if (!file.exists()) {
    throw new RuntimeException(file + ": file not found");
  }

  if (!file.isFile()) {
    throw new RuntimeException(file + ": not a file");
  }

  if (!file.canRead()) {
    throw new RuntimeException(file + ": file not readable");
  }

  long longLength = file.length();
  int length = (int) longLength;
  if (length != longLength) {
    throw new RuntimeException(file + ": file too long");
  }

  byte[] result = new byte[length];

  try {
    FileInputStream in = new FileInputStream(file);
    int at = 0;
    while (length > 0) {
      int amt = in.read(result, at, length);
      if (amt == -1) {
        throw new RuntimeException(file + ": unexpected EOF");
      }
      at += amt;
      length -= amt;
    }
    in.close();
  } catch (IOException ex) {
    throw new RuntimeException(file + ": trouble reading", ex);
  }

  return result;
}

/**
 * Recursive method call
 *
 * @param cl
 * @param searchRealPath
 * @param fileNames
 * @throws Exception
 */
private Properties _load(
    boolean buildFileInfos, ClassLoader cl, boolean searchRealPath, String... fileNames)
    throws Exception {

  Properties propRet = new Properties();
  InputStream fis = null;

  for (String fn : fileNames) {
    URL url = cl.getResource(fn);

    File f = null;

    if (url != null) {
      f = new File(url.getFile());
    } else if (searchRealPath) {
      f = new File(fn);
      if (!(f.exists() && f.isFile())) {
        f = null;
      }
    }

    if (f != null) {
      // we found a file
      Properties propCur = new Properties();

      try {
        fis = new FileInputStream(f);
        propCur.load(fis);
      } finally {
        CleanupUtil.release(fis);
        fis = null;
      }

      if (buildFileInfos) {
        if (fileInfos == null) {
          fileInfos = new ArrayList<FileInfo>();
        }
        FileInfo fi = new FileInfo(f);
        fi.setLastModified(f.lastModified());
        fileInfos.add(fi);
      }

      //
      // check imports
      //
      boolean srp;
      String srpStr = propCur.getProperty("import.search_real_path");
      srp = StringUtils.equalsIgnoreCase(srpStr, "true");

      String[] files = null;
      String flStr = propCur.getProperty("import.files");
      if (flStr != null) {
        files = flStr.split("\\s*,\\s*");
      }

      if (!CollectionUtil.isEmpty(files)) {
        Properties propImport = _load(buildFileInfos, cl, srp, files);
        // once done,
        propRet.putAll(propImport);
      }

      propRet.putAll(propCur);

      //
      // now check sub dir
      //
      checkIncludeHome(propRet, f);
      // once done, the include_home_subdir will be removed
    }
  }

  return propRet;
}

/**
 * Force content to be loaded into mem
 *
 * @throws XMLDBException
 */
protected byte[] getData() throws XMLDBException {
  if (file != null) {
    if (!file.canRead())
      throw new XMLDBException(
          ErrorCodes.INVALID_RESOURCE,
          "failed to read resource content from file " + file.getAbsolutePath());
    try {
      final byte[] chunk = new byte[512];
      final ByteArrayOutputStream out = new ByteArrayOutputStream();
      final FileInputStream in = new FileInputStream(file);
      int l;
      do {
        l = in.read(chunk);
        if (l > 0) out.write(chunk, 0, l);

      } while (l > -1);
      in.close();
      final byte[] data = out.toByteArray();
      // content = new String(data);
      file = null;
      return data;
    } catch (IOException e) {
      throw new XMLDBException(
          ErrorCodes.INVALID_RESOURCE,
          "failed to read resource content from file " + file.getAbsolutePath(),
          e);
    }
  } else if (content != null)
    try {
      return content.getBytes("UTF-8");
    } catch (UnsupportedEncodingException e) {
      LOG.warn(e);
    }
  return null;
}

private void readPage(File file) throws Exception {
  byte[] data = IOUtils.readBytesAndClose(new FileInputStream(file), 0);
  String text = new String(data, "UTF-8");
  StringTokenizer t = new StringTokenizer(text, "<> \r\n", true);
  boolean inTag = false;
  title = false;
  heading = false;
  while (t.hasMoreTokens()) {
    String token = t.nextToken();
    if (token.length() == 1) {
      char c = token.charAt(0);
      switch (c) {
        case '<':
          {
            if (inTag) {
              process("???");
            }
            inTag = true;
            if (!t.hasMoreTokens()) {
              break;
            }
            token = t.nextToken();
            if (token.startsWith("/")) {
              title = false;
              heading = false;
            } else if (token.equalsIgnoreCase("title")) {
              title = true;
            } else if (token.length() == 2
                && Character.toLowerCase(token.charAt(0)) == 'h'
                && Character.isDigit(token.charAt(1))) {
              heading = true;
            }
            // TODO maybe skip script tags?
            break;
          }
        case '>':
          {
            if (!inTag) {
              process("???");
            }
            inTag = false;
            break;
          }
        case '\r':
        case '\n':
        case ' ':
          break;
        default:
          if (!inTag) {
            process(token);
          }
      }
    } else {
      if (!inTag) {
        process(token);
      }
    }
  }

  if (page.title == null || page.title.trim().length() == 0) {
    System.out.println("Error: not title found in " + file.getName());
    page.title = file.getName();
  }
  page.title = page.title.trim();
}

