public Dataset fetchVisibleDatasetFromCache(String fileName, int period, int end)
    throws IOException {
  String folder = FileUtils.cacheDatasetsFolder();
  File file = FileUtils.hashedCacheFile(folder, fileName);

  Properties properties = new Properties();
  FileInputStream fileInputStream = new FileInputStream(file);
  properties.load(fileInputStream);

  List<DataItem> items = new ArrayList<DataItem>();
  int size = properties.size();
  for (int i = 0; i < period; i++) {
    int j = end - period + i;
    if (j < size && j >= 0) {
      String key = Integer.toString(j);
      String property = properties.getProperty(key);
      if (property.equals("null")) {
        items.add(null);
      } else {
        String[] values = property.split(",");
        DataItem item =
            new DataItem(
                Long.parseLong(values[0]),
                Double.parseDouble(values[1]),
                Double.parseDouble(values[2]),
                Double.parseDouble(values[3]),
                Double.parseDouble(values[4]),
                Double.parseDouble(values[5]));
        items.add(item);
      }
    }
  }

  fileInputStream.close();
  Dataset dataset = new Dataset(items);
  return dataset;
}

private void handleQuestion(
    String idVersion, HttpServletRequest request, HttpServletResponse response) throws Exception {
  Matcher m = QUESTIONID_REGEXP.matcher(idVersion);
  if (!m.matches()) {
    sendError(
        request,
        response,
        HttpServletResponse.SC_NOT_FOUND,
        "Not found",
        "Not a valid question id-version.",
        null);
  }
  File file = new File(getServletContext().getRealPath("questions"), m.group(1) + ".jar");

  // Check that the requested file exits.
  if (!file.exists()) {
    sendError(
        request,
        response,
        HttpServletResponse.SC_NOT_FOUND,
        "Not found",
        "The requested question is not present on this server.",
        null);
  }

  // Then send it.
  byte[] abQuestion = IO.loadBytes(new FileInputStream(file));
  response.setContentType("application/x-openmark");
  response.setContentLength(abQuestion.length);
  OutputStream os = response.getOutputStream();
  os.write(abQuestion);
  os.close();
}

public static void copy(String fromFileName, String toFileName) throws IOException {
  File fromFile = new File(fromFileName);
  File toFile = new File(toFileName);

  if (!fromFile.exists())
    throw new IOException("FileCopy: " + "no such source file: " + fromFileName);
  if (!fromFile.isFile())
    throw new IOException("FileCopy: " + "can't copy directory: " + fromFileName);
  if (!fromFile.canRead())
    throw new IOException("FileCopy: " + "source file is unreadable: " + fromFileName);

  if (toFile.isDirectory()) toFile = new File(toFile, fromFile.getName());

  if (toFile.exists()) {
    if (!toFile.canWrite())
      throw new IOException("FileCopy: " + "destination file is unwriteable: " + toFileName);
    System.out.print("Overwrite existing file " + toFile.getName() + "? (Y/N): ");
    System.out.flush();
    BufferedReader in = new BufferedReader(new InputStreamReader(System.in));
    String response = in.readLine();
    if (!response.equals("Y") && !response.equals("y"))
      throw new IOException("FileCopy: " + "existing file was not overwritten.");
  } else {
    String parent = toFile.getParent();
    if (parent == null) parent = System.getProperty("user.dir");
    File dir = new File(parent);
    if (!dir.exists())
      throw new IOException("FileCopy: " + "destination directory doesn't exist: " + parent);
    if (dir.isFile())
      throw new IOException("FileCopy: " + "destination is not a directory: " + parent);
    if (!dir.canWrite())
      throw new IOException("FileCopy: " + "destination directory is unwriteable: " + parent);
  }

  FileInputStream from = null;
  FileOutputStream to = null;
  try {
    from = new FileInputStream(fromFile);
    to = new FileOutputStream(toFile);
    byte[] buffer = new byte[4096];
    int bytesRead;

    while ((bytesRead = from.read(buffer)) != -1) to.write(buffer, 0, bytesRead); // write
  } finally {
    if (from != null)
      try {
        from.close();
      } catch (IOException e) {;
      }
    if (to != null)
      try {
        to.close();
      } catch (IOException e) {;
      }
  }
}

