/** Load the file. */
void load() {
  long fs = file.length();
  long max = (long) Integer.MAX_VALUE;
  int bs = (int) (fs > max ? max : fs);

  groups = new LinkedList();
  lines = new HashMap(bs / 20);
  subs = new LinkedList();

  // Load
  try {
    long t1 = System.currentTimeMillis();
    if (debug) {
      System.err.println("DEBUG: nntp: newsrc loading " + file.getPath());
    }

    FileInputStream fr = new FileInputStream(file);
    InputStreamReader ir = new InputStreamReader(fr, NEWSRC_ENCODING);
    BufferedReader reader = new BufferedReader(ir, bs);
    String line = reader.readLine();
    while (line != null) {
      int cp = line.indexOf(':');
      if (cp > -1) {
        // Subscribed newsgroup
        String name = line.substring(0, cp);
        groups.add(name);
        subs.add(name);
        cp++;
        if (cp < line.length()) {
          String tail = line.substring(cp).trim();
          if (tail.length() > 0) {
            lines.put(name, tail);
          }
        }
      } else {
        int pp = line.indexOf('!');
        if (pp > -1) {
          // Unsubscribed newsgroup
          String name = line.substring(0, pp);
          groups.add(name);
          pp++;
          if (pp < line.length()) {
            String tail = line.substring(pp).trim();
            if (tail.length() > 0) {
              lines.put(name, tail);
            }
          }
        }
        // else ignore - comments etc will not be saved!
      }
      line = reader.readLine();
    }
    reader.close();
    long t2 = System.currentTimeMillis();
    if (debug) {
      System.err.println(
          "DEBUG: nntp: newsrc load: " + groups.size() + " groups in " + (t2 - t1) + "ms");
    }
  } catch (FileNotFoundException e) {
  } catch (IOException e) {
    System.err.println("WARNING: nntp: unable to read newsrc file");
    if (debug) {
      e.printStackTrace(System.err);
    }
  } catch (SecurityException e) {
    System.err.println("WARNING: nntp: " + "no read permission on newsrc file");
  }
  dirty = false;
}

/**
 * Return an input stream.
 *
 * @param resource - the resource for the stream It is a non-fatal error for the file not to be
 *     readible as long as the resource's expected length is zero. A zero length body is
 *     indicated by returning null. We check for the body length *after* we try to read the
 *     file. If the file is readible, we simply read it and return it as the body.
 */
protected InputStream streamResourceBodyFilesystem(ContentResource resource)
    throws ServerOverloadException {
  // form the file name
  File file = new File(externalResourceFileName(resource));

  // read the new
  try {
    FileInputStream in = new FileInputStream(file);
    return in;
  } catch (Throwable t) {
    // If there is not supposed to be data in the file - simply return null
    if (((BaseResourceEdit) resource).m_contentLength == 0) {
      return null;
    }

    // If we have a non-zero body length and reading failed, it is an error worth of note
    M_log.warn(
        ": failed to read resource: "
            + resource.getId()
            + " len: "
            + ((BaseResourceEdit) resource).m_contentLength
            + " : "
            + t);
    throw new ServerOverloadException("failed to read resource body");
    // return null;
  }
}

