      System.out.println("Usage: msgshow [-L url] [-T protocol] [-H host] [-p port] [-U user]");
      System.out.println("\t[-P password] [-f mailbox] [msgnum ...] [-v] [-D] [-s] [-S] [-a]");
      System.out.println("or msgshow -m [-v] [-D] [-s] [-S] [-f msg-file]");
      System.exit(1);
    } else {
      break;
    }
  }

  try {
    // Get a Properties object
    Properties props = System.getProperties();

    // Get a Session object
    Session session = Session.getInstance(props, null);
    session.setDebug(debug);

    if (showMessage) {
      MimeMessage msg;
      if (mbox != null)
        msg = new MimeMessage(session, new BufferedInputStream(new FileInputStream(mbox)));
      else msg = new MimeMessage(session, msgStream);
      dumpPart(msg);
      System.exit(0);
    }

    // Get a Store object
    Store store = null;
    if (url != null) {
      URLName urln = new URLName(url);
      store = session.getStore(urln);
      if (showAlert) {
        store.addStoreListener(
            new StoreListener() {
              public void notification(StoreEvent e) {
                String s;
                if (e.getMessageType() == StoreEvent.ALERT) s = "ALERT: ";
                else s = "NOTICE: ";
                System.out.println(s + e.getMessage());
              }
            });
      }
      store.connect();
    } else {
      if (protocol != null) store = session.getStore(protocol);
      else store = session.getStore();

      // Connect
      if (host != null || user != null || password != null)
        store.connect(host, port, user, password);
      else store.connect();
    }

    // Open the Folder

    Folder folder = store.getDefaultFolder();
    if (folder == null) {
      System.out.println("No default folder");
      System.exit(1);
    }

    if (mbox == null) mbox = "INBOX";
    folder = folder.getFolder(mbox);
    if (folder == null) {
      System.out.println("Invalid folder");
      System.exit(1);
    }

    // try to open read/write and if that fails try read-only
    try {
      folder.open(Folder.READ_WRITE);
    } catch (MessagingException ex) {
      folder.open(Folder.READ_ONLY);
    }
    int totalMessages = folder.getMessageCount();

    if (totalMessages == 0) {
      System.out.println("Empty folder");
      folder.close(false);
      store.close();
      System.exit(1);
    }

    if (verbose) {
      int newMessages = folder.getNewMessageCount();
      System.out.println("Total messages = " + totalMessages);
      System.out.println("New messages = " + newMessages);
      System.out.println("-------------------------------");
    }

    if (optind >= argv.length) {
      // Attributes & Flags for all messages ..
      Message[] msgs = folder.getMessages();

      // Use a suitable FetchProfile
      FetchProfile fp = new FetchProfile();
      fp.add(FetchProfile.Item.ENVELOPE);
      fp.add(FetchProfile.Item.FLAGS);
      fp.add("X-Mailer");
      folder.fetch(msgs, fp);

      for (int i = 0; i < msgs.length; i++) {
        System.out.println("--------------------------");
        System.out.println("MESSAGE #" + (i + 1) + ":");
        dumpEnvelope(msgs[i]);
        // dumpPart(msgs[i]);
      }
    } else {
      while (optind < argv.length) {
        int msgnum = Integer.parseInt(argv[optind++]);
        System.out.println("Getting message number: " + msgnum);
        Message m = null;

        try {
          m = folder.getMessage(msgnum);
          dumpPart(m);
        } catch (IndexOutOfBoundsException iex) {
          System.out.println("Message number out of range");
        }
      }
    }

    folder.close(false);
    store.close();
  } catch (Exception ex) {
    System.out.println("Oops, got exception! " + ex.getMessage());
    ex.printStackTrace();
    System.exit(1);
  }
  System.exit(0);
}

public static void sendFile(
    final HttpServletRequest request,
    final HttpServletResponse response,
    final File file,
    final String type)
    throws IOException {
  // Make sure the file exits and is readable and send a 404 error
  // response if it doesn't:
  if (!file.exists() || !file.canRead()) {
    log.error(
        "Can't read file \""
            + file.getAbsolutePath()
            + "\" for request \""
            + request.getRequestURI()
            + "\", will send a 404 error response.");
    response.sendError(HttpServletResponse.SC_NOT_FOUND);
    return;
  }

  // Advice against large files:
  long length = file.length();
  if (length > LARGE) {
    log.warn(
        "File \""
            + file.getAbsolutePath()
            + " is "
            + length
            + " bytes long. Please reconsider using this servlet for files larger than "
            + LARGE
            + " bytes.");
  }

  // Set the content type:
  response.setContentType(type);
  response.setContentLength((int) length);

  // Send the content of the file:
  InputStream in = null;
  OutputStream out = null;
  try {
    in = new FileInputStream(file);
    out = response.getOutputStream();
    byte[] buffer = new byte[4096];
    int count = 0;
    while ((count = in.read(buffer)) != -1) {
      out.write(buffer, 0, count);
    }
  } catch (IOException exception) {
    final String message = "Error sending file \"" + file.getAbsolutePath() + "\".";
    log.error(message, exception);
    throw new IOException(message);
  } finally {
    if (in != null) {
      in.close();
    }
  }
}

