protected void downloadTemplate(HttpServletRequest request, HttpServletResponse response)
    throws ServletException, IOException {
  // String filename = URLDecoder.decode(request.getPathInfo(), "UTF-8");
  // File file = new File("/path/to/files", filename);

  String filename =
      Constants
          .CDISC_MULTIPLE_TERM_REQUEST_TEMPLATE; // "CDISC_Controlled_Terminology_Multiple_Term_Request_Spreadsheet.xlsx";
  String pathname = AppProperties.getConfigurationDir() + filename;
  // Systen.out.println(pathname);

  File file = new File(pathname);

  response.setHeader("Content-Type", getServletContext().getMimeType(file.getName()));
  // response.setHeader("Content-Length", file.length());
  response.setHeader("Content-Disposition", "inline; filename=\"" + file.getName() + "\"");

  BufferedInputStream input = null;
  BufferedOutputStream output = null;

  try {
    input = new BufferedInputStream(new FileInputStream(file));
    output = new BufferedOutputStream(response.getOutputStream());

    byte[] buffer = new byte[8192];
    for (int length = 0; (length = input.read(buffer)) > 0; ) {
      output.write(buffer, 0, length);
    }
  } finally {
    if (output != null)
      try {
        output.close();
      } catch (IOException ignore) {
      }
    if (input != null)
      try {
        input.close();
      } catch (IOException ignore) {
      }
  }
}

public static void compress(String dir, String srcName, String destName, String encoding) {

  FileInputStream fin = null;
  FileOutputStream fout = null;
  GZIPOutputStream gzout = null;
  try {
    // 
    fin = new FileInputStream(dir + FileUtil.PATH_CHAR + srcName);
    // 
    fout = new FileOutputStream(dir + FileUtil.PATH_CHAR + destName);
    // gzip
    gzout = new GZIPOutputStream(fout);
    byte[] buf = new byte[1024]; // 
    int num;

    while ((num = fin.read(buf)) != -1) {
      gzout.write(buf, 0, num);
    }
  } catch (Exception e) {
    e.printStackTrace();

  } finally {
    if (gzout != null)
      try {
        gzout.close();
      } catch (IOException e) {
        e.printStackTrace();
      }
    if (fout != null)
      try {
        fout.close();
      } catch (IOException e) {
        e.printStackTrace();
      }
    if (fin != null)
      try {
        fin.close();
      } catch (IOException e) {
        e.printStackTrace();
      }
  }
}

