/**
 * 
 *
 * @param resp
 * @throws ServletException
 * @throws IOException
 */
protected void writePropertiesBySplit(HttpServletResponse resp)
    throws ServletException, IOException {
  InputStream inputStream = null;
  InputStream cInputStream = null;
  StringBuilder sb = new StringBuilder();
  try {
    ClassLoader loader = Thread.currentThread().getContextClassLoader();
    inputStream = loader.getResourceAsStream("application-files.properties");
    Properties props = new LinkedProperties();
    props.load(inputStream);
    Set<Object> fileKeySet = props.keySet();
    for (Object obj : fileKeySet) {
      logger.debug(":key={}, value={}", obj, props.getProperty(obj.toString()));
      sb.append(
          "<span style='color:red;font-weight:bold;'>"
              + props.getProperty(obj.toString())
              + "</span><br/>");
      if (props.getProperty(obj.toString()).startsWith("file:")) {
        File file = new File(props.getProperty(obj.toString()).split(":")[1]);
        if (file.getParentFile() != null && !file.getParentFile().exists()) {
          continue;
        }
        if (!file.exists()) {
          continue;
        }
        cInputStream = new FileInputStream(file);
      } else {
        cInputStream = loader.getResourceAsStream(props.getProperty(obj.toString()));
      }

      Properties cProps = new LinkedProperties();
      cProps.load(cInputStream);
      Set<Object> cFileKeySet = cProps.keySet();
      for (Object cObj : cFileKeySet) {
        sb.append(
            cObj.toString()
                + "<span style='color:red;font-weight:bold;'>=</span>"
                + cProps.get(cObj.toString())
                + "<br/>");
      }
      cInputStream.close();
    }
    inputStream.close();
    resp.setContentType("text/html");
    resp.getWriter().print("<html><body>" + sb.toString() + "</body></html>");
  } catch (Exception e) {
    logger.error(":", e);
    resp.getWriter().print("error:" + e.getMessage());
  } finally {
    if (null != inputStream) {
      inputStream.close();
    }
    if (null != cInputStream) {
      cInputStream.close();
    }
  }
}

private static void copyRecursive(File source, File target, Set<File> visited)
    throws IOException {
  if (visited.contains(source)) throw new IOException("Circular file structure detected");

  visited.add(source);

  if (source.isDirectory()) {
    for (File subSource : source.listFiles()) {
      File subTarget = new File(target, subSource.getPath());
      subTarget.mkdirs();
      if (!subTarget.isDirectory())
        throw new IOException(
            "Unable to create or access directory " + subTarget.getAbsolutePath());
      copyRecursive(subSource, subTarget, visited);
    }
  } else {
    File directory = target.getParentFile();
    directory.mkdirs();
    if (!directory.isDirectory())
      throw new IOException(
          "Unable to create or access directory " + directory.getAbsolutePath());
    OutputStream targetStream = new FileOutputStream(target);
    InputStream sourceStream = new FileInputStream(source);
    StreamUtil.copy(sourceStream, targetStream);
    sourceStream.close();
    targetStream.close();
  }
}

