/**
 * Loads the index from the index file.
 *
 * <p>N.B. For this particular implementation, loading the index from the simple serialization
 * format is in fact slower than constructing the index from scratch. For the more efficient
 * indices, loading should be much faster than constructing.
 *
 * @throws IOException, ClassNotFoundException
 */
@Override
public void loadIndex() throws IOException, ClassNotFoundException {
  String indexFile = _options._indexPrefix + "/corpus.idx";
  System.out.println("Load index from: " + indexFile);

  ObjectInputStream reader = new ObjectInputStream(new FileInputStream(indexFile));
  IndexerFullScan loaded = (IndexerFullScan) reader.readObject();

  this._documents = loaded._documents;
  // Compute numDocs and totalTermFrequency b/c Indexer is not serializable.
  this._numDocs = _documents.size();
  for (Integer freq : loaded._termCorpusFrequency.values()) {
    this._totalTermFrequency += freq;
  }
  this._dictionary = loaded._dictionary;
  this._terms = loaded._terms;
  this._termCorpusFrequency = loaded._termCorpusFrequency;
  this._termDocFrequency = loaded._termDocFrequency;
  reader.close();

  System.out.println(
      Integer.toString(_numDocs)
          + " documents loaded "
          + "with "
          + Long.toString(_totalTermFrequency)
          + " terms!");
}

