private InputStream resolve(String location) {

  if (location == null) throw new RuntimeException("location is undefined");

  File file = new File(location);

  if (!file.exists() || !file.canRead() || file.isDirectory())
    throw new RuntimeException(
        "configuration @ " + file + " does not exist, cannot be read, or is a directory");

  try {

    InputStream stream = new FileInputStream(file);

    log.info("using configuration @ {}", file);

    return stream;

  } catch (Exception e) {

    throw new RuntimeException("cannot read configuration @ " + file, e);
  }
}

