/**
 * This method creates an alternative UI for this panel. This UI can be used when the creation of
 * shortcuts is not supported on the target system. It displays an apology for the inability to
 * create shortcuts on this system, along with information about the intended targets. In
 * addition, there is a button that allows the user to save more complete information in a text
 * file. Based on this information the user might be able to create the necessary shortcut him or
 * herself. At least there will be information about how to launch the application.
 */

/*--------------------------------------------------------------------------*/
private void buildAlternateUI() {
  layout = new GridBagLayout();
  constraints = new GridBagConstraints();
  setLayout(layout);

  // ----------------------------------------------------
  // static text a the top of the panel, that apologizes
  // about the fact that we can not create shortcuts on
  // this particular target OS.
  // ----------------------------------------------------
  MultiLineLabel apologyLabel =
      new MultiLineLabel(parent.langpack.getString("ShortcutPanel.alternate.apology"), 0, 0);

  constraints.gridx = 0;
  constraints.gridy = 0;
  constraints.gridwidth = 1;
  constraints.gridheight = 1;
  constraints.weightx = 1.0;
  constraints.weighty = 1.0;
  constraints.insets = new Insets(5, 5, 5, 5);
  constraints.fill = GridBagConstraints.HORIZONTAL;
  constraints.anchor = GridBagConstraints.WEST;
  layout.addLayoutComponent(apologyLabel, constraints);
  add(apologyLabel);

  // ----------------------------------------------------
  // label that explains the significance ot the list box
  // ----------------------------------------------------
  MultiLineLabel listLabel =
      new MultiLineLabel(parent.langpack.getString("ShortcutPanel.alternate.targetsLabel"), 0, 0);

  constraints.gridx = 0;
  constraints.gridy = 1;
  constraints.gridwidth = 1;
  constraints.gridheight = 1;
  constraints.weightx = 1.0;
  constraints.weighty = 1.0;
  layout.addLayoutComponent(listLabel, constraints);
  add(listLabel);

  // ----------------------------------------------------
  // list box to list all of the intended shortcut targets
  // ----------------------------------------------------
  Vector targets = new Vector();

  for (int i = 0; i < shortcuts.size(); i++) {
    targets.add(((ShortcutData) shortcuts.elementAt(i)).target);
  }

  targetList = new JList(targets);

  JScrollPane scrollPane = new JScrollPane(targetList);

  constraints.gridx = 0;
  constraints.gridy = 2;
  constraints.fill = GridBagConstraints.BOTH;
  layout.addLayoutComponent(scrollPane, constraints);
  add(scrollPane);

  // ----------------------------------------------------
  // static text that explains about the text file
  // ----------------------------------------------------
  MultiLineLabel fileExplanation =
      new MultiLineLabel(
          parent.langpack.getString("ShortcutPanel.alternate.textFileExplanation"), 0, 0);

  constraints.gridx = 0;
  constraints.gridy = 3;
  constraints.weightx = 1.0;
  constraints.weighty = 1.0;
  constraints.fill = GridBagConstraints.HORIZONTAL;
  layout.addLayoutComponent(fileExplanation, constraints);
  add(fileExplanation);

  // ----------------------------------------------------
  // button to save the text file
  // ----------------------------------------------------
  saveButton =
      ButtonFactory.createButton(
          parent.langpack.getString("ShortcutPanel.alternate.saveButton"), idata.buttonsHColor);
  saveButton.addActionListener(this);

  constraints.gridx = 0;
  constraints.gridy = 4;
  constraints.gridwidth = 1;
  constraints.gridheight = 1;
  constraints.fill = GridBagConstraints.NONE;
  constraints.anchor = GridBagConstraints.CENTER;
  layout.addLayoutComponent(saveButton, constraints);
  add(saveButton);
}

