public static ByteBuffer readLine(ByteBuffer buf) {
  ByteBuffer sbuf = ByteBuffer.allocate(buf.remaining());
  byte prev = '0';
  byte cur = '0';
  while (buf.hasRemaining()) {
    prev = cur;
    cur = buf.get();
    sbuf.put(cur);
    if (prev == (byte) '\r' && cur == (byte) '\n') {
      sbuf.limit(sbuf.position() - 2);
      sbuf.position(0);
      return sbuf;
    }
  }
  // ensure that there wont be any bytes skipped
  buf.position(buf.position() - sbuf.position());
  return null;
}

/**
 * Get a cursor based on a image reference on the classpath
 *
 * @param ref The reference to the image to be loaded
 * @param x The x-coordinate of the cursor hotspot (left -> right)
 * @param y The y-coordinate of the cursor hotspot (bottom -> top)
 * @return The create cursor
 * @throws IOException Indicates a failure to load the image
 * @throws LWJGLException Indicates a failure to create the hardware cursor
 */
public Cursor getCursor(String ref, int x, int y) throws IOException, LWJGLException {
  LoadableImageData imageData = null;

  imageData = ImageDataFactory.getImageDataFor(ref);
  imageData.configureEdging(false);

  ByteBuffer buf = imageData.loadImage(ResourceLoader.getResourceAsStream(ref), true, true, null);
  for (int i = 0; i < buf.limit(); i += 4) {
    byte red = buf.get(i);
    byte green = buf.get(i + 1);
    byte blue = buf.get(i + 2);
    byte alpha = buf.get(i + 3);

    buf.put(i + 2, red);
    buf.put(i + 1, green);
    buf.put(i, blue);
    buf.put(i + 3, alpha);
  }

  try {
    int yspot = imageData.getHeight() - y - 1;
    if (yspot < 0) {
      yspot = 0;
    }

    return new Cursor(
        imageData.getTexWidth(), imageData.getTexHeight(), x, yspot, 1, buf.asIntBuffer(), null);
  } catch (Throwable e) {
    Log.info("Chances are you cursor is too small for this platform");
    throw new LWJGLException(e);
  }
}

public static byte[] toBytes(Schema schema, Tuple tuple) {
  int size = StorageUtil.getRowByteSize(schema);
  ByteBuffer bb = ByteBuffer.allocate(size);
  Column col;
  for (int i = 0; i < schema.size(); i++) {
    col = schema.getColumn(i);
    switch (col.getDataType().getType()) {
      case BOOLEAN:
        bb.put(tuple.get(i).asByte());
        break;
      case BIT:
        bb.put(tuple.get(i).asByte());
        break;
      case CHAR:
        bb.put(tuple.get(i).asByte());
        break;
      case INT2:
        bb.putShort(tuple.get(i).asInt2());
        break;
      case INT4:
        bb.putInt(tuple.get(i).asInt4());
        break;
      case INT8:
        bb.putLong(tuple.get(i).asInt8());
        break;
      case FLOAT4:
        bb.putFloat(tuple.get(i).asFloat4());
        break;
      case FLOAT8:
        bb.putDouble(tuple.get(i).asFloat8());
        break;
      case TEXT:
        byte[] _string = tuple.get(i).asByteArray();
        bb.putInt(_string.length);
        bb.put(_string);
        break;
      case DATE:
        bb.putInt(tuple.get(i).asInt4());
        break;
      case TIME:
      case TIMESTAMP:
        bb.putLong(tuple.get(i).asInt8());
        break;
      case BLOB:
        byte[] bytes = tuple.get(i).asByteArray();
        bb.putInt(bytes.length);
        bb.put(bytes);
        break;
      case INET4:
        byte[] ipBytes = tuple.get(i).asByteArray();
        bb.put(ipBytes);
        break;
      case INET6:
        bb.put(tuple.get(i).asByteArray());
        break;
      default:
    }
  }

  bb.flip();
  byte[] buf = new byte[bb.limit()];
  bb.get(buf);
  return buf;
}

