@Override
public StaticBuffer getStaticBufferFlipBytes(int from, int to) {
  ByteBuffer b = buffer.duplicate();
  b.flip();
  Preconditions.checkArgument(from >= 0 && from <= to);
  Preconditions.checkArgument(to <= b.limit());
  for (int i = from; i < to; i++) b.put(i, (byte) ~b.get(i));
  return new StaticByteBuffer(b);
}

/**
 * @see org.newdawn.slick.opengl.LoadableImageData#loadImage(java.io.InputStream, boolean,
 *     boolean, int[])
 */
public ByteBuffer loadImage(
    InputStream fis, boolean flipped, boolean forceAlpha, int[] transparent) throws IOException {
  if (transparent != null) {
    forceAlpha = true;
  }
  byte red = 0;
  byte green = 0;
  byte blue = 0;
  byte alpha = 0;

  BufferedInputStream bis = new BufferedInputStream(fis, 100000);
  DataInputStream dis = new DataInputStream(bis);

  // Read in the Header
  short idLength = (short) dis.read();
  short colorMapType = (short) dis.read();
  short imageType = (short) dis.read();
  short cMapStart = flipEndian(dis.readShort());
  short cMapLength = flipEndian(dis.readShort());
  short cMapDepth = (short) dis.read();
  short xOffset = flipEndian(dis.readShort());
  short yOffset = flipEndian(dis.readShort());

  width = flipEndian(dis.readShort());
  height = flipEndian(dis.readShort());
  pixelDepth = (short) dis.read();
  if (pixelDepth == 32) {
    forceAlpha = false;
  }

  texWidth = get2Fold(width);
  texHeight = get2Fold(height);

  short imageDescriptor = (short) dis.read();
  if ((imageDescriptor & 0x0020) == 0) {
    flipped = !flipped;
  }

  // Skip image ID
  if (idLength > 0) {
    bis.skip(idLength);
  }

  byte[] rawData = null;
  if ((pixelDepth == 32) || (forceAlpha)) {
    pixelDepth = 32;
    rawData = new byte[texWidth * texHeight * 4];
  } else if (pixelDepth == 24) {
    rawData = new byte[texWidth * texHeight * 3];
  } else {
    throw new RuntimeException("Only 24 and 32 bit TGAs are supported");
  }

  if (pixelDepth == 24) {
    if (flipped) {
      for (int i = height - 1; i >= 0; i--) {
        for (int j = 0; j < width; j++) {
          blue = dis.readByte();
          green = dis.readByte();
          red = dis.readByte();

          int ofs = ((j + (i * texWidth)) * 3);
          rawData[ofs] = red;
          rawData[ofs + 1] = green;
          rawData[ofs + 2] = blue;
        }
      }
    } else {
      for (int i = 0; i < height; i++) {
        for (int j = 0; j < width; j++) {
          blue = dis.readByte();
          green = dis.readByte();
          red = dis.readByte();

          int ofs = ((j + (i * texWidth)) * 3);
          rawData[ofs] = red;
          rawData[ofs + 1] = green;
          rawData[ofs + 2] = blue;
        }
      }
    }
  } else if (pixelDepth == 32) {
    if (flipped) {
      for (int i = height - 1; i >= 0; i--) {
        for (int j = 0; j < width; j++) {
          blue = dis.readByte();
          green = dis.readByte();
          red = dis.readByte();
          if (forceAlpha) {
            alpha = (byte) 255;
          } else {
            alpha = dis.readByte();
          }

          int ofs = ((j + (i * texWidth)) * 4);

          rawData[ofs] = red;
          rawData[ofs + 1] = green;
          rawData[ofs + 2] = blue;
          rawData[ofs + 3] = alpha;

          if (alpha == 0) {
            rawData[ofs + 2] = (byte) 0;
            rawData[ofs + 1] = (byte) 0;
            rawData[ofs] = (byte) 0;
          }
        }
      }
    } else {
      for (int i = 0; i < height; i++) {
        for (int j = 0; j < width; j++) {
          blue = dis.readByte();
          green = dis.readByte();
          red = dis.readByte();
          if (forceAlpha) {
            alpha = (byte) 255;
          } else {
            alpha = dis.readByte();
          }

          int ofs = ((j + (i * texWidth)) * 4);

          if (ByteOrder.nativeOrder() == ByteOrder.BIG_ENDIAN) {
            rawData[ofs] = red;
            rawData[ofs + 1] = green;
            rawData[ofs + 2] = blue;
            rawData[ofs + 3] = alpha;
          } else {
            rawData[ofs] = red;
            rawData[ofs + 1] = green;
            rawData[ofs + 2] = blue;
            rawData[ofs + 3] = alpha;
          }

          if (alpha == 0) {
            rawData[ofs + 2] = 0;
            rawData[ofs + 1] = 0;
            rawData[ofs] = 0;
          }
        }
      }
    }
  }
  fis.close();

  if (transparent != null) {
    for (int i = 0; i < rawData.length; i += 4) {
      boolean match = true;
      for (int c = 0; c < 3; c++) {
        if (rawData[i + c] != transparent[c]) {
          match = false;
        }
      }

      if (match) {
        rawData[i + 3] = 0;
      }
    }
  }

  // Get a pointer to the image memory
  ByteBuffer scratch = BufferUtils.createByteBuffer(rawData.length);
  scratch.put(rawData);

  int perPixel = pixelDepth / 8;
  if (height < texHeight - 1) {
    int topOffset = (texHeight - 1) * (texWidth * perPixel);
    int bottomOffset = (height - 1) * (texWidth * perPixel);
    for (int x = 0; x < texWidth * perPixel; x++) {
      scratch.put(topOffset + x, scratch.get(x));
      scratch.put(
          bottomOffset + (texWidth * perPixel) + x, scratch.get((texWidth * perPixel) + x));
    }
  }
  if (width < texWidth - 1) {
    for (int y = 0; y < texHeight; y++) {
      for (int i = 0; i < perPixel; i++) {
        scratch.put(
            ((y + 1) * (texWidth * perPixel)) - perPixel + i,
            scratch.get(y * (texWidth * perPixel) + i));
        scratch.put(
            (y * (texWidth * perPixel)) + (width * perPixel) + i,
            scratch.get((y * (texWidth * perPixel)) + ((width - 1) * perPixel) + i));
      }
    }
  }

  scratch.flip();

  return scratch;
}

private final Hasher putBytes(ByteBuffer readBuffer) {
  // If we have room for all of it, this is easy
  if (readBuffer.remaining() <= buffer.remaining()) {
    buffer.put(readBuffer);
    munchIfFull();
    return this;
  }

  // First add just enough to fill buffer size, and munch that
  int bytesToCopy = bufferSize - buffer.position();
  for (int i = 0; i < bytesToCopy; i++) {
    buffer.put(readBuffer.get());
  }
  munch(); // buffer becomes empty here, since chunkSize divides bufferSize

  // Now process directly from the rest of the input buffer
  while (readBuffer.remaining() >= chunkSize) {
    process(readBuffer);
  }

  // Finally stick the remainder back in our usual buffer
  buffer.put(readBuffer);
  return this;
}

/** Tests that valid xsd:dateTime values are accepted and processed correctly. */
public void testValid() {
  // Create a new factory
  SPDateTimeFactory factory = new SPDateTimeFactory();

  // Create a dateTime object by lexical string
  SPDateTimeImpl dateTime =
      (SPDateTimeImpl) factory.newSPTypedLiteral(XSD.DATE_TIME_URI, VALID_XSD_DATETIME);

  // Test that the lexical form of the date is correct
  assertEquals(VALID_XSD_DATETIME, dateTime.getLexicalForm());

  // Retrieve the byte data of the dateTime object
  ByteBuffer dtBytes = dateTime.getData();

  // Retrieve the long value from the buffer
  long dtLong = dtBytes.getLong();

  // Create a date object from the dateTime's long
  Date dtDate = new Date(dtLong);

  // Format the resulting day
  format.format(dtDate);

  // Test the correct value is stored
  assertEquals(VALID_XSD_DATETIME, getDateString(dateTime));

  // Byte buffer to hold our date information
  ByteBuffer buffer = ByteBuffer.wrap(new byte[Constants.SIZEOF_LONG + Constants.SIZEOF_INT]);

  // If the previous step passed then we know the long value is what we want,
  // so store it in our buffer
  buffer.putLong(dtLong);
  buffer.put((byte) 0x02); // The value of the "local" flag
  buffer.put((byte) 0);

  // Reset the buffer for reading
  buffer.flip();

  if (log.isDebugEnabled()) {

    log.debug(
        "Creating dateTime from byte buffer storing value: " + format.format(new Date(dtLong)));

    log.debug("Original dateTime long vs. stored long: " + dtLong + " vs. " + buffer.getLong());
    log.debug("Stored timezone code: " + buffer.get());
    log.debug("Stored decimal places: " + buffer.get());

    // Reset the buffer
    buffer.flip();
  }

  // Create a dateTime object by byte buffer
  dateTime = (SPDateTimeImpl) factory.newSPTypedLiteral(0, buffer);

  // Test that the lexical form of the date is correct
  assertEquals(VALID_XSD_DATETIME, dateTime.getLexicalForm());

  // Test the correct value is stored
  assertEquals(VALID_XSD_DATETIME, getDateString(dateTime));

  // Create a dateTime object by lexical string
  dateTime = (SPDateTimeImpl) factory.newSPTypedLiteral(XSD.DATE_TIME_URI, VALID_XSD_DATETIME2);

  // Test that the lexical form of the date is correct
  assertEquals(VALID_XSD_DATETIME2, dateTime.getLexicalForm());

  // Test the correct value is stored
  assertEquals(VALID_XSD_DATETIME2, getDateString(dateTime));

  // Create a dateTime object by lexical string
  dateTime = (SPDateTimeImpl) factory.newSPTypedLiteral(XSD.DATE_TIME_URI, VALID_XSD_DATETIME3);

  // Test that the lexical form of the date is correct
  assertEquals(VALID_XSD_DATETIME3, dateTime.getLexicalForm());

  // Test the correct value is stored
  assertEquals(VALID_XSD_DATETIME3, getDateString(dateTime));

  // Create a dateTime object by lexical string
  dateTime = (SPDateTimeImpl) factory.newSPTypedLiteral(XSD.DATE_TIME_URI, VALID_XSD_DATETIME4);

  // Test that the lexical form of the date is correct
  assertEquals(VALID_XSD_DATETIME4, dateTime.getLexicalForm());

  // Test the correct value is stored
  assertEquals(VALID_XSD_DATETIME4, getDateString(dateTime));

  // Create a dateTime object by lexical string
  dateTime = (SPDateTimeImpl) factory.newSPTypedLiteral(XSD.DATE_TIME_URI, VALID_XSD_DATETIME5);

  // Test that the lexical form of the date is correct
  assertEquals(VALID_XSD_DATETIME5, dateTime.getLexicalForm());

  // Test the correct value is stored
  assertEquals(VALID_XSD_DATETIME5, getDateString(dateTime));

  // Create a dateTime object by lexical string
  dateTime = (SPDateTimeImpl) factory.newSPTypedLiteral(XSD.DATE_TIME_URI, VALID_XSD_DATETIME6);

  // Test that the lexical form of the date is correct
  assertEquals(VALID_XSD_DATETIME6, dateTime.getLexicalForm());

  // Test the correct value is stored
  assertEquals(VALID_JAVA_DATETIME6, getDateString(dateTime));
}

public void testBasicAlignment() throws Exception {

  FakeSequentialFileFactory factory = new FakeSequentialFileFactory(200, true);

  SequentialFile file = factory.createSequentialFile("test1", 1);

  file.open();

  try {
    ByteBuffer buffer = ByteBuffer.allocateDirect(57);
    file.write(buffer, true);
    fail("Exception expected");
  } catch (Exception ignored) {
  }

  try {
    ByteBuffer buffer = ByteBuffer.allocateDirect(200);
    for (int i = 0; i < 200; i++) {
      buffer.put(i, (byte) 1);
    }

    file.write(buffer, true);

    buffer = ByteBuffer.allocate(400);
    for (int i = 0; i < 400; i++) {
      buffer.put(i, (byte) 2);
    }

    file.write(buffer, true);

    buffer = ByteBuffer.allocate(600);

    file.position(0);

    file.read(buffer);

    for (int i = 0; i < 200; i++) {
      assertEquals((byte) 1, buffer.get(i));
    }

    for (int i = 201; i < 600; i++) {
      assertEquals("Position " + i, (byte) 2, buffer.get(i));
    }

  } catch (Exception ignored) {
  }
}

/**
 * Verifies a buffer of a given size using the given start hash offset
 *
 * @param buf the buffer to verify
 * @param size the number of bytes to be used in that buffer
 * @param startOffset the start hash offset
 * @param hasher the hasher to use for calculating expected values
 * @return ResumeBytes a set of data about the next offset and chunks analyzed
 */
private VerifyInfo verifyBuffer(ByteBuffer buf, int size, long startOffset, DataHasher hasher) {
  ByteBuffer cmpBuf = ByteBuffer.wrap(new byte[BYTES_PER_LONG]);
  long hashOffset = startOffset;
  long chunksSame = 0;
  long chunksDifferent = 0;
  for (long i = 0; i < size; ++i) {
    cmpBuf.put(buf.get());
    if (!cmpBuf.hasRemaining()) {
      cmpBuf.rewind();
      long receivedData = cmpBuf.getLong();
      cmpBuf.rewind();
      long expected = hasher.generate(hashOffset);
      hashOffset += BYTES_PER_LONG;
      if (receivedData == expected) {
        ++chunksSame;
      } else {
        ++chunksDifferent;
      }
    }
  }
  // any left over??
  if (cmpBuf.hasRemaining() && cmpBuf.position() != 0) {
    // partial capture
    // zero fill and compare with zero filled
    int curSize = cmpBuf.position();
    while (cmpBuf.hasRemaining()) {
      cmpBuf.put((byte) 0);
    }
    long expected = hasher.generate(hashOffset);
    ByteBuffer tempBuf = ByteBuffer.wrap(new byte[BYTES_PER_LONG]);
    tempBuf.putLong(expected);
    tempBuf.position(curSize);
    while (tempBuf.hasRemaining()) {
      tempBuf.put((byte) 0);
    }
    cmpBuf.rewind();
    tempBuf.rewind();
    if (cmpBuf.equals(tempBuf)) {
      ++chunksSame;
    } else {
      ++chunksDifferent;
    }
  }
  return new VerifyInfo(chunksSame, chunksDifferent);
}

void go() throws Exception {
  log.println("Server: Listening " + (block ? "(blocking)" : "(non-blocking)"));
  if (!block) ssc.configureBlocking(false);
  log.println(" " + ssc);
  // log.println(" " + ssc.options());
  SocketChannel sc = null;
  for (; ; ) {
    sc = ssc.accept();
    if (sc != null) {
      break;
    }
    log.println("Server: Sleeping...");
    Thread.sleep(50);
  }
  log.println("Server: Accepted " + sc);
  ByteBuffer bb = ByteBuffer.allocateDirect(100);
  if (sc.read(bb) != 1) throw new Exception("Read failed");
  bb.flip();
  byte b = bb.get();
  log.println("Server: Read " + b + ", writing " + (b + 1));
  bb.clear();
  bb.put((byte) 43);
  bb.flip();
  if (sc.write(bb) != 1) throw new Exception("Write failed");
  sc.close();
  ssc.close();
  log.println("Server: Finished");
}

private static void uncorruptBufferOffset(ByteBuffer buf, int offset) {
