 */
void set(long pos) {
  int bytePos = (int) (pos / 8);
  int bitPos = (int) (pos % 8);
  byte curByte = bloom.get(bytePos);
  curByte |= bitvals[bitPos];
  bloom.put(bytePos, curByte);
}

