/**
 * Serializes the packet. Will compute and set the following fields if they are set to specific
 * values at the time serialize is called: -checksum : 0 -length : 0
 */
public byte[] serialize() {
  int length;
  if (dataOffset == 0) dataOffset = 5; // default header length
  length = dataOffset << 2;
  byte[] payloadData = null;
  if (payload != null) {
    payload.setParent(this);

    payloadData = payload.serialize();
    length += payloadData.length;
  }

  byte[] data = new byte[length];
  ByteBuffer bb = ByteBuffer.wrap(data);

  bb.putShort((short) this.sourcePort.getPort()); // TCP ports are defined to be 16 bits
  bb.putShort((short) this.destinationPort.getPort());
  bb.putInt(this.sequence);
  bb.putInt(this.acknowledge);
  bb.putShort((short) (this.flags | (dataOffset << 12)));
  bb.putShort(this.windowSize);
  bb.putShort(this.checksum);
  bb.putShort(this.urgentPointer);
  if (dataOffset > 5) {
    int padding;
    bb.put(options);
    padding = (dataOffset << 2) - 20 - options.length;
    for (int i = 0; i < padding; i++) bb.put((byte) 0);
  }
  if (payloadData != null) bb.put(payloadData);

  if (this.parent != null && this.parent instanceof IPv4)
    ((IPv4) this.parent).setProtocol(IpProtocol.TCP);

  // compute checksum if needed
  if (this.checksum == 0) {

    if (this.parent != null && this.parent instanceof IPv4) {
      // Checksum calculation based on the JSocket Wrench code
      // https://github.com/ehrmann/jswrench
      // The original code can be found at
      // https://github.com/ehrmann/jswrench/blob/master/src/com/act365/net/SocketUtils.java
      IPv4 ipv4 = (IPv4) this.parent;

      int bufferlength = length + 12;
      boolean odd = length % 2 == 1;
      byte[] source = ipv4.getSourceAddress().getBytes();
      byte[] destination = ipv4.getDestinationAddress().getBytes();

      if (odd) {
        ++bufferlength;
      }

      byte[] buffer = new byte[bufferlength];

      buffer[0] = source[0];
      buffer[1] = source[1];
      buffer[2] = source[2];
      buffer[3] = source[3];

      buffer[4] = destination[0];
      buffer[5] = destination[1];
      buffer[6] = destination[2];
      buffer[7] = destination[3];

      buffer[8] = (byte) 0;
      buffer[9] = (byte) ipv4.getProtocol().getIpProtocolNumber();

      shortToBytes((short) length, buffer, 10);

      int i = 11;

      while (++i < length + 12) {
        buffer[i] = data[i + 0 - 12];
      }

      if (odd) {
        buffer[i] = (byte) 0;
      }

      this.checksum = checksum(buffer, buffer.length, 0);
    } else {
      bb.rewind();
      int accumulation = 0;
      for (int i = 0; i < length / 2; ++i) {
        accumulation += 0xffff & bb.getShort();
      }
      // pad to an even number of shorts
      if (length % 2 > 0) {
        accumulation += (bb.get() & 0xff) << 8;
      }

      accumulation = ((accumulation >> 16) & 0xffff) + (accumulation & 0xffff);
      this.checksum = (short) (~accumulation & 0xffff);
    }

    bb.putShort(16, this.checksum);
  }

  return data;
}

/**
 * Adds the specified buffer, extracting the contained messages and making them available to
 * getMessage(). The left over data is buffered to be combined with future data. &
 *
 * @return The total number of queued messages after this call.
 */
public int addBuffer(ByteBuffer buffer) {
  // push the data from the buffer into as
  // many messages as we can
  while (buffer.remaining() > 0) {

    if (current == null) {

      // If we have a left over carry then we need to
      // do manual processing to get the short value
      if (carry != null) {
        byte high = carry;
        byte low = buffer.get();

        size = (high & 0xff) << 8 | (low & 0xff);
        carry = null;
      } else if (buffer.remaining() < 2) {
        // It's possible that the supplied buffer only has one
        // byte in it... and in that case we will get an underflow
        // when attempting to read the short below.

        // It has to be 1 or we'd never get here... but one
        // isn't enough so we stash it away.
        carry = buffer.get();
        break;
      } else {
        // We are not currently reading an object so
        // grab the size.
        // Note: this is somewhat limiting... int would
        // be better.
        size = buffer.getShort();
      }

      // Allocate the buffer into which we'll feed the
      // data as we get it
      current = ByteBuffer.allocate(size);
    }

    if (current.remaining() <= buffer.remaining()) {
      // We have at least one complete object so
      // copy what we can into current, create a message,
      // and then continue pulling from buffer.

      // Artificially set the limit so we don't overflow
      int extra = buffer.remaining() - current.remaining();
      buffer.limit(buffer.position() + current.remaining());

      // Now copy the data
      current.put(buffer);
      current.flip();

      // Now set the limit back to a good value
      buffer.limit(buffer.position() + extra);

      createMessage(current);

      current = null;
    } else {

      // Not yet a complete object so just copy what we have
      current.put(buffer);
    }
  }

  return messages.size();
}

/**
 * Convert the audio bytes into the stream
 *
 * @param format The audio format being decoded
 * @param audio_bytes The audio byts
 * @param two_bytes_data True if we using double byte data
 * @return The byte bufer of data
 */
private static ByteBuffer convertAudioBytes(
    AudioFormat format, byte[] audio_bytes, boolean two_bytes_data) {
  ByteBuffer dest = ByteBuffer.allocateDirect(audio_bytes.length);
  dest.order(ByteOrder.nativeOrder());
  ByteBuffer src = ByteBuffer.wrap(audio_bytes);
  src.order(ByteOrder.BIG_ENDIAN);
  if (two_bytes_data) {
    ShortBuffer dest_short = dest.asShortBuffer();
    ShortBuffer src_short = src.asShortBuffer();
    while (src_short.hasRemaining()) dest_short.put(src_short.get());
  } else {
    while (src.hasRemaining()) {
      byte b = src.get();
      if (format.getEncoding() == Encoding.PCM_SIGNED) {
        b = (byte) (b + 127);
      }
      dest.put(b);
    }
  }
  dest.rewind();
  return dest;
}

