/**
 * This method serializes the informations of this AHS object to the byte representation defined
 * by the iSCSI Standard.
 *
 * @param dst The destination array to write in.
 * @param offset The start offset in <code>dst</code>.
 * @return The length of used integers of the serialized form of this AHS object.
 * @throws InternetSCSIException If any violation of the iSCSI-Standard emerge.
 */
final int serialize(final ByteBuffer dst, final int offset) throws InternetSCSIException {

  dst.position(offset);
  if (dst.remaining() < length) {
    throw new IllegalArgumentException("Destination array is too small.");
  }

  dst.putShort(length);
  dst.put(type.value());
  dst.put(specificField.get());

  while (specificField.hasRemaining()) {
    dst.putInt(specificField.getInt());
  }

  return length + FIX_SIZE_OVERHEAD;
}

/**
 * @see org.newdawn.slick.opengl.LoadableImageData#loadImage(java.io.InputStream, boolean,
 *     boolean, int[])
 */
public ByteBuffer loadImage(
    InputStream fis, boolean flipped, boolean forceAlpha, int[] transparent) throws IOException {
  if (transparent != null) {
    forceAlpha = true;
  }
  byte red = 0;
  byte green = 0;
  byte blue = 0;
  byte alpha = 0;

  BufferedInputStream bis = new BufferedInputStream(fis, 100000);
  DataInputStream dis = new DataInputStream(bis);

  // Read in the Header
  short idLength = (short) dis.read();
  short colorMapType = (short) dis.read();
  short imageType = (short) dis.read();
  short cMapStart = flipEndian(dis.readShort());
  short cMapLength = flipEndian(dis.readShort());
  short cMapDepth = (short) dis.read();
  short xOffset = flipEndian(dis.readShort());
  short yOffset = flipEndian(dis.readShort());

  width = flipEndian(dis.readShort());
  height = flipEndian(dis.readShort());
  pixelDepth = (short) dis.read();
  if (pixelDepth == 32) {
    forceAlpha = false;
  }

  texWidth = get2Fold(width);
  texHeight = get2Fold(height);

  short imageDescriptor = (short) dis.read();
  if ((imageDescriptor & 0x0020) == 0) {
    flipped = !flipped;
  }

  // Skip image ID
  if (idLength > 0) {
    bis.skip(idLength);
  }

  byte[] rawData = null;
  if ((pixelDepth == 32) || (forceAlpha)) {
    pixelDepth = 32;
    rawData = new byte[texWidth * texHeight * 4];
  } else if (pixelDepth == 24) {
    rawData = new byte[texWidth * texHeight * 3];
  } else {
    throw new RuntimeException("Only 24 and 32 bit TGAs are supported");
  }

  if (pixelDepth == 24) {
    if (flipped) {
      for (int i = height - 1; i >= 0; i--) {
        for (int j = 0; j < width; j++) {
          blue = dis.readByte();
          green = dis.readByte();
          red = dis.readByte();

          int ofs = ((j + (i * texWidth)) * 3);
          rawData[ofs] = red;
          rawData[ofs + 1] = green;
          rawData[ofs + 2] = blue;
        }
      }
    } else {
      for (int i = 0; i < height; i++) {
        for (int j = 0; j < width; j++) {
          blue = dis.readByte();
          green = dis.readByte();
          red = dis.readByte();

          int ofs = ((j + (i * texWidth)) * 3);
          rawData[ofs] = red;
          rawData[ofs + 1] = green;
          rawData[ofs + 2] = blue;
        }
      }
    }
  } else if (pixelDepth == 32) {
    if (flipped) {
      for (int i = height - 1; i >= 0; i--) {
        for (int j = 0; j < width; j++) {
          blue = dis.readByte();
          green = dis.readByte();
          red = dis.readByte();
          if (forceAlpha) {
            alpha = (byte) 255;
          } else {
            alpha = dis.readByte();
          }

          int ofs = ((j + (i * texWidth)) * 4);

          rawData[ofs] = red;
          rawData[ofs + 1] = green;
          rawData[ofs + 2] = blue;
          rawData[ofs + 3] = alpha;

          if (alpha == 0) {
            rawData[ofs + 2] = (byte) 0;
            rawData[ofs + 1] = (byte) 0;
            rawData[ofs] = (byte) 0;
          }
        }
      }
    } else {
      for (int i = 0; i < height; i++) {
        for (int j = 0; j < width; j++) {
          blue = dis.readByte();
          green = dis.readByte();
          red = dis.readByte();
          if (forceAlpha) {
            alpha = (byte) 255;
          } else {
            alpha = dis.readByte();
          }

          int ofs = ((j + (i * texWidth)) * 4);

          if (ByteOrder.nativeOrder() == ByteOrder.BIG_ENDIAN) {
            rawData[ofs] = red;
            rawData[ofs + 1] = green;
            rawData[ofs + 2] = blue;
            rawData[ofs + 3] = alpha;
          } else {
            rawData[ofs] = red;
            rawData[ofs + 1] = green;
            rawData[ofs + 2] = blue;
            rawData[ofs + 3] = alpha;
          }

          if (alpha == 0) {
            rawData[ofs + 2] = 0;
            rawData[ofs + 1] = 0;
            rawData[ofs] = 0;
          }
        }
      }
    }
  }
  fis.close();

  if (transparent != null) {
    for (int i = 0; i < rawData.length; i += 4) {
      boolean match = true;
      for (int c = 0; c < 3; c++) {
        if (rawData[i + c] != transparent[c]) {
          match = false;
        }
      }

      if (match) {
        rawData[i + 3] = 0;
      }
    }
  }

  // Get a pointer to the image memory
  ByteBuffer scratch = BufferUtils.createByteBuffer(rawData.length);
  scratch.put(rawData);

  int perPixel = pixelDepth / 8;
  if (height < texHeight - 1) {
    int topOffset = (texHeight - 1) * (texWidth * perPixel);
    int bottomOffset = (height - 1) * (texWidth * perPixel);
    for (int x = 0; x < texWidth * perPixel; x++) {
      scratch.put(topOffset + x, scratch.get(x));
      scratch.put(
          bottomOffset + (texWidth * perPixel) + x, scratch.get((texWidth * perPixel) + x));
    }
  }
  if (width < texWidth - 1) {
    for (int y = 0; y < texHeight; y++) {
      for (int i = 0; i < perPixel; i++) {
        scratch.put(
            ((y + 1) * (texWidth * perPixel)) - perPixel + i,
            scratch.get(y * (texWidth * perPixel) + i));
        scratch.put(
            (y * (texWidth * perPixel)) + (width * perPixel) + i,
            scratch.get((y * (texWidth * perPixel)) + ((width - 1) * perPixel) + i));
      }
    }
  }

  scratch.flip();

  return scratch;
}

private void handleSubNegotiation(byte b) {
  if (negoState == State.Option && isInterpreatAsControl(b)) {
    negoState = State.Command;
    return;
  }

  // subnegotiation end (SE)
  if (negoState == State.Command && b == TelnetCommand.SE) {
    negoBuffer.flip();

    // window size
    byte c = negoBuffer.get();
    if (c == 0x1f) {
      short width = negoBuffer.getShort();
      short height = negoBuffer.getShort();

      context.setWindowSize(width, height);
      logger.trace("araqne-core: changed negotiate about window size: [{}, {}]", width, height);
    }

    negoBuffer.clear();
    state = State.Data;
    return;
  }

  negoBuffer.put(b);
}

/**
 * Convert the audio bytes into the stream
 *
 * @param format The audio format being decoded
 * @param audio_bytes The audio byts
 * @param two_bytes_data True if we using double byte data
 * @return The byte bufer of data
 */
private static ByteBuffer convertAudioBytes(
    AudioFormat format, byte[] audio_bytes, boolean two_bytes_data) {
  ByteBuffer dest = ByteBuffer.allocateDirect(audio_bytes.length);
  dest.order(ByteOrder.nativeOrder());
  ByteBuffer src = ByteBuffer.wrap(audio_bytes);
  src.order(ByteOrder.BIG_ENDIAN);
  if (two_bytes_data) {
    ShortBuffer dest_short = dest.asShortBuffer();
    ShortBuffer src_short = src.asShortBuffer();
    while (src_short.hasRemaining()) dest_short.put(src_short.get());
  } else {
    while (src.hasRemaining()) {
      byte b = src.get();
      if (format.getEncoding() == Encoding.PCM_SIGNED) {
        b = (byte) (b + 127);
      }
      dest.put(b);
    }
  }
  dest.rewind();
  return dest;
}

private static ByteBuffer convertAudioBytes(byte[] audio_bytes, boolean two_bytes_data) {
  ByteBuffer dest = ByteBuffer.allocateDirect(audio_bytes.length);
  dest.order(ByteOrder.nativeOrder());
  ByteBuffer src = ByteBuffer.wrap(audio_bytes);
  src.order(ByteOrder.LITTLE_ENDIAN);
  if (two_bytes_data) {
    ShortBuffer dest_short = dest.asShortBuffer();
    ShortBuffer src_short = src.asShortBuffer();
    while (src_short.hasRemaining()) dest_short.put(src_short.get());
  } else {
    while (src.hasRemaining()) dest.put(src.get());
  }
  dest.rewind();
  return dest;
}

