public void testDirectByteBufferHasArray() throws Exception {
  ByteBuffer b = ByteBuffer.allocateDirect(10);
  assertTrue(b.isDirect());
  // Check the buffer has an array of the right size.
  assertTrue(b.hasArray());
  assertEquals(0, b.arrayOffset());
  byte[] array = b.array();
  assertEquals(10, array.length);
  // Check that writes to the array show up in the buffer.
  assertEquals(0, b.get(0));
  array[0] = 1;
  assertEquals(1, b.get(0));
  // Check that writes to the buffer show up in the array.
  assertEquals(1, array[0]);
  b.put(0, (byte) 0);
  assertEquals(0, array[0]);
}

private Boolean formatBiRequestData(HashMap<String, String> requestData) {
  Charset charset = Charset.forName("UTF-8");
  ByteBuffer biLogData = ByteBuffer.allocate(2048);
  // timestamp
  biLogData.putInt(LogUtil.getTimestamp());
  // timestamp
  if (null != requestData.get("time")) {
    biLogData.putInt(Integer.parseInt(requestData.get("time").toString()));
  } else {
    biLogData.putInt(0);
  }
  // event
  if (null != requestData.get("event")) {
    biLogData.putInt(Integer.parseInt(requestData.get("event").toString()));
  } else {
    biLogData.putInt(-1);
  }
  // uid
  String uid;
  if (null != requestData.get("uid")) {
    if (requestData.get("uid").toString().length() < 50) {
      uid =
          String.format(
              "%0" + (50 - requestData.get("uid").toString().length()) + "d%s",
              0,
              requestData.get("uid"));
    } else {
      uid = requestData.get("uid").substring(0, 50);
    }
  } else {
    uid = String.format("%050d", 0);
  }
  biLogData.put(uid.getBytes(charset));

  // p1->p16
  short datalen = 0;
  String[] params = {"p1", "p2", "p3", "p4", "p5", "p6", "p7", "p8", "p9", "p10"};

  for (String p : params) {
    datalen += null != requestData.get(p) ? requestData.get(p).length() : 0;
  }
  biLogData.putShort(datalen);

  for (String p : params) {
    biLogData.putShort(null != requestData.get(p) ? (short) (requestData.get(p).length()) : 0);
  }

  for (String p : params) {
    if (null != requestData.get(p)) {
      biLogData.put(requestData.get(p).toString().getBytes(charset));
    }
  }

  // write to bi
  biLogData.flip();
  byte[] logContent = new byte[biLogData.limit()];
  biLogData.get(logContent);
  biLogData.clear();

  Logger.bi(
      null != requestData.get("time")
          ? Integer.parseInt(requestData.get("time"))
          : LogUtil.getTimestamp(),
      logContent);

  return true;
}

/** {@inheritDoc} */
public final void execute() throws InternetSCSIException {

  ProtocolDataUnit protocolDataUnit;

  do {
    protocolDataUnit = connection.receive();
    boolean dataWasRead = false;
    if (protocolDataUnit.getBasicHeaderSegment().getParser() instanceof DataInParser) {
      final DataInParser parser =
          (DataInParser) protocolDataUnit.getBasicHeaderSegment().getParser();

      if (LOGGER.isDebugEnabled()) {
        LOGGER.debug(
            "Remaining, DataSegmentLength: "
                + buffer.remaining()
                + ", "
                + protocolDataUnit.getBasicHeaderSegment().getDataSegmentLength());
      }

      final ByteBuffer dataSegment = protocolDataUnit.getDataSegment();
      while (buffer.hasRemaining() && dataSegment.hasRemaining()) {
        buffer.put(dataSegment.get());
      }
      dataWasRead = true;
      // last message with the status flag set
      if (parser.isStatusFlag() && parser.getStatus() == SCSIStatus.GOOD) {
        // return false;
        return;
      } else if (connection.getSettingAsInt(OperationalTextKey.ERROR_RECOVERY_LEVEL) > 0
          && parser.isAcknowledgeFlag()) {
        // TODO: Test this case
        // send a DataAck
        connection.nextState(new SNACKRequestState(connection, this, parser.getTargetTaskTag()));
        // return true;
        return;
      } else if (protocolDataUnit.getBasicHeaderSegment().getParser()
              instanceof SCSIResponseParser
          && !dataWasRead) {
        readHandleImmediateData(protocolDataUnit);
      }
    }
  } while (!protocolDataUnit.getBasicHeaderSegment().isFinalFlag());

  if (connection.getSettingAsBoolean(OperationalTextKey.IMMEDIATE_DATA)) {
    return;
  } else {
    protocolDataUnit = connection.receive();
    if (protocolDataUnit.getBasicHeaderSegment().getParser() instanceof SCSIResponseParser) {
      readHandleImmediateData(protocolDataUnit);
    }
  }
}

private void testRelativePositions(ByteBuffer b) throws Exception {
  // gets
  b.position(0);
  b.get();
  assertEquals(1, b.position());

  byte[] buf = new byte[5];
  b.position(0);
  b.get(buf);
  assertEquals(5, b.position());

  b.position(0);
  b.get(buf, 1, 3);
  assertEquals(3, b.position());

  b.position(0);
  b.getChar();
  assertEquals(2, b.position());

  b.position(0);
  b.getDouble();
  assertEquals(8, b.position());

  b.position(0);
  b.getFloat();
  assertEquals(4, b.position());

  b.position(0);
  b.getInt();
  assertEquals(4, b.position());

  b.position(0);
  b.getLong();
  assertEquals(8, b.position());

  b.position(0);
  b.getShort();
  assertEquals(2, b.position());

  // puts
  b.position(0);
  b.put((byte) 0);
  assertEquals(1, b.position());

  b.position(0);
  b.put(buf);
  assertEquals(5, b.position());

  b.position(0);
  b.put(buf, 1, 3);
  assertEquals(3, b.position());

  b.position(0);
  b.putChar('x');
  assertEquals(2, b.position());

  b.position(0);
  b.putDouble(0);
  assertEquals(8, b.position());

  b.position(0);
  b.putFloat(0);
  assertEquals(4, b.position());

  b.position(0);
  b.putInt(0);
  assertEquals(4, b.position());

  b.position(0);
  b.putLong(0);
  assertEquals(8, b.position());

  b.position(0);
  b.putShort((short) 0);
  assertEquals(2, b.position());
}

/**
 * @see org.newdawn.slick.opengl.LoadableImageData#loadImage(java.io.InputStream, boolean,
 *     boolean, int[])
 */
public ByteBuffer loadImage(
    InputStream fis, boolean flipped, boolean forceAlpha, int[] transparent) throws IOException {
  if (transparent != null) {
    forceAlpha = true;
  }
  byte red = 0;
  byte green = 0;
  byte blue = 0;
  byte alpha = 0;

  BufferedInputStream bis = new BufferedInputStream(fis, 100000);
  DataInputStream dis = new DataInputStream(bis);

  // Read in the Header
  short idLength = (short) dis.read();
  short colorMapType = (short) dis.read();
  short imageType = (short) dis.read();
  short cMapStart = flipEndian(dis.readShort());
  short cMapLength = flipEndian(dis.readShort());
  short cMapDepth = (short) dis.read();
  short xOffset = flipEndian(dis.readShort());
  short yOffset = flipEndian(dis.readShort());

  width = flipEndian(dis.readShort());
  height = flipEndian(dis.readShort());
  pixelDepth = (short) dis.read();
  if (pixelDepth == 32) {
    forceAlpha = false;
  }

  texWidth = get2Fold(width);
  texHeight = get2Fold(height);

  short imageDescriptor = (short) dis.read();
  if ((imageDescriptor & 0x0020) == 0) {
    flipped = !flipped;
  }

  // Skip image ID
  if (idLength > 0) {
    bis.skip(idLength);
  }

  byte[] rawData = null;
  if ((pixelDepth == 32) || (forceAlpha)) {
    pixelDepth = 32;
    rawData = new byte[texWidth * texHeight * 4];
  } else if (pixelDepth == 24) {
    rawData = new byte[texWidth * texHeight * 3];
  } else {
    throw new RuntimeException("Only 24 and 32 bit TGAs are supported");
  }

  if (pixelDepth == 24) {
    if (flipped) {
      for (int i = height - 1; i >= 0; i--) {
        for (int j = 0; j < width; j++) {
          blue = dis.readByte();
          green = dis.readByte();
          red = dis.readByte();

          int ofs = ((j + (i * texWidth)) * 3);
          rawData[ofs] = red;
          rawData[ofs + 1] = green;
          rawData[ofs + 2] = blue;
        }
      }
    } else {
      for (int i = 0; i < height; i++) {
        for (int j = 0; j < width; j++) {
          blue = dis.readByte();
          green = dis.readByte();
          red = dis.readByte();

          int ofs = ((j + (i * texWidth)) * 3);
          rawData[ofs] = red;
          rawData[ofs + 1] = green;
          rawData[ofs + 2] = blue;
        }
      }
    }
  } else if (pixelDepth == 32) {
    if (flipped) {
      for (int i = height - 1; i >= 0; i--) {
        for (int j = 0; j < width; j++) {
          blue = dis.readByte();
          green = dis.readByte();
          red = dis.readByte();
          if (forceAlpha) {
            alpha = (byte) 255;
          } else {
            alpha = dis.readByte();
          }

          int ofs = ((j + (i * texWidth)) * 4);

          rawData[ofs] = red;
          rawData[ofs + 1] = green;
          rawData[ofs + 2] = blue;
          rawData[ofs + 3] = alpha;

          if (alpha == 0) {
            rawData[ofs + 2] = (byte) 0;
            rawData[ofs + 1] = (byte) 0;
            rawData[ofs] = (byte) 0;
          }
        }
      }
    } else {
      for (int i = 0; i < height; i++) {
        for (int j = 0; j < width; j++) {
          blue = dis.readByte();
          green = dis.readByte();
          red = dis.readByte();
          if (forceAlpha) {
            alpha = (byte) 255;
          } else {
            alpha = dis.readByte();
          }

          int ofs = ((j + (i * texWidth)) * 4);

          if (ByteOrder.nativeOrder() == ByteOrder.BIG_ENDIAN) {
            rawData[ofs] = red;
            rawData[ofs + 1] = green;
            rawData[ofs + 2] = blue;
            rawData[ofs + 3] = alpha;
          } else {
            rawData[ofs] = red;
            rawData[ofs + 1] = green;
            rawData[ofs + 2] = blue;
            rawData[ofs + 3] = alpha;
          }

          if (alpha == 0) {
            rawData[ofs + 2] = 0;
            rawData[ofs + 1] = 0;
            rawData[ofs] = 0;
          }
        }
      }
    }
  }
  fis.close();

  if (transparent != null) {
    for (int i = 0; i < rawData.length; i += 4) {
      boolean match = true;
      for (int c = 0; c < 3; c++) {
        if (rawData[i + c] != transparent[c]) {
          match = false;
        }
      }

      if (match) {
        rawData[i + 3] = 0;
      }
    }
  }

  // Get a pointer to the image memory
  ByteBuffer scratch = BufferUtils.createByteBuffer(rawData.length);
  scratch.put(rawData);

  int perPixel = pixelDepth / 8;
  if (height < texHeight - 1) {
    int topOffset = (texHeight - 1) * (texWidth * perPixel);
    int bottomOffset = (height - 1) * (texWidth * perPixel);
    for (int x = 0; x < texWidth * perPixel; x++) {
      scratch.put(topOffset + x, scratch.get(x));
      scratch.put(
          bottomOffset + (texWidth * perPixel) + x, scratch.get((texWidth * perPixel) + x));
    }
  }
  if (width < texWidth - 1) {
    for (int y = 0; y < texHeight; y++) {
      for (int i = 0; i < perPixel; i++) {
        scratch.put(
            ((y + 1) * (texWidth * perPixel)) - perPixel + i,
            scratch.get(y * (texWidth * perPixel) + i));
        scratch.put(
            (y * (texWidth * perPixel)) + (width * perPixel) + i,
            scratch.get((y * (texWidth * perPixel)) + ((width - 1) * perPixel) + i));
      }
    }
  }

  scratch.flip();

  return scratch;
}

private void recieveFrame() {
  byte newestByte = this.buffer.get();

  if (newestByte == START_OF_FRAME && !readingState) { // Beginning of Frame
    this.currentFrame = null;
    readingState = true;

  } else if (newestByte == END_OF_FRAME && readingState) { // End of Frame
    readingState = false;
    String textFrame = null;
    // currentFrame will be null if END_OF_FRAME was send directly after
    // START_OF_FRAME, thus we will send 'null' as the sent message.
    if (this.currentFrame != null) {
      try {
        textFrame = new String(this.currentFrame.array(), UTF8_CHARSET);
      } catch (UnsupportedEncodingException ex) {
        // TODO: Fire an 'onError' handler here
        ex.printStackTrace();
        textFrame = "";
      }
    }
    this.wsl.onMessage(this, textFrame);

  } else { // Regular frame data, add to current frame buffer
    ByteBuffer frame =
        ByteBuffer.allocate(
            (this.currentFrame != null ? this.currentFrame.capacity() : 0)
                + this.buffer.capacity());
    if (this.currentFrame != null) {
      this.currentFrame.rewind();
      frame.put(this.currentFrame);
    }
    frame.put(newestByte);
    this.currentFrame = frame;
  }
}

private ByteArrayInputStream nextBuffer() {
  while (idBuffer.hasRemaining()) {
    switch (idBuffer.get()) {
      case 0:
        {
          int len = DataUtils.readVarInt(idBuffer);
          if (skip >= len) {
            skip -= len;
            idBuffer.position(idBuffer.position() + len);
            continue;
          }
          int p = (int) (idBuffer.position() + skip);
          int l = (int) (len - skip);
          idBuffer.position(p + l);
          return new ByteArrayInputStream(idBuffer.array(), p, l);
        }
      case 1:
        {
          int len = DataUtils.readVarInt(idBuffer);
          long key = DataUtils.readVarLong(idBuffer);
          if (skip >= len) {
            skip -= len;
            continue;
          }
          byte[] data = store.getBlock(key);
          int s = (int) skip;
          skip = 0;
          return new ByteArrayInputStream(data, s, data.length - s);
        }
      case 2:
        {
          long len = DataUtils.readVarInt(idBuffer);
          long key = DataUtils.readVarLong(idBuffer);
          if (skip >= len) {
            skip -= len;
            continue;
          }
          byte[] k = store.getBlock(key);
          ByteBuffer newBuffer =
              ByteBuffer.allocate(k.length + idBuffer.limit() - idBuffer.position());
          newBuffer.put(k);
          newBuffer.put(idBuffer);
          newBuffer.flip();
          idBuffer = newBuffer;
          return nextBuffer();
        }
      default:
        throw new IllegalArgumentException("Unsupported id");
    }
  }
  return null;
}

/** See the {@link #readData()} for documentation on the data format. */
private static void writeDatabase(File file, List<String> lines) throws IOException {
  /*
   * 1. A file header, which is the exact contents of {@link FILE_HEADER} encoded
   * as ASCII characters. The purpose of the header is to identify what the file
   * is for, for anyone attempting to open the file.
   * 2. A file version number. If the binary file does not match the reader's expected
   * version, it can ignore it (and regenerate the cache from XML).
   */

  // Drop comments etc
  List<String> words = new ArrayList<String>(lines.size());
  for (String line : lines) {
    if (!line.isEmpty() && Character.isLetter(line.charAt(0))) {
      int end = line.indexOf(WORD_SEPARATOR);
      if (end == -1) {
        end = line.trim().length();
      }
      String typo = line.substring(0, end).trim();
      String replacements = line.substring(end + WORD_SEPARATOR.length()).trim();
      if (replacements.isEmpty()) {
        // We don't support empty replacements
        continue;
      }
      String combined = typo + (char) 0 + replacements;

      words.add(combined);
    }
  }

  byte[][] wordArrays = new byte[words.size()][];
  for (int i = 0, n = words.size(); i < n; i++) {
    String word = words.get(i);
    wordArrays[i] = word.getBytes(Charsets.UTF_8);
  }
  // Sort words, using our own comparator to ensure that it matches the
  // binary search in getTypos()
  Comparator<byte[]> comparator =
      new Comparator<byte[]>() {
        @Override
        public int compare(byte[] o1, byte[] o2) {
          return TypoLookup.compare(o1, 0, (byte) 0, o2, 0, o2.length);
        }
      };
  Arrays.sort(wordArrays, comparator);

  int entryCount = wordArrays.length;
  int capacity = entryCount * BYTES_PER_ENTRY;
  ByteBuffer buffer = ByteBuffer.allocate(capacity);
  buffer.order(ByteOrder.BIG_ENDIAN);
  // 1. A file header, which is the exact contents of {@link FILE_HEADER} encoded
  // as ASCII characters. The purpose of the header is to identify what the file
  // is for, for anyone attempting to open the file.
  buffer.put(FILE_HEADER.getBytes(Charsets.US_ASCII));

  // 2. A file version number. If the binary file does not match the reader's expected
  // version, it can ignore it (and regenerate the cache from XML).
  buffer.put((byte) BINARY_FORMAT_VERSION);

  // 3. The number of words [1 int]
  buffer.putInt(entryCount);

  // 4. Word offset table (one integer per word, pointing to the byte offset in the
  // file (relative to the beginning of the file) where each word begins.
  // The words are always sorted alphabetically.
  int wordOffsetTable = buffer.position();

  // Reserve enough room for the offset table here: we will backfill it with pointers
  // as we're writing out the data structures below
  for (int i = 0, n = entryCount; i < n; i++) {
    buffer.putInt(0);
  }

  int nextEntry = buffer.position();
  int nextOffset = wordOffsetTable;

  // 7. Word entry table. Each word entry consists of the word, followed by the byte 0
  // as a terminator, followed by a comma separated list of suggestions (which
  // may be empty), or a final 0.
  for (int i = 0; i < entryCount; i++) {
    byte[] word = wordArrays[i];
    buffer.position(nextOffset);
    buffer.putInt(nextEntry);
    nextOffset = buffer.position();
    buffer.position(nextEntry);

    buffer.put(word); // already embeds 0 to separate typo from words
    buffer.put((byte) 0);

    nextEntry = buffer.position();
  }

  int size = buffer.position();
  assert size <= buffer.limit();
  buffer.mark();

  if (WRITE_STATS) {
    System.out.println("Wrote " + words.size() + " word entries");
    System.out.print("Actual binary size: " + size + " bytes");
    System.out.println(String.format(" (%.1fM)", size / (1024 * 1024.f)));

    System.out.println("Allocated size: " + (entryCount * BYTES_PER_ENTRY) + " bytes");
    System.out.println("Required bytes per entry: " + (size / entryCount) + " bytes");
  }

  // Now dump this out as a file
  // There's probably an API to do this more efficiently; TODO: Look into this.
  byte[] b = new byte[size];
  buffer.rewind();
  buffer.get(b);
  FileOutputStream output = Files.newOutputStreamSupplier(file).getOutput();
  output.write(b);
  output.close();
}

protected List<Framedata> translateRegularFrame(ByteBuffer buffer) throws InvalidDataException {
  while (buffer.hasRemaining()) {
    byte newestByte = buffer.get();
    if (newestByte == START_OF_FRAME) { // Beginning of Frame
      if (readingState) return null;
      readingState = true;
    } else if (newestByte == END_OF_FRAME) { // End of Frame
      if (!readingState) return null;
      // currentFrame will be null if END_OF_FRAME was send directly after
      // START_OF_FRAME, thus we will send 'null' as the sent message.
      if (this.currentFrame != null) {
        currentFrame.flip();
        FramedataImpl1 curframe = new FramedataImpl1();
        curframe.setPayload(currentFrame);
        curframe.setFin(true);
        curframe.setOptcode(inframe ? Opcode.CONTINUOUS : Opcode.TEXT);
        readyframes.add(curframe);
        this.currentFrame = null;
        buffer.mark();
      }
      readingState = false;
      inframe = false;
    } else if (readingState) { // Regular frame data, add to current frame buffer //TODO This code
                               // is very expensive and slow
      if (currentFrame == null) {
        currentFrame = createBuffer();
      } else if (!currentFrame.hasRemaining()) {
        currentFrame = increaseBuffer(currentFrame);
      }
      currentFrame.put(newestByte);
    } else {
      return null;
    }
  }
  if (readingState) {
    FramedataImpl1 curframe = new FramedataImpl1();
    currentFrame.flip();
    curframe.setPayload(currentFrame);
    curframe.setFin(false);
    curframe.setOptcode(inframe ? Opcode.CONTINUOUS : Opcode.TEXT);
    inframe = true;
    readyframes.add(curframe);
  }

  List<Framedata> frames = readyframes;
  readyframes = new LinkedList<Framedata>();
  this.currentFrame = null;
  return frames;
}

/**
 * @see org.newdawn.slick.opengl.LoadableImageData#loadImage(java.io.InputStream, boolean,
 *     boolean, int[])
 */
public ByteBuffer loadImage(
    InputStream fis, boolean flipped, boolean forceAlpha, int[] transparent) throws IOException {
  if (transparent != null) {
    forceAlpha = true;
  }
  byte red = 0;
  byte green = 0;
  byte blue = 0;
  byte alpha = 0;

  BufferedInputStream bis = new BufferedInputStream(fis, 100000);
  DataInputStream dis = new DataInputStream(bis);

  // Read in the Header
  short idLength = (short) dis.read();
  short colorMapType = (short) dis.read();
  short imageType = (short) dis.read();
  short cMapStart = flipEndian(dis.readShort());
  short cMapLength = flipEndian(dis.readShort());
  short cMapDepth = (short) dis.read();
  short xOffset = flipEndian(dis.readShort());
  short yOffset = flipEndian(dis.readShort());

  width = flipEndian(dis.readShort());
  height = flipEndian(dis.readShort());
  pixelDepth = (short) dis.read();
  if (pixelDepth == 32) {
    forceAlpha = false;
  }

  texWidth = get2Fold(width);
  texHeight = get2Fold(height);

  short imageDescriptor = (short) dis.read();
  if ((imageDescriptor & 0x0020) == 0) {
    flipped = !flipped;
  }

  // Skip image ID
  if (idLength > 0) {
    bis.skip(idLength);
  }

  byte[] rawData = null;
  if ((pixelDepth == 32) || (forceAlpha)) {
    pixelDepth = 32;
    rawData = new byte[texWidth * texHeight * 4];
  } else if (pixelDepth == 24) {
    rawData = new byte[texWidth * texHeight * 3];
  } else {
    throw new RuntimeException("Only 24 and 32 bit TGAs are supported");
  }

  if (pixelDepth == 24) {
    if (flipped) {
      for (int i = height - 1; i >= 0; i--) {
        for (int j = 0; j < width; j++) {
          blue = dis.readByte();
          green = dis.readByte();
          red = dis.readByte();

          int ofs = ((j + (i * texWidth)) * 3);
          rawData[ofs] = red;
          rawData[ofs + 1] = green;
          rawData[ofs + 2] = blue;
        }
      }
    } else {
      for (int i = 0; i < height; i++) {
        for (int j = 0; j < width; j++) {
          blue = dis.readByte();
          green = dis.readByte();
          red = dis.readByte();

          int ofs = ((j + (i * texWidth)) * 3);
          rawData[ofs] = red;
          rawData[ofs + 1] = green;
          rawData[ofs + 2] = blue;
        }
      }
    }
  } else if (pixelDepth == 32) {
    if (flipped) {
      for (int i = height - 1; i >= 0; i--) {
        for (int j = 0; j < width; j++) {
          blue = dis.readByte();
          green = dis.readByte();
          red = dis.readByte();
          if (forceAlpha) {
            alpha = (byte) 255;
          } else {
            alpha = dis.readByte();
          }

          int ofs = ((j + (i * texWidth)) * 4);

          rawData[ofs] = red;
          rawData[ofs + 1] = green;
          rawData[ofs + 2] = blue;
          rawData[ofs + 3] = alpha;

          if (alpha == 0) {
            rawData[ofs + 2] = (byte) 0;
            rawData[ofs + 1] = (byte) 0;
            rawData[ofs] = (byte) 0;
          }
        }
      }
    } else {
      for (int i = 0; i < height; i++) {
        for (int j = 0; j < width; j++) {
          blue = dis.readByte();
          green = dis.readByte();
          red = dis.readByte();
          if (forceAlpha) {
            alpha = (byte) 255;
          } else {
            alpha = dis.readByte();
          }

          int ofs = ((j + (i * texWidth)) * 4);

          if (ByteOrder.nativeOrder() == ByteOrder.BIG_ENDIAN) {
            rawData[ofs] = red;
            rawData[ofs + 1] = green;
            rawData[ofs + 2] = blue;
            rawData[ofs + 3] = alpha;
          } else {
            rawData[ofs] = red;
            rawData[ofs + 1] = green;
            rawData[ofs + 2] = blue;
            rawData[ofs + 3] = alpha;
          }

          if (alpha == 0) {
            rawData[ofs + 2] = 0;
            rawData[ofs + 1] = 0;
            rawData[ofs] = 0;
          }
        }
      }
    }
  }
  fis.close();

  if (transparent != null) {
    for (int i = 0; i < rawData.length; i += 4) {
      boolean match = true;
      for (int c = 0; c < 3; c++) {
        if (rawData[i + c] != transparent[c]) {
          match = false;
        }
      }

      if (match) {
        rawData[i + 3] = 0;
      }
    }
  }

  // Get a pointer to the image memory
  ByteBuffer scratch = BufferUtils.createByteBuffer(rawData.length);
  scratch.put(rawData);

  int perPixel = pixelDepth / 8;
  if (height < texHeight - 1) {
    int topOffset = (texHeight - 1) * (texWidth * perPixel);
    int bottomOffset = (height - 1) * (texWidth * perPixel);
    for (int x = 0; x < texWidth * perPixel; x++) {
      scratch.put(topOffset + x, scratch.get(x));
      scratch.put(
          bottomOffset + (texWidth * perPixel) + x, scratch.get((texWidth * perPixel) + x));
    }
  }
  if (width < texWidth - 1) {
    for (int y = 0; y < texHeight; y++) {
      for (int i = 0; i < perPixel; i++) {
        scratch.put(
            ((y + 1) * (texWidth * perPixel)) - perPixel + i,
            scratch.get(y * (texWidth * perPixel) + i));
        scratch.put(
            (y * (texWidth * perPixel)) + (width * perPixel) + i,
            scratch.get((y * (texWidth * perPixel)) + ((width - 1) * perPixel) + i));
      }
    }
  }

  scratch.flip();

  return scratch;
}

