private void completeFinalBuffer(ByteBuffer buffer) {
  if (finalBuffer.position() == 0) return;

  while (buffer.remaining() > 0 && finalBuffer.remaining() > 0) {
    finalBuffer.put(buffer.get());
  }

  if (finalBuffer.remaining() == 0) {
    transform(finalBuffer.array(), 0);
    finalBuffer.rewind();
  }
}

private void requestLogicServerRoom(ByteBuffer buffer) {
  int length = Integer.MIN_VALUE;
  int type = Integer.MIN_VALUE;

  int roomType = Integer.MIN_VALUE;
  int roomId = Integer.MIN_VALUE;
  String roomTitle = null;
  int peopleCount = Integer.MIN_VALUE;
  String ownerGuid = null;
  List<String> playerList = new ArrayList<String>();
  Map<String, String> heroList = new HashMap<String, String>();
  Map<String, Integer> groupList = new HashMap<String, Integer>();
  Map<String, Integer> positionList = new HashMap<String, Integer>();
  String playerGuid = null;
  int playerGroup = Integer.MIN_VALUE;
  int playerPosition = Integer.MIN_VALUE;
  String heroCardId = null;
  byte[] dst;
  while (buffer.hasRemaining()) {
    length = buffer.getInt();
    type = buffer.get();
    if (type == EnumProtocol.TYPE_INT) {
      if (roomType == Integer.MIN_VALUE) {
        roomType = buffer.getInt();
      } else if (roomId == Integer.MIN_VALUE) {
        roomId = buffer.getInt();
      } else if (peopleCount == Integer.MIN_VALUE) {
        peopleCount = buffer.getInt();
      } else if (playerGroup == Integer.MIN_VALUE) {
        playerGroup = buffer.getInt();
      } else if (playerPosition == Integer.MIN_VALUE) {
        playerPosition = buffer.getInt();
      }
    } else if (type == EnumProtocol.TYPE_STRING) {
      if (roomTitle == null) {
        dst = new byte[length];
        buffer.get(dst);
        try {
          roomTitle = new String(dst, "UTF-8");
        } catch (UnsupportedEncodingException e) {
          e.printStackTrace();
        }
      } else if (ownerGuid == null) {
        dst = new byte[length];
        buffer.get(dst);
        try {
          ownerGuid = new String(dst, "UTF-8");
        } catch (UnsupportedEncodingException e) {
          e.printStackTrace();
        }
      } else if (heroList.size() < peopleCount) {
        if (playerGuid == null) {
          dst = new byte[length];
          buffer.get(dst);
          try {
            playerGuid = new String(dst, "UTF-8");
          } catch (UnsupportedEncodingException e) {
            e.printStackTrace();
          }
        } else if (heroCardId == null) {
          dst = new byte[length];
          buffer.get(dst);
          try {
            heroCardId = new String(dst, "UTF-8");
          } catch (UnsupportedEncodingException e) {
            e.printStackTrace();
          }
        }
      }

      if (playerGuid != null
          && playerGroup != Integer.MIN_VALUE
          && playerPosition != Integer.MIN_VALUE
          && heroCardId != null) {
        heroList.put(playerGuid, heroCardId);
        groupList.put(playerGuid, playerGroup);
        positionList.put(playerGuid, playerPosition);

        playerGuid = null;
        playerGroup = Integer.MIN_VALUE;
        playerPosition = Integer.MIN_VALUE;
        heroCardId = null;
      }
    }
  }

  if (roomType == 0) {
    BattleRoom room = BattleHall.getInstance().addRoom(roomId);
    if (room != null) {
      room.setTitle(roomTitle);
      room.setPeopleCount(peopleCount);
      room.initialize();
      Iterator<Entry<String, String>> it = heroList.entrySet().iterator();
      Entry<String, String> en;
      String guid;
      while (it.hasNext()) {
        en = it.next();
        guid = en.getKey();
        room.addHeroCardId(guid, en.getValue());
        room.addPlayerGuid(guid, groupList.get(guid));
        room.addPlayerPosition(guid, positionList.get(guid));
      }

      log.info("[RequestRoom] , room id = " + roomId);
      // TODO GameServer
      DatagramPacket p = DatagramPacketPool.getInstance().getObject();
      p.setSocketAddress(LogicServer.gameServerAdd);

      ByteBuffer bf = BufferPool.getInstance().getBuffer();

      bf.putShort(EnumProtocol.BASE_REQUEST_LOGIC_SERVER_ROOM_CONFIRM);

      bf.putInt(4);
      bf.put((byte) EnumProtocol.TYPE_INT);
      bf.putInt(roomType);

      bf.putInt(4);
      bf.put((byte) EnumProtocol.TYPE_INT);
      bf.putInt(roomId);

      bf.flip();

      byte[] dest = new byte[bf.remaining()];
      bf.get(dest, 0, dest.length);

      p.setData(dest);

      GameServerConnector.getInstance().send(p);
    } else {
      log.error("[RequestRoom] , room id = " + roomId);
      // TODO GameServer
    }
  }
}

public void processTo(Graphics2D g, Object graphicsCalls) {
  calls.clear();

  calls.mark(); // Mark for rollback
  try {
    calls.put((byte[]) graphicsCalls);
  } catch (BufferOverflowException e) {
    calls.reset(); // Rollback buffer
    if (reallocBuffer()) {
      processTo(g, graphicsCalls);
      return; // must exit here
    }
    calls.clear();
  }

  calls.flip();
  calls.order(calls.get() == 1 ? ByteOrder.BIG_ENDIAN : ByteOrder.LITTLE_ENDIAN);

  while (calls.remaining() > 0) {
    try {
      processQueuedCall(g);
    } catch (Exception e) {
      e.printStackTrace();
      // FOR-DEBUG } catch (Error e) {
      // FOR-DEBUG calls.position(lastPos - 4);
    }
  }
}

public static ByteBuffer readLine(ByteBuffer buf) {
  ByteBuffer sbuf = ByteBuffer.allocate(buf.remaining());
  byte prev = '0';
  byte cur = '0';
  while (buf.hasRemaining()) {
    prev = cur;
    cur = buf.get();
    sbuf.put(cur);
    if (prev == (byte) '\r' && cur == (byte) '\n') {
      sbuf.limit(sbuf.position() - 2);
      sbuf.position(0);
      return sbuf;
    }
  }
  // ensure that there wont be any bytes skipped
  buf.position(buf.position() - sbuf.position());
  return null;
}

/**
 * Should be called when a Selector has a key that is writable for this WebSocket's SocketChannel
 * connection.
 *
 * @throws IOException When socket related I/O errors occur.
 * @throws NoSuchAlgorithmException
 */
void handleRead() throws IOException, NoSuchAlgorithmException {

  int bytesRead = -1;

  try {
    socketBuffer.rewind();
    bytesRead = this.socketChannel.read(this.socketBuffer);
  } catch (Exception ex) {
  }

  if (bytesRead == -1) {
    close();
  } else if (bytesRead > 0) {
    for (int i = 0; i < bytesRead; i++) {
      buffer.rewind();
      buffer.put(socketBuffer.get(i));

      this.buffer.rewind();

      if (!this.handshakeComplete) recieveHandshake();
      else recieveFrame();
    }
  }
}

