 */
private byte[] handleDoFinalBytes(ByteBuffer decryptBuffer, int decryptDataLen) {
  byte decryptData = 0;
  int handledArrayLen = 0;
  for (int i = 0; i < decryptDataLen; i++) {
    decryptData = decryptBuffer.get(i);
    if (decryptData != 0 && decryptData != -1 && decryptData != 1) {
      decryptBuffer.put(decryptData);
      handledArrayLen++;
    }
  }
  byte[] handledArray = new byte[handledArrayLen];
  decryptBuffer.position(decryptDataLen);
  decryptBuffer.get(handledArray, 0, handledArrayLen);
  return handledArray;
}

private static ByteBuffer convertIconMask(ByteBuffer icon, int width, int height) {
  ByteBuffer icon_mask = BufferUtils.createByteBuffer((icon.capacity() / 4) / 8);
  int x;
  int y;
  byte a;

  int depth = 4;

  for (y = 0; y < height; y++) {
    for (x = 0; x < width; x++) {
      a = icon.get((x * 4) + (y * width * 4) + 3);

      int mask_index = x + y * width;
      int mask_byte_index = mask_index / 8;
      int mask_bit_index = mask_index % 8;
      byte bit = (((int) a) & 0xff) >= 127 ? (byte) 1 : (byte) 0;
      byte new_byte = (byte) ((icon_mask.get(mask_byte_index) | (bit << mask_bit_index)) & 0xff);
      icon_mask.put(mask_byte_index, new_byte);
    }
  }
  return icon_mask;
}

