protected void cryptoIn(ByteBuffer source_buffer, ByteBuffer target_buffer) throws IOException {

  int rem = source_buffer.remaining();

  for (int i = 0; i < rem; i++) {

    byte b = source_buffer.get();

    b = (byte) (b ^ mask[read_position++]);

    target_buffer.put(b);

    if (read_position == mask.length) {

      read_position = 0;
    }
  }
}

private void processFrame(WiFiFrame frame) {
  // System.out.println("wifi --> " + frame.print());
  PacketPayload payload = (PacketPayload) frame.findHeader(PacketPayload.class);

  boolean asciiFound = false;
  if (payload == null) {
    return;
  }

  ByteBuffer payloadBuffer = payload.toByteBuffer();
  ByteBuffer lineBuffer = ByteBuffer.allocate(1024);

  while (payloadBuffer.remaining() > 0) {
    byte b = payloadBuffer.get();
    if (b >= 32 && b <= 126) {
      if (lineBuffer.remaining() > 0) {
        lineBuffer.put(b);
      }
    } else {
      if (lineBuffer.position() >= MIN_LENGTH) {
        if (!asciiFound) {
          outputBuffer.append(frame.print() + "\n");
        }
        lineBuffer.flip();
        printBuffer(lineBuffer);
        asciiFound = true;
      }
      lineBuffer.clear();
    }
  }

  if (asciiFound) {
    outputBuffer.append("\n");
    // ctx.extra("wifi-ascii", outputBuffer.toString());
    resetBuffer();
  }
}

/**
 * Serializes the packet. Will compute and set the following fields if they are set to specific
 * values at the time serialize is called: -checksum : 0 -length : 0
 */
public byte[] serialize() {
  int length;
  if (dataOffset == 0) dataOffset = 5; // default header length
  length = dataOffset << 2;
  byte[] payloadData = null;
  if (payload != null) {
    payload.setParent(this);
    payloadData = payload.serialize();
    length += payloadData.length;
  }

  byte[] data = new byte[length];
  ByteBuffer bb = ByteBuffer.wrap(data);

  bb.putShort(this.sourcePort);
  bb.putShort(this.destinationPort);
  bb.putInt(this.sequence);
  bb.putInt(this.acknowledge);
  bb.putShort((short) (this.flags | (dataOffset << 12)));
  bb.putShort(this.windowSize);
  bb.putShort(this.checksum);
  bb.putShort(this.urgentPointer);
  if (dataOffset > 5) {
    int padding;
    bb.put(options);
    padding = (dataOffset << 2) - 20 - options.length;
    for (int i = 0; i < padding; i++) bb.put((byte) 0);
  }
  if (payloadData != null) bb.put(payloadData);

  if (this.parent != null && this.parent instanceof IPv4)
    ((IPv4) this.parent).setProtocol(IPv4.PROTOCOL_TCP);

  // compute checksum if needed
  if (this.checksum == 0) {
    bb.rewind();
    int accumulation = 0;

    // compute pseudo header mac
    if (this.parent != null && this.parent instanceof IPv4) {
      IPv4 ipv4 = (IPv4) this.parent;
      accumulation +=
          ((ipv4.getSourceAddress() >> 16) & 0xffff) + (ipv4.getSourceAddress() & 0xffff);
      accumulation +=
          ((ipv4.getDestinationAddress() >> 16) & 0xffff)
              + (ipv4.getDestinationAddress() & 0xffff);
      accumulation += ipv4.getProtocol() & 0xff;
      accumulation += length & 0xffff;
    }

    for (int i = 0; i < length / 2; ++i) {
      accumulation += 0xffff & bb.getShort();
    }
    // pad to an even number of shorts
    if (length % 2 > 0) {
      accumulation += (bb.get() & 0xff) << 8;
    }

    accumulation = ((accumulation >> 16) & 0xffff) + (accumulation & 0xffff);
    this.checksum = (short) (~accumulation & 0xffff);
    bb.putShort(16, this.checksum);
  }
  return data;
}

public void testCopyBinaryWithByteBuffer() throws Exception {
  byte[] bytes = new byte[] {0, 1, 2, 3, 4, 5};
  ByteBuffer b = ByteBuffer.wrap(bytes);
  ByteBuffer bCopy = TBaseHelper.copyBinary(b);
  assertEquals(b, bCopy);
  assertEquals(0, b.position());

  b = ByteBuffer.allocateDirect(6);
  b.put(bytes);
  b.position(0);
  bCopy = TBaseHelper.copyBinary(b);
  assertEquals(6, b.remaining());
  assertEquals(0, b.position());
  assertEquals(b, bCopy);

  b.mark();
  b.get();
  bCopy = TBaseHelper.copyBinary(b);
  assertEquals(ByteBuffer.wrap(bytes, 1, 5), bCopy);
  assertEquals(1, b.position());
  b.reset();
  assertEquals(0, b.position());

  assertNull(TBaseHelper.copyBinary((ByteBuffer) null));
}

/**
 * @see org.newdawn.slick.opengl.LoadableImageData#loadImage(java.io.InputStream, boolean,
 *     boolean, int[])
 */
public ByteBuffer loadImage(
    InputStream fis, boolean flipped, boolean forceAlpha, int[] transparent) throws IOException {
  if (transparent != null) {
    forceAlpha = true;
  }
  byte red = 0;
  byte green = 0;
  byte blue = 0;
  byte alpha = 0;

  BufferedInputStream bis = new BufferedInputStream(fis, 100000);
  DataInputStream dis = new DataInputStream(bis);

  // Read in the Header
  short idLength = (short) dis.read();
  short colorMapType = (short) dis.read();
  short imageType = (short) dis.read();
  short cMapStart = flipEndian(dis.readShort());
  short cMapLength = flipEndian(dis.readShort());
  short cMapDepth = (short) dis.read();
  short xOffset = flipEndian(dis.readShort());
  short yOffset = flipEndian(dis.readShort());

  width = flipEndian(dis.readShort());
  height = flipEndian(dis.readShort());
  pixelDepth = (short) dis.read();
  if (pixelDepth == 32) {
    forceAlpha = false;
  }

  texWidth = get2Fold(width);
  texHeight = get2Fold(height);

  short imageDescriptor = (short) dis.read();
  if ((imageDescriptor & 0x0020) == 0) {
    flipped = !flipped;
  }

  // Skip image ID
  if (idLength > 0) {
    bis.skip(idLength);
  }

  byte[] rawData = null;
  if ((pixelDepth == 32) || (forceAlpha)) {
    pixelDepth = 32;
    rawData = new byte[texWidth * texHeight * 4];
  } else if (pixelDepth == 24) {
    rawData = new byte[texWidth * texHeight * 3];
  } else {
    throw new RuntimeException("Only 24 and 32 bit TGAs are supported");
  }

  if (pixelDepth == 24) {
    if (flipped) {
      for (int i = height - 1; i >= 0; i--) {
        for (int j = 0; j < width; j++) {
          blue = dis.readByte();
          green = dis.readByte();
          red = dis.readByte();

          int ofs = ((j + (i * texWidth)) * 3);
          rawData[ofs] = red;
          rawData[ofs + 1] = green;
          rawData[ofs + 2] = blue;
        }
      }
    } else {
      for (int i = 0; i < height; i++) {
        for (int j = 0; j < width; j++) {
          blue = dis.readByte();
          green = dis.readByte();
          red = dis.readByte();

          int ofs = ((j + (i * texWidth)) * 3);
          rawData[ofs] = red;
          rawData[ofs + 1] = green;
          rawData[ofs + 2] = blue;
        }
      }
    }
  } else if (pixelDepth == 32) {
    if (flipped) {
      for (int i = height - 1; i >= 0; i--) {
        for (int j = 0; j < width; j++) {
          blue = dis.readByte();
          green = dis.readByte();
          red = dis.readByte();
          if (forceAlpha) {
            alpha = (byte) 255;
          } else {
            alpha = dis.readByte();
          }

          int ofs = ((j + (i * texWidth)) * 4);

          rawData[ofs] = red;
          rawData[ofs + 1] = green;
          rawData[ofs + 2] = blue;
          rawData[ofs + 3] = alpha;

          if (alpha == 0) {
            rawData[ofs + 2] = (byte) 0;
            rawData[ofs + 1] = (byte) 0;
            rawData[ofs] = (byte) 0;
          }
        }
      }
    } else {
      for (int i = 0; i < height; i++) {
        for (int j = 0; j < width; j++) {
          blue = dis.readByte();
          green = dis.readByte();
          red = dis.readByte();
          if (forceAlpha) {
            alpha = (byte) 255;
          } else {
            alpha = dis.readByte();
          }

          int ofs = ((j + (i * texWidth)) * 4);

          if (ByteOrder.nativeOrder() == ByteOrder.BIG_ENDIAN) {
            rawData[ofs] = red;
            rawData[ofs + 1] = green;
            rawData[ofs + 2] = blue;
            rawData[ofs + 3] = alpha;
          } else {
            rawData[ofs] = red;
            rawData[ofs + 1] = green;
            rawData[ofs + 2] = blue;
            rawData[ofs + 3] = alpha;
          }

          if (alpha == 0) {
            rawData[ofs + 2] = 0;
            rawData[ofs + 1] = 0;
            rawData[ofs] = 0;
          }
        }
      }
    }
  }
  fis.close();

  if (transparent != null) {
    for (int i = 0; i < rawData.length; i += 4) {
      boolean match = true;
      for (int c = 0; c < 3; c++) {
        if (rawData[i + c] != transparent[c]) {
          match = false;
        }
      }

      if (match) {
        rawData[i + 3] = 0;
      }
    }
  }

  // Get a pointer to the image memory
  ByteBuffer scratch = BufferUtils.createByteBuffer(rawData.length);
  scratch.put(rawData);

  int perPixel = pixelDepth / 8;
  if (height < texHeight - 1) {
    int topOffset = (texHeight - 1) * (texWidth * perPixel);
    int bottomOffset = (height - 1) * (texWidth * perPixel);
    for (int x = 0; x < texWidth * perPixel; x++) {
      scratch.put(topOffset + x, scratch.get(x));
      scratch.put(
          bottomOffset + (texWidth * perPixel) + x, scratch.get((texWidth * perPixel) + x));
    }
  }
  if (width < texWidth - 1) {
    for (int y = 0; y < texHeight; y++) {
      for (int i = 0; i < perPixel; i++) {
        scratch.put(
            ((y + 1) * (texWidth * perPixel)) - perPixel + i,
            scratch.get(y * (texWidth * perPixel) + i));
        scratch.put(
            (y * (texWidth * perPixel)) + (width * perPixel) + i,
            scratch.get((y * (texWidth * perPixel)) + ((width - 1) * perPixel) + i));
      }
    }
  }

  scratch.flip();

  return scratch;
}

public static byte bytesToByte(byte[] data) {
  ByteBuffer buf = ByteBuffer.allocate(1);
  buf.put(data);
  buf.flip();
  return buf.get();
}

@Override
public boolean flush() throws IOException {
  if (currentBuffer == null) {
    if (anyAreSet(state, NEXT_SHUTDOWN)) {
      return next.flush();
    } else {
      return true;
    }
  }
  try {
    boolean nextCreated = false;
    try {
      if (anyAreSet(state, SHUTDOWN)) {
        if (anyAreSet(state, NEXT_SHUTDOWN)) {
          return next.flush();
        } else {
          if (!performFlushIfRequired()) {
            return false;
          }
          // if the deflater has not been fully flushed we need to flush it
          if (!deflater.finished()) {
            deflateData(false);
            // if could not fully flush
            if (!deflater.finished()) {
              return false;
            }
          }
          final ByteBuffer buffer = currentBuffer.getBuffer();
          if (allAreClear(state, WRITTEN_TRAILER)) {
            state |= WRITTEN_TRAILER;
            byte[] data = getTrailer();
            if (data != null) {
              Connectors.updateResponseBytesSent(exchange, data.length);
              if (additionalBuffer != null) {
                byte[] newData = new byte[additionalBuffer.remaining() + data.length];
                int pos = 0;
                while (additionalBuffer.hasRemaining()) {
                  newData[pos++] = additionalBuffer.get();
                }
                for (byte aData : data) {
                  newData[pos++] = aData;
                }
                this.additionalBuffer = ByteBuffer.wrap(newData);
              } else if (anyAreSet(state, FLUSHING_BUFFER)
                  && buffer.capacity() - buffer.remaining() >= data.length) {
                buffer.compact();
                buffer.put(data);
                buffer.flip();
              } else if (data.length <= buffer.remaining()
                  && !anyAreSet(state, FLUSHING_BUFFER)) {
                buffer.put(data);
              } else {
                additionalBuffer = ByteBuffer.wrap(data);
              }
            }
          }

          // ok the deflater is flushed, now we need to flush the buffer
          if (!anyAreSet(state, FLUSHING_BUFFER)) {
            buffer.flip();
            state |= FLUSHING_BUFFER;
            if (next == null) {
              nextCreated = true;
              this.next = createNextChannel();
            }
          }
          if (performFlushIfRequired()) {
            state |= NEXT_SHUTDOWN;
            freeBuffer();
            next.terminateWrites();
            return next.flush();
          } else {
            return false;
          }
        }
      } else {
        if (allAreClear(state, FLUSHING_BUFFER)) {
          if (next == null) {
            nextCreated = true;
            this.next = createNextChannel();
          }
          deflateData(true);
          if (allAreClear(state, FLUSHING_BUFFER)) {
            // deflateData can cause this to be change
            currentBuffer.getBuffer().flip();
            this.state |= FLUSHING_BUFFER;
          }
        }
        if (!performFlushIfRequired()) {
          return false;
        }
        return next.flush();
      }
    } finally {
      if (nextCreated) {
        if (anyAreSet(state, WRITES_RESUMED) && !anyAreSet(state, NEXT_SHUTDOWN)) {
          try {
            next.resumeWrites();
          } catch (Throwable e) {
            UndertowLogger.REQUEST_LOGGER.debug("Failed to resume", e);
          }
        }
      }
    }
  } catch (IOException | RuntimeException | Error e) {
    freeBuffer();
    throw e;
  }
}

public void optimize() {
  calcInterleavedSize();
  interleavedBuffer = BufferUtil.createByteBuffer(interleavedSize);
  // interleavedBuffer = ByteBuffer.allocateDirect(interleavedSize);
  // interleavedBuffer.order(ByteOrder.nativeOrder());
  VertexBuffer allData = new VertexBuffer(VertexBuffer.Type.InterleavedData);
  // ByteBuffer dataBuf = BufferUtils.createByteBuffer(stride * getVertexCount());
  allData.setupData(
      VertexBuffer.Usage.Static, 1, VertexBuffer.Format.UnsignedByte, interleavedBuffer);
  int offset = 0;
  for (Mesh mesh : meshSet) {
    int stride = 0;
    for (VertexBuffer vb : mesh.getBufferList()) {
      if (vb.getBufferType().equals(VertexBuffer.Type.Index)
          || vb.getBufferType().equals(VertexBuffer.Type.InterleavedData)) {
        continue;
      }
      vb.getData().clear();
      vb.setOffset(offset + stride);
      stride += vb.getComponentsLength();
    }
    for (VertexBuffer vb : mesh.getBufferList()) {
      if (vb.getBufferType().equals(VertexBuffer.Type.Index)
          || vb.getBufferType().equals(VertexBuffer.Type.InterleavedData)) {
        continue;
      }
      vb.setStride(stride);
    }
    for (int i = 0; i < mesh.getVertexCount(); i++)
      for (VertexBuffer vb : mesh.getBufferList()) {
        if (vb.getBufferType().equals(VertexBuffer.Type.Index)
            || vb.getBufferType().equals(VertexBuffer.Type.InterleavedData)) {
          continue;
        }
        switch (vb.getFormat()) {
          case Float:
            FloatBuffer fb = (FloatBuffer) vb.getData();
            for (int comp = 0; comp < vb.getNumComponents(); comp++) {
              interleavedBuffer.putFloat(fb.get());
            }
            break;
          case Byte:
          case UnsignedByte:
            ByteBuffer bb = (ByteBuffer) vb.getData();
            for (int comp = 0; comp < vb.getNumComponents(); comp++) {
              interleavedBuffer.put(bb.get());
            }
            break;
          case Half:
          case Short:
          case UnsignedShort:
            ShortBuffer sb = (ShortBuffer) vb.getData();
            for (int comp = 0; comp < vb.getNumComponents(); comp++) {
              interleavedBuffer.putShort(sb.get());
            }
            break;
          case Int:
          case UnsignedInt:
            IntBuffer ib = (IntBuffer) vb.getData();
            for (int comp = 0; comp < vb.getNumComponents(); comp++) {
              interleavedBuffer.putInt(ib.get());
            }
            break;
          case Double:
            DoubleBuffer db = (DoubleBuffer) vb.getData();
            for (int comp = 0; comp < vb.getNumComponents(); comp++) {
              interleavedBuffer.putDouble(db.get());
            }
            break;
        }
      }
    // mesh.setBuffer(allData);
    offset = interleavedBuffer.position();
    for (VertexBuffer vb : mesh.getBufferList()) {
      if (vb.getBufferType().equals(VertexBuffer.Type.Index)
          || vb.getBufferType().equals(VertexBuffer.Type.InterleavedData)) {
        continue;
      }
      // System.out.println(
      // "type = "+vb.getBufferType()
      // + "stride = "+vb.getStride()
      // + "offset "+vb.getOffset()
      // );
      vb.updateData(null);
    }
    // System.out.println("done");
    // optimize2();
    // VertexBuffer allData2 = mesh.getBuffer(VertexBuffer.Type.InterleavedData);
    // ByteBuffer bb = (ByteBuffer)allData2.getData();
    // for(int i=0;i<allData.getData().capacity();i++) {
    // System.out.printf("%d %x %x ", i, bb.get(i), interleavedBuffer.get(i));
    // System.out.println();
    // }
    mesh.setBuffer(allData);
  }
}

/** {@inheritDoc} */
public final IDataSegmentChunk next(final int chunkSize) {

  if (!hasNext()) {
    throw new NoSuchElementException();
  }

  if (chunkSize < 0 && chunkSize > maxChunkSize) {
    throw new IllegalArgumentException();
  }

  final int pos = dataBuffer.position();
  dataBuffer.position(cursor);

  data.position(0).limit(chunkSize);

  while (data.hasRemaining() && dataBuffer.hasRemaining()) {
    data.put(dataBuffer.get());
  }

  final int dataSegmentLength = data.position();
  final int totalLength = dataSegmentLength + calcPadding(dataSegmentLength);

  // set limit to the next nearest bound (with padding)
  data.limit(totalLength);
  data.rewind();
  dataBuffer.position(pos);

  // cursor += chunkSize;
  cursor += totalLength;

  return new DataSegmentChunk(dataSegmentLength, totalLength);
}

