  byte array[] = new byte[1];
  if (buf.isReadOnly()) {
    try {
      buf.put(array);
      fail("Should throw Exception"); // $NON-NLS-1$
    } catch (ReadOnlyBufferException e) {
      // expected
    }
    return;
  }

  buf.clear();
  for (int i = 0; i < buf.capacity(); i++) {
    assertEquals(i, buf.position());
    array[0] = (byte) i;
    ByteBuffer ret = buf.put(array);
    assertEquals((byte) i, buf.get(i));
    assertSame(ret, buf);
  }
  try {
    buf.put(array);
    fail("Should throw Exception"); // $NON-NLS-1$
  } catch (BufferOverflowException e) {
    // expected
  }
  try {
    buf.put((byte[]) null);
    fail("Should throw Exception"); // $NON-NLS-1$
  } catch (NullPointerException e) {
    // expected
  }
}

/**
 * @see org.newdawn.slick.opengl.LoadableImageData#loadImage(java.io.InputStream, boolean,
 *     boolean, int[])
 */
public ByteBuffer loadImage(
    InputStream fis, boolean flipped, boolean forceAlpha, int[] transparent) throws IOException {
  if (transparent != null) {
    forceAlpha = true;
  }

  init(fis);

  if (!isRGB()) {
    throw new IOException("Only RGB formatted images are supported by the PNGLoader");
  }

  texWidth = get2Fold(width);
  texHeight = get2Fold(height);

  int perPixel = hasAlpha() ? 4 : 3;

  // Get a pointer to the image memory
  scratch = BufferUtils.createByteBuffer(texWidth * texHeight * perPixel);
  decode(scratch, texWidth * perPixel, flipped);

  if (height < texHeight - 1) {
    int topOffset = (texHeight - 1) * (texWidth * perPixel);
    int bottomOffset = (height - 1) * (texWidth * perPixel);
    for (int x = 0; x < texWidth * perPixel; x++) {
      scratch.put(topOffset + x, scratch.get(x));
      scratch.put(
          bottomOffset + (texWidth * perPixel) + x, scratch.get((texWidth * perPixel) + x));
    }
  }
  if (width < texWidth - 1) {
    for (int y = 0; y < texHeight; y++) {
      for (int i = 0; i < perPixel; i++) {
        scratch.put(
            ((y + 1) * (texWidth * perPixel)) - perPixel + i,
            scratch.get(y * (texWidth * perPixel) + i));
        scratch.put(
            (y * (texWidth * perPixel)) + (width * perPixel) + i,
            scratch.get((y * (texWidth * perPixel)) + ((width - 1) * perPixel) + i));
      }
    }
  }

  if (!hasAlpha() && forceAlpha) {
    ByteBuffer temp = BufferUtils.createByteBuffer(texWidth * texHeight * 4);
    for (int x = 0; x < texWidth; x++) {
      for (int y = 0; y < texHeight; y++) {
        int srcOffset = (y * 3) + (x * texHeight * 3);
        int dstOffset = (y * 4) + (x * texHeight * 4);

        temp.put(dstOffset, scratch.get(srcOffset));
        temp.put(dstOffset + 1, scratch.get(srcOffset + 1));
        temp.put(dstOffset + 2, scratch.get(srcOffset + 2));
        temp.put(dstOffset + 3, (byte) 255);
      }
    }

    colorType = COLOR_TRUEALPHA;
    bitDepth = 32;
    scratch = temp;
  }

  if (transparent != null) {
    for (int i = 0; i < texWidth * texHeight * 4; i += 4) {
      boolean match = true;
      for (int c = 0; c < 3; c++) {
        if (toInt(scratch.get(i + c)) != transparent[c]) {
          match = false;
        }
      }

      if (match) {
        scratch.put(i + 3, (byte) 0);
      }
    }
  }

  scratch.position(0);

  return scratch;
}

@Override
public void nextUpstreamHandler(
    WebSocket ws, ByteBuffer buffer, Frame frame, StreamHandlerChain chain)
    throws WebSocketException {
  buffer.put(1, (byte) (buffer.get(1) | 0x80)); // force set mask bit;
  int maskkeyOffset = 2;
  if (buffer.get(buffer.position() + 1) == (byte) 0xFE) {
    maskkeyOffset = 4;
  } else if (buffer.get(buffer.position() + 1) == 0xFF) {
    maskkeyOffset = 10;
  }

  // avoid copy buffer
  // buffer already reserved mask key field in FrameRfc6455
  ByteBuffer buf = ByteBuffer.wrap(buffer.array());
  int limit = buffer.limit();
  buffer.limit(buffer.position() + maskkeyOffset);
  buf.put(buffer);
  buffer.limit(limit);
  buf.putInt(random.nextInt());

  byte[] maskkey = new byte[4];
  buf.position(maskkeyOffset);
  buf.get(maskkey, 0, 4);
  int m = 0;
  while (buf.hasRemaining()) {
    int position = buf.position();
    buf.put((byte) (buf.get(position) ^ maskkey[m++ % 4]));
  }
  buf.flip();
  chain.nextUpstreamHandler(ws, buf, frame);
}

private ByteArrayInputStream nextBuffer() {
  while (idBuffer.hasRemaining()) {
    switch (idBuffer.get()) {
      case 0:
        {
          int len = DataUtils.readVarInt(idBuffer);
          if (skip >= len) {
            skip -= len;
            idBuffer.position(idBuffer.position() + len);
            continue;
          }
          int p = (int) (idBuffer.position() + skip);
          int l = (int) (len - skip);
          idBuffer.position(p + l);
          return new ByteArrayInputStream(idBuffer.array(), p, l);
        }
      case 1:
        {
          int len = DataUtils.readVarInt(idBuffer);
          long key = DataUtils.readVarLong(idBuffer);
          if (skip >= len) {
            skip -= len;
            continue;
          }
          byte[] data = store.getBlock(key);
          int s = (int) skip;
          skip = 0;
          return new ByteArrayInputStream(data, s, data.length - s);
        }
      case 2:
        {
          long len = DataUtils.readVarInt(idBuffer);
          long key = DataUtils.readVarLong(idBuffer);
          if (skip >= len) {
            skip -= len;
            continue;
          }
          byte[] k = store.getBlock(key);
          ByteBuffer newBuffer =
              ByteBuffer.allocate(k.length + idBuffer.limit() - idBuffer.position());
          newBuffer.put(k);
          newBuffer.put(idBuffer);
          newBuffer.flip();
          idBuffer = newBuffer;
          return nextBuffer();
        }
      default:
        throw DataUtils.newIllegalArgumentException(
            "Unsupported id {0}", Arrays.toString(idBuffer.array()));
    }
  }
  return null;
}

public void updateShaderSourceData(ShaderSource source, String language) {
  if (glslVer != -1) {
    GL gl = GLContext.getCurrentGL();
    int id = source.getId();
    if (id == -1) {
      // create id
      id = gl.getGL2().glCreateShader(convertShaderType(source.getType()));
      if (id <= 0) {
        throw new RendererException("Invalid ID received when trying to create shader.");
      }

      source.setId(id);
    }

    // upload shader source
    // merge the defines and source code
    byte[] versionData = new byte[] {}; // "#version 140\n".getBytes();
    // versionData = "#define INSTANCING 1\n".getBytes();
    byte[] definesCodeData = source.getDefines().getBytes();
    byte[] sourceCodeData = source.getSource().getBytes();
    ByteBuffer codeBuf =
        BufferUtils.createByteBuffer(
            versionData.length + definesCodeData.length + sourceCodeData.length);
    codeBuf.put(versionData);
    codeBuf.put(definesCodeData);
    codeBuf.put(sourceCodeData);
    codeBuf.flip();

    final byte[] array = new byte[codeBuf.limit()];
    codeBuf.get(array);
    gl.getGL2()
        .glShaderSourceARB(id, 1, new String[] {new String(array)}, new int[] {array.length}, 0);
    gl.getGL2().glCompileShader(id);

    gl.getGL2().glGetShaderiv(id, GL2ES2.GL_COMPILE_STATUS, intBuf1);

    boolean compiledOK = intBuf1.get(0) == GL.GL_TRUE;
    String infoLog = null;

    if (VALIDATE_SHADER || !compiledOK) {
      // even if compile succeeded, check
      // log for warnings
      gl.getGL2().glGetShaderiv(id, GL2ES2.GL_INFO_LOG_LENGTH, intBuf1);
      int length = intBuf1.get(0);
      if (length > 3) {
        // get infos
        ByteBuffer logBuf = BufferUtils.createByteBuffer(length);
        gl.getGL2().glGetShaderInfoLog(id, logBuf.limit(), intBuf1, logBuf);
        byte[] logBytes = new byte[length];
        logBuf.get(logBytes, 0, length);
        // convert to string, etc
        infoLog = new String(logBytes);
      }
    }

    if (compiledOK) {
      if (infoLog != null) {
        logger.log(
            Level.INFO, "{0} compile success\n{1}", new Object[] {source.getName(), infoLog});
      } else {
        logger.log(Level.FINE, "{0} compile success", source.getName());
      }
    } else {
      if (infoLog != null) {
        logger.log(
            Level.WARNING, "{0} compile error: {1}", new Object[] {source.getName(), infoLog});
      } else {
        logger.log(Level.WARNING, "{0} compile error: ?", source.getName());
      }
      logger.log(Level.WARNING, "{0}{1}", new Object[] {source.getDefines(), source.getSource()});
    }

    source.clearUpdateNeeded();
    // only usable if compiled
    source.setUsable(compiledOK);
    if (!compiledOK) {
      // make sure to dispose id cause all program's
      // shaders will be cleared later.
      gl.getGL2().glDeleteShader(id);
    } else {
      // register for cleanup since the ID is usable
      objManager.registerForCleanup(source);
    }
  }
}

