/**
 * Convert the audio bytes into the stream
 *
 * @param format The audio format being decoded
 * @param audio_bytes The audio byts
 * @param two_bytes_data True if we using double byte data
 * @return The byte bufer of data
 */
private static ByteBuffer convertAudioBytes(
    AudioFormat format, byte[] audio_bytes, boolean two_bytes_data) {
  ByteBuffer dest = ByteBuffer.allocateDirect(audio_bytes.length);
  dest.order(ByteOrder.nativeOrder());
  ByteBuffer src = ByteBuffer.wrap(audio_bytes);
  src.order(ByteOrder.BIG_ENDIAN);
  if (two_bytes_data) {
    ShortBuffer dest_short = dest.asShortBuffer();
    ShortBuffer src_short = src.asShortBuffer();
    while (src_short.hasRemaining()) dest_short.put(src_short.get());
  } else {
    while (src.hasRemaining()) {
      byte b = src.get();
      if (format.getEncoding() == Encoding.PCM_SIGNED) {
        b = (byte) (b + 127);
      }
      dest.put(b);
    }
  }
  dest.rewind();
  return dest;
}

public ByteBuffer readNext() throws IOException {
  if (closed) {
    throw new IOException();
  }
  while (true) {
    if (stack.isEmpty()) {
      throw new NoSuchElementException();
    }
    final Node node = stack.get(stack.size() - 1);
    if (!node.branches.hasRemaining()) {
      stack.remove(stack.size() - 1);
      continue;
    }
    rwkey.limit(node.depth);
    rokey.limit(rwkey.limit());
    if (node.depth > 0) {
      rwkey.put(node.depth - 1, node.heads.get());
    }
    long ref = node.branches.getLong();
    while (true) {
      type = type(ref);
      at = data(ref);
      if (!isARun(type)) {
        break;
      }
      final int length = lengthOfRun(type);
      final int size = sizeOfRun(length);
      final ByteBuffer run = read(size, at);
      ref = value(run, RunBranch);
      if (rwkey.limit() + length > rwkey.capacity()) {
        rwkey = ByteBuffer.allocate(2 * (rwkey.limit() + length)).put(rwkey);
        rokey = rwkey.asReadOnlyBuffer();
      }
      rwkey.limit(rwkey.limit() + length);
      rokey.limit(rwkey.limit());
      run.rewind().limit(length);
      rwkey.put(run);
    }
    if (isAMap(type)) {
      if (rwkey.limit() == rwkey.capacity()) {
        rwkey = ByteBuffer.allocate(2 * (rwkey.limit() + 1)).put(rwkey);
        rokey = rwkey.asReadOnlyBuffer();
      }
      final int arity = arityOfMap(type);
      final int size = sizeOfMap(arity);
      final ByteBuffer map = read(size, at);
      stack.add(
          new Node(
              rwkey.limit() + 1,
              listMap(map, arity),
              (ByteBuffer) ByteBuffer.allocate(map.remaining()).put(map).rewind()));
    } else if (TypeOfLeaf == type) {
      final ByteBuffer leaf = read(SizeOfLeaf, at);
      final long child = value(leaf, LeafChild);
      type = type(child);
      at = data(child);
      final int depth = rwkey.limit();
      stack.add(
          new Node(
              depth,
              0 == depth ? null : ByteBuffer.allocate(1).put(0, rwkey.get(depth - 1)),
              ByteBuffer.allocate(8).putLong(0, value(leaf, LeafBranch))));
      rokey.rewind();
      return rokey;
    } else {
      rokey.rewind();
      return rokey;
    }
  }
}

private void takeScreenshot() {
  screenshot = false;

  // Are we taking pictures of a demo?
  boolean video = (Ref.client.demo.isPlaying() && Ref.cvars.Find("cl_demorecord").isTrue());

  int width = (int) Ref.glRef.GetResolution().x;
  int height = (int) Ref.glRef.GetResolution().y;

  // Copy backbuffer
  if (screenshotBuffer == null || screenshotBuffer.capacity() < 3 * width * height) {
    screenshotBuffer = ByteBuffer.allocateDirect(3 * width * height);
  }
  screenshotBuffer.clear();
  GL11.glReadPixels(0, 0, width, height, GL12.GL_BGR, GL11.GL_UNSIGNED_BYTE, screenshotBuffer);

  byte[] encData = null;
  if (video) {
    // Bmp for speed
    BMPWriter enc = new BMPWriter(screenshotBuffer, width, height);
    encData = enc.encodeImage();
    writeVideoFrame(encData);
  } else {
    // Encode as png
    // Change format from bgr to rgb
    screenshotBuffer.position(0);
    for (int i = 0; i < width * height; i++) {
      byte b = screenshotBuffer.get();
      byte g = screenshotBuffer.get();
      byte r = screenshotBuffer.get();
      screenshotBuffer.position(i * 3);
      screenshotBuffer.put(r);
      screenshotBuffer.put(g);
      screenshotBuffer.put(b);
    }
    PngEncoder enc =
        new PngEncoder(screenshotBuffer, false, PngEncoder.FILTER_NONE, 3, width, height);
    encData = enc.pngEncode();
    writeScreenshot(encData);
  }
}

/**
 * Get a cursor based on a image reference on the classpath
 *
 * @param ref The reference to the image to be loaded
 * @param x The x-coordinate of the cursor hotspot (left -> right)
 * @param y The y-coordinate of the cursor hotspot (bottom -> top)
 * @return The create cursor
 * @throws IOException Indicates a failure to load the image
 * @throws LWJGLException Indicates a failure to create the hardware cursor
 */
public Cursor getCursor(String ref, int x, int y) throws IOException, LWJGLException {
  LoadableImageData imageData = null;

  imageData = ImageDataFactory.getImageDataFor(ref);
  imageData.configureEdging(false);

  ByteBuffer buf = imageData.loadImage(ResourceLoader.getResourceAsStream(ref), true, true, null);
  for (int i = 0; i < buf.limit(); i += 4) {
    byte red = buf.get(i);
    byte green = buf.get(i + 1);
    byte blue = buf.get(i + 2);
    byte alpha = buf.get(i + 3);

    buf.put(i + 2, red);
    buf.put(i + 1, green);
    buf.put(i, blue);
    buf.put(i + 3, alpha);
  }

  try {
    int yspot = imageData.getHeight() - y - 1;
    if (yspot < 0) {
      yspot = 0;
    }

    return new Cursor(
        imageData.getTexWidth(), imageData.getTexHeight(), x, yspot, 1, buf.asIntBuffer(), null);
  } catch (Throwable e) {
    Log.info("Chances are you cursor is too small for this platform");
    throw new LWJGLException(e);
  }
}

/** See the {@link #readData()} for documentation on the data format. */
private static void writeDatabase(File file, Api info) throws IOException {
  /*
   * 1. A file header, which is the exact contents of {@link FILE_HEADER} encoded
   * as ASCII characters. The purpose of the header is to identify what the file
   * is for, for anyone attempting to open the file.
   * 2. A file version number. If the binary file does not match the reader's expected
   * version, it can ignore it (and regenerate the cache from XML).
   */
  Map<String, ApiClass> classMap = info.getClasses();
  // Write the class table

  List<String> classes = new ArrayList<String>(classMap.size());
  Map<ApiClass, List<String>> memberMap = Maps.newHashMapWithExpectedSize(classMap.size());
  int memberCount = 0;
  Set<String> javaPackageSet = Sets.newHashSetWithExpectedSize(70);
  for (Map.Entry<String, ApiClass> entry : classMap.entrySet()) {
    String className = entry.getKey();
    ApiClass apiClass = entry.getValue();

    if (className.startsWith("java/") // $NON-NLS-1$
        || className.startsWith("javax/")) { // $NON-NLS-1$
      String pkg = apiClass.getPackage();
      javaPackageSet.add(pkg);
    }

    if (!isRelevantOwner(className)) {
      System.out.println("Warning: The isRelevantOwner method does not pass " + className);
    }

    Set<String> allMethods = apiClass.getAllMethods(info);
    Set<String> allFields = apiClass.getAllFields(info);

    // Strip out all members that have been supported since version 1.
    // This makes the database *much* leaner (down from about 4M to about
    // 1.7M), and this just fills the table with entries that ultimately
    // don't help the API checker since it just needs to know if something
    // requires a version *higher* than the minimum. If in the future the
    // database needs to answer queries about whether a method is public
    // or not, then we'd need to put this data back in.
    List<String> members = new ArrayList<String>(allMethods.size() + allFields.size());
    for (String member : allMethods) {

      Integer since = apiClass.getMethod(member, info);
      if (since == null) {
        assert false : className + ':' + member;
        since = 1;
      }
      if (since != 1) {
        members.add(member);
      }
    }

    // Strip out all members that have been supported since version 1.
    // This makes the database *much* leaner (down from about 4M to about
    // 1.7M), and this just fills the table with entries that ultimately
    // don't help the API checker since it just needs to know if something
    // requires a version *higher* than the minimum. If in the future the
    // database needs to answer queries about whether a method is public
    // or not, then we'd need to put this data back in.
    for (String member : allFields) {
      Integer since = apiClass.getField(member, info);
      if (since == null) {
        assert false : className + ':' + member;
        since = 1;
      }
      if (since != 1) {
        members.add(member);
      }
    }

    // Only include classes that have one or more members requiring version 2 or higher:
    if (!members.isEmpty()) {
      classes.add(className);
      memberMap.put(apiClass, members);
      memberCount += members.size();
    }
  }
  Collections.sort(classes);

  List<String> javaPackages = Lists.newArrayList(javaPackageSet);
  Collections.sort(javaPackages);
  int javaPackageCount = javaPackages.size();

  int entryCount = classMap.size() + memberCount;
  int capacity = entryCount * BYTES_PER_ENTRY;
  ByteBuffer buffer = ByteBuffer.allocate(capacity);
  buffer.order(ByteOrder.BIG_ENDIAN);
  // 1. A file header, which is the exact contents of {@link FILE_HEADER} encoded
  // as ASCII characters. The purpose of the header is to identify what the file
  // is for, for anyone attempting to open the file.

  buffer.put(FILE_HEADER.getBytes(Charsets.US_ASCII));

  // 2. A file version number. If the binary file does not match the reader's expected
  // version, it can ignore it (and regenerate the cache from XML).
  buffer.put((byte) BINARY_FORMAT_VERSION);

  // 3. The number of classes [1 int]
  buffer.putInt(classes.size());

  // 4. The number of members (across all classes) [1 int].
  buffer.putInt(memberCount);

  // 5. The number of Java packages [1 int].
  buffer.putInt(javaPackageCount);

  // 6. The Java package table. There are javaPackage.size() entries, where each entry
  // consists of a string length, as a byte, followed by the bytes in the package.
  // There is no terminating 0.
  for (String pkg : javaPackages) {
    byte[] bytes = pkg.getBytes(Charsets.UTF_8);
    assert bytes.length < 255 : pkg;
    buffer.put((byte) bytes.length);
    buffer.put(bytes);
  }

  // 7. Class offset table (one integer per class, pointing to the byte offset in the
  // file (relative to the beginning of the file) where each class begins.
  // The classes are always sorted alphabetically by fully qualified name.
  int classOffsetTable = buffer.position();

  // Reserve enough room for the offset table here: we will backfill it with pointers
  // as we're writing out the data structures below
  for (int i = 0, n = classes.size(); i < n; i++) {
    buffer.putInt(0);
  }

  // 8. Member offset table (one integer per member, pointing to the byte offset in the
  // file (relative to the beginning of the file) where each member entry begins.
  // The members are always sorted alphabetically.
  int methodOffsetTable = buffer.position();
  for (int i = 0, n = memberCount; i < n; i++) {
    buffer.putInt(0);
  }

  int nextEntry = buffer.position();
  int nextOffset = classOffsetTable;

  // 9. Class entry table. Each class entry consists of the fully qualified class name,
  // in JVM format (using / instead of . in package names and $ for inner classes),
  // followed by the byte 0 as a terminator, followed by the API version as a byte.
  for (String clz : classes) {
    buffer.position(nextOffset);
    buffer.putInt(nextEntry);
    nextOffset = buffer.position();
    buffer.position(nextEntry);
    buffer.put(clz.getBytes(Charsets.UTF_8));
    buffer.put((byte) 0);

    ApiClass apiClass = classMap.get(clz);
    assert apiClass != null : clz;
    int since = apiClass.getSince();
    assert since == UnsignedBytes.toInt((byte) since) : since; // make sure it fits
    buffer.put((byte) since);

    nextEntry = buffer.position();
  }

  // 10. Member entry table. Each member entry consists of the class number (as a short),
  // followed by the JVM method/field signature, encoded as UTF-8, followed by a 0 byte
  // signature terminator, followed by the API level as a byte.
  assert nextOffset == methodOffsetTable;

  for (int classNumber = 0, n = classes.size(); classNumber < n; classNumber++) {
    String clz = classes.get(classNumber);
    ApiClass apiClass = classMap.get(clz);
    assert apiClass != null : clz;
    List<String> members = memberMap.get(apiClass);
    Collections.sort(members);

    for (String member : members) {
      buffer.position(nextOffset);
      buffer.putInt(nextEntry);
      nextOffset = buffer.position();
      buffer.position(nextEntry);

      Integer since;
      if (member.indexOf('(') != -1) {
        since = apiClass.getMethod(member, info);
      } else {
        since = apiClass.getField(member, info);
      }
      if (since == null) {
        assert false : clz + ':' + member;
        since = 1;
      }

      assert classNumber == (short) classNumber;
      buffer.putShort((short) classNumber);
      byte[] signature = member.getBytes(Charsets.UTF_8);
      for (int i = 0; i < signature.length; i++) {
        // Make sure all signatures are really just simple ASCII
        byte b = signature[i];
        assert b == (b & 0x7f) : member;
        buffer.put(b);
        // Skip types on methods
        if (b == (byte) ')') {
          break;
        }
      }
      buffer.put((byte) 0);
      int api = since;
      assert api == UnsignedBytes.toInt((byte) api);
      // assert api >= 1 && api < 0xFF; // max that fits in a byte
      buffer.put((byte) api);
      nextEntry = buffer.position();
    }
  }

  int size = buffer.position();
  assert size <= buffer.limit();
  buffer.mark();

  if (WRITE_STATS) {
    System.out.println(
        "Wrote " + classes.size() + " classes and " + memberCount + " member entries");
    System.out.print("Actual binary size: " + size + " bytes");
    System.out.println(String.format(" (%.1fM)", size / (1024 * 1024.f)));

    System.out.println("Allocated size: " + (entryCount * BYTES_PER_ENTRY) + " bytes");
    System.out.println("Required bytes per entry: " + (size / entryCount) + " bytes");
  }

  // Now dump this out as a file
  // There's probably an API to do this more efficiently; TODO: Look into this.
  byte[] b = new byte[size];
  buffer.rewind();
  buffer.get(b);
  if (file.exists()) {
    file.delete();
  }
  FileOutputStream output = Files.newOutputStreamSupplier(file).getOutput();
  output.write(b);
  output.close();
}

/** Method has similar semantics to {@link BufferedReader#readLine()} method. */
public String readLine(Charset charset) throws IOException {
  lineBuffer.clear();

  while (true) {
    if (buffer.hasRemaining()) {
      boolean lineEndFound = false;
      int pos;
      findingLineEnd:
      for (pos = buffer.position(); pos < buffer.limit(); pos++) {
        if (buffer.get(pos) == LF_BYTE) {
          lineEndFound = true;
          break findingLineEnd;
        }
      }
      int chunkLen = pos - buffer.position();
      if (chunkLen > 0) {
        if (lineBuffer.remaining() < chunkLen) {
          int newSize = Math.max(lineBuffer.capacity() * 2, lineBuffer.position() + chunkLen);
          ByteBuffer newLineBuffer = ByteBuffer.allocate(newSize);
          lineBuffer.flip();
          newLineBuffer.put(lineBuffer);
          lineBuffer = newLineBuffer;
        }
        buffer.get(lineBuffer.array(), lineBuffer.position(), chunkLen);
        lineBuffer.position(lineBuffer.position() + chunkLen);
      }
      if (lineEndFound) {
        // Shift position.
        buffer.get();
        break;
      }
    }
    assert !buffer.hasRemaining();
    buffer.clear();
    int readRes = inputStream.read(buffer.array());
    if (readRes <= 0) {
      if (lineBuffer.position() == 0) {
        return null;
      } else {
        throw new IOException("End of stream while expecting line end");
      }
    }
    buffer.position(readRes);
    buffer.flip();
  }
  if (lineBuffer.position() > 0 && lineBuffer.get(lineBuffer.position() - 1) == CR_BYTE) {
    lineBuffer.position(lineBuffer.position() - 1);
  }
  return new String(lineBuffer.array(), 0, lineBuffer.position(), charset);
}

/** @param oldData Can be null if the delta does not contain copy commands. */
public void decompress(ByteBuffer oldData, ByteBuffer deltaData, ByteBuffer outputBuffer) {
  while (deltaData.remaining() > 0) {
    int command = deltaData.get();
    int length;
    if (command > 0 && command <= DATA_MAX) {
      length = command;
      command = COMMAND_APPEND;
    } else length = IntSerializer.get(deltaData, true);
    switch (command) {
      case COMMAND_APPEND:
        if (debug)
          System.out.println(
              "decompress APPEND "
                  + length
                  + ": "
                  + dump(deltaData, deltaData.position(), length));
        // Append new data.
        while (length-- > 0) outputBuffer.put(deltaData.get());
        break;
      case COMMAND_COPY:
        // Copy old data.
        if (oldData == null)
          throw new SerializationException("Delta copy command received without previous data.");
        int offset = IntSerializer.get(deltaData, true);
        if (debug)
          System.out.println(
              "decompress COPY at "
                  + offset
                  + ", length "
                  + length
                  + ": "
                  + dump(oldData, offset, length));
        oldData.position(offset);
        while (length-- > 0) outputBuffer.put(oldData.get());
        break;
      default:
        throw new SerializationException("Invalid delta command: " + command);
    }
  }
}

