private static ByteBuffer convertAudioBytes(byte[] audio_bytes, boolean two_bytes_data) {
  ByteBuffer dest = ByteBuffer.allocateDirect(audio_bytes.length);
  dest.order(ByteOrder.nativeOrder());
  ByteBuffer src = ByteBuffer.wrap(audio_bytes);
  src.order(ByteOrder.LITTLE_ENDIAN);
  if (two_bytes_data) {
    ShortBuffer dest_short = dest.asShortBuffer();
    ShortBuffer src_short = src.asShortBuffer();
    while (src_short.hasRemaining()) dest_short.put(src_short.get());
  } else {
    while (src.hasRemaining()) dest.put(src.get());
  }
  dest.rewind();
  return dest;
}

private void testRelativePositions(ByteBuffer b) throws Exception {
  // gets
  b.position(0);
  b.get();
  assertEquals(1, b.position());

  byte[] buf = new byte[5];
  b.position(0);
  b.get(buf);
  assertEquals(5, b.position());

  b.position(0);
  b.get(buf, 1, 3);
  assertEquals(3, b.position());

  b.position(0);
  b.getChar();
  assertEquals(2, b.position());

  b.position(0);
  b.getDouble();
  assertEquals(8, b.position());

  b.position(0);
  b.getFloat();
  assertEquals(4, b.position());

  b.position(0);
  b.getInt();
  assertEquals(4, b.position());

  b.position(0);
  b.getLong();
  assertEquals(8, b.position());

  b.position(0);
  b.getShort();
  assertEquals(2, b.position());

  // puts
  b.position(0);
  b.put((byte) 0);
  assertEquals(1, b.position());

  b.position(0);
  b.put(buf);
  assertEquals(5, b.position());

  b.position(0);
  b.put(buf, 1, 3);
  assertEquals(3, b.position());

  b.position(0);
  b.putChar('x');
  assertEquals(2, b.position());

  b.position(0);
  b.putDouble(0);
  assertEquals(8, b.position());

  b.position(0);
  b.putFloat(0);
  assertEquals(4, b.position());

  b.position(0);
  b.putInt(0);
  assertEquals(4, b.position());

  b.position(0);
  b.putLong(0);
  assertEquals(8, b.position());

  b.position(0);
  b.putShort((short) 0);
  assertEquals(2, b.position());
}
