private static String findCategory(ContentResolver resolver, long providerId) {
  // find the provider category for this chat
  Cursor providerCursor =
      resolver.query(
          Imps.Provider.CONTENT_URI,
          PROVIDER_CATEGORY_PROJECTION,
          "_id = " + providerId,
          null /* selection args */,
          null /* sort order */);
  String category = null;

  try {
    if (providerCursor.moveToFirst()) {
      category = providerCursor.getString(PROVIDER_CATEGORY_COLUMN);
    }
  } finally {
    providerCursor.close();
  }

  return category;
}

public UserStimuli getUserStimuli(String imageName) {
  SQLiteDatabase db = this.getReadableDatabase();
  UserStimuli us = null;

  String getUserStimuli = "select * from " + tableName + " where imageName = '" + imageName + "'";
  Cursor cursor = db.rawQuery(getUserStimuli, null);

  if (cursor == null) {
    return null;
  }

  if (cursor.moveToFirst() && cursor != null) {
    try {
      us = new UserStimuli();
      us.setImageName(cursor.getString(0));
      us.setCategory(cursor.getString(1));
      us.setIsFavorite(Integer.parseInt(cursor.getString(2)));
      us.setAttempts(Integer.parseInt(cursor.getString(3)));
      us.setCorrectAttempts(Integer.parseInt(cursor.getString(4)));
      us.setSoundHints(Integer.parseInt(cursor.getString(5)));
      us.setPlaywordHints(Integer.parseInt(cursor.getString(6)));
      us.setNoHint(Integer.parseInt(cursor.getString(7)));

      Calendar cd = Calendar.getInstance();
      cd.setTime(new Date(cursor.getString(8)));
      us.setLastSeen(cd);
      us.setDifficulty(Double.parseDouble(cursor.getString(9)));
      us.setUrl(cursor.getString(10));

    } catch (Exception e) {
      Log.d("Exception in parsing", e.toString());
    }
  }
  return us;
}

/**
 * Reads the value of the parameter identified by <code>name</code>
 *
 * @param name
 * @return String value of the parameter, <code>null</code> otherwise
 */
private String getVariable(String name) {
  String[] cols = new String[] {"value"};
  String[] whereArgs = new String[] {name};

  Cursor c = null;

  String result = null;
  try {
    SQLiteDatabase db = this.getReadableDatabase();
    c = db.query(VARIABLE_TABLE_NAME, cols, "name=?", whereArgs, null, null, null);
    boolean hasResult = c.moveToFirst();
    if (hasResult && !c.isNull(0)) {
      result = c.getString(0);
    }
    c.close();
    db.close();
  } catch (Exception e) {
    Log.e(LOG_TAG, "Error getting variable [" + name + "]", e);
  } finally {
    if (null != c) {
      try {
        c.close();
      } catch (Exception e) {
        Log.e(LOG_TAG, "Error closing cursor", e);
      }
    }
  }
  return result;
}

