/**
 * We now want to bring up the pin request screen AFTER the contact information is displayed, to
 * help with user experience.
 *
 * <p>Also, process the results from the contact picker.
 */
@Override
protected void onActivityResult(int requestCode, int resultCode, Intent intent) {
  if (DBG) log("onActivityResult request:" + requestCode + " result:" + resultCode);

  switch (requestCode) {
    case PIN2_REQUEST_CODE:
      Bundle extras = (intent != null) ? intent.getExtras() : null;
      if (extras != null) {
        mPin2 = extras.getString("pin2");
        if (mAddContact) {
          addContact();
        } else {
          updateContact();
        }
      } else if (resultCode != RESULT_OK) {
        // if they cancelled, then we just cancel too.
        if (DBG) log("onActivityResult: cancelled.");
        finish();
      }
      break;

      // look for the data associated with this number, and update
      // the display with it.
    case CONTACTS_PICKER_CODE:
      if (resultCode != RESULT_OK) {
        if (DBG) log("onActivityResult: cancelled.");
        return;
      }
      Cursor cursor =
          getContentResolver().query(intent.getData(), NUM_PROJECTION, null, null, null);
      if ((cursor == null) || (!cursor.moveToFirst())) {
        Log.w(LOG_TAG, "onActivityResult: bad contact data, no results found.");
        return;
      }
      mNameField.setText(cursor.getString(0));
      mNumberField.setText(cursor.getString(1));
      break;
  }
}

/**
 * Read the bills of current day
 *
 * @param month
 * @param year
 */
public synchronized ArrayList<Conta> readDailyBills(int day, int month, int year) {
  // Open Database
  mDataBase = mDatabaseHelper.getWritableDatabase();

  ArrayList<Conta> arrayConta = new ArrayList<Conta>();
  Conta conta;

  String[] allColumns = {
    "_id", "conta", "valor", "vencimento", "notificar", "codigo", "status", "dia", "mes", "ano"
  };

  String where;
  where = "dia=" + day + " and mes=" + month + " and ano=" + year + " and status=" + "0";
  Log.d("log", "dia " + day);
  Log.d("log", "mes " + month);
  Log.d("log", "ano " + year);

  // SELECT * FROM CONTAS WHERE MES = ? AND ANO = ?
  Cursor cursor = mDataBase.query(TABLE_NAME, allColumns, where, null, null, null, "dia");
  Log.d("log", "cursor: " + cursor.getCount());
  if (cursor.getCount() > 0) {
    cursor.moveToFirst();
    for (int i = 0; i < cursor.getCount(); i++) {
      conta = new Conta();
      conta.setId(cursor.getInt(0));
      conta.setNome(cursor.getString(1));
      conta.setValor(cursor.getString(2));
      conta.setVencimento(cursor.getString(3));
      conta.setNotificar(cursor.getString(4));
      conta.setCodigoBarra(cursor.getString(5));
      if (cursor.getInt(6) == 0) {
        conta.setPago(false);
      } else {
        conta.setPago(true);
      }
      conta.setDia(cursor.getString(7));
      conta.setMes(cursor.getString(8));
      conta.setAno(cursor.getString(9));

      arrayConta.add(conta);
      cursor.moveToNext();
    }
  }

  // Open Database
  closeDb();

  return arrayConta;
}

private String getGeometryColumn(SQLiteDatabase db, String featureType)
    throws FeatureHelperException {
  String[] columns = {
    GeometryColumnsHelper.FEATURE_GEOMETRY_COLUMN // 0
  };

  String whereClause = GeometryColumnsHelper.FEATURE_TABLE_NAME + "=?";
  String[] whereArgs = {featureType};

  Cursor cursor =
      db.query(
          GeometryColumnsHelper.GEOMETRY_COLUMNS_TABLE_NAME,
          columns,
          whereClause,
          whereArgs,
          null,
          null,
          null);

  String geometryColumn = null;

  if (cursor.moveToFirst()) {
    geometryColumn = cursor.getString(0);
  }

  cursor.close();

  if (geometryColumn != null) {
    return geometryColumn;
  }

  throw new FeatureHelperException(Errors.NO_GEOMETRY_COLUMN);
}

