@Override
protected void onCreate(Bundle savedInstanceState) {
  super.onCreate(savedInstanceState);

  setContentView(R.layout.feedsettings);
  setResult(RESULT_CANCELED);

  Intent intent = getIntent();

  nameEditText = (EditText) findViewById(R.id.feed_title);
  urlEditText = (EditText) findViewById(R.id.feed_url);
  refreshOnlyWifiCheckBox = (CheckBox) findViewById(R.id.wifionlycheckbox);

  if (intent.getAction().equals(Intent.ACTION_INSERT)) {
    setTitle(R.string.newfeed_title);
    restoreInstanceState(savedInstanceState);
    ((Button) findViewById(R.id.button_ok))
        .setOnClickListener(
            new OnClickListener() {
              public void onClick(View v) {
                String url = urlEditText.getText().toString();

                if (!url.startsWith(Strings.HTTP) && !url.startsWith(Strings.HTTPS)) {
                  url = Strings.HTTP + url;
                }

                Cursor cursor =
                    getContentResolver()
                        .query(
                            FeedData.FeedColumns.CONTENT_URI,
                            null,
                            new StringBuilder(FeedData.FeedColumns.URL)
                                .append(Strings.DB_ARG)
                                .toString(),
                            new String[] {url},
                            null);

                if (cursor.moveToFirst()) {
                  cursor.close();
                  Toast.makeText(
                          FeedConfigActivity.this,
                          R.string.error_feedurlexists,
                          Toast.LENGTH_LONG)
                      .show();
                } else {
                  cursor.close();
                  ContentValues values = new ContentValues();

                  values.put(
                      FeedData.FeedColumns.WIFIONLY, refreshOnlyWifiCheckBox.isChecked() ? 1 : 0);
                  values.put(FeedData.FeedColumns.URL, url);
                  values.put(FeedData.FeedColumns.ERROR, (String) null);

                  String name = nameEditText.getText().toString();

                  if (name.trim().length() > 0) {
                    values.put(FeedData.FeedColumns.NAME, name);
                  }
                  getContentResolver().insert(FeedData.FeedColumns.CONTENT_URI, values);
                  setResult(RESULT_OK);
                  finish();
                }
              }
            });
  } else {
    setTitle(R.string.editfeed_title);

    if (!restoreInstanceState(savedInstanceState)) {
      Cursor cursor = getContentResolver().query(intent.getData(), PROJECTION, null, null, null);

      if (cursor.moveToNext()) {
        nameEditText.setText(cursor.getString(0));
        urlEditText.setText(cursor.getString(1));
        refreshOnlyWifiCheckBox.setChecked(cursor.getInt(2) == 1);
        cursor.close();
      } else {
        cursor.close();
        Toast.makeText(FeedConfigActivity.this, R.string.error, Toast.LENGTH_LONG).show();
        finish();
      }
    }
    ((Button) findViewById(R.id.button_ok))
        .setOnClickListener(
            new OnClickListener() {
              public void onClick(View v) {
                String url = urlEditText.getText().toString();

                Cursor cursor =
                    getContentResolver()
                        .query(
                            FeedData.FeedColumns.CONTENT_URI,
                            new String[] {FeedData.FeedColumns._ID},
                            new StringBuilder(FeedData.FeedColumns.URL)
                                .append(Strings.DB_ARG)
                                .toString(),
                            new String[] {url},
                            null);

                if (cursor.moveToFirst()
                    && !getIntent().getData().getLastPathSegment().equals(cursor.getString(0))) {
                  cursor.close();
                  Toast.makeText(
                          FeedConfigActivity.this,
                          R.string.error_feedurlexists,
                          Toast.LENGTH_LONG)
                      .show();
                } else {
                  cursor.close();
                  ContentValues values = new ContentValues();

                  if (!url.startsWith(Strings.HTTP) && !url.startsWith(Strings.HTTPS)) {
                    url = Strings.HTTP + url;
                  }
                  values.put(FeedData.FeedColumns.URL, url);

                  String name = nameEditText.getText().toString();

                  values.put(FeedData.FeedColumns.NAME, name.trim().length() > 0 ? name : null);
                  values.put(FeedData.FeedColumns.FETCHMODE, 0);
                  values.put(
                      FeedData.FeedColumns.WIFIONLY, refreshOnlyWifiCheckBox.isChecked() ? 1 : 0);
                  values.put(FeedData.FeedColumns.ERROR, (String) null);
                  getContentResolver().update(getIntent().getData(), values, null, null);

                  setResult(RESULT_OK);
                  finish();
                }
              }
            });
  }

  ((Button) findViewById(R.id.button_cancel))
      .setOnClickListener(
          new OnClickListener() {
            public void onClick(View v) {
              finish();
            }
          });
}

private Contact loadContactById(
    ContentResolver contentResolver,
    Context context,
    long contactId,
    String sortOrder,
    boolean showHighRes,
    boolean supportDirectDial,
    Rect size) {
  Contact contact = new Contact();
  contact.setContactId(contactId);
  Uri contactUri = ContentUris.withAppendedId(ContactsContract.Contacts.CONTENT_URI, contactId);
  contact.setContactUri(
      ContactsContract.Contacts.getLookupUri(context.getContentResolver(), contactUri));

  Uri uri = ContactsContract.Contacts.CONTENT_URI;
  CursorLoader loader =
      new CursorLoader(
          context,
          uri,
          CONTACT_PROJECTION,
          CONTACT_SELECTION,
          new String[] {String.valueOf(contactId)},
          sortOrder);

  Cursor cursor = null;
  try {
    loader.startLoading();
    cursor = loader.loadInBackground();
    if (cursor == null) {
      // did not find the contact
      return null;
    } else if (cursor.moveToFirst()) {
      String displayName = cursor.getString(1);
      String photoUri = cursor.getString(2);
      contact.setContactId(contactId);
      contact.setDisplayName(displayName);
      contact.setPhotoUri(photoUri);
      if (photoUri != null && photoUri.length() > 0) {
        contact.setPhoto(loadContactPhoto(contentResolver, contactUri, showHighRes, size));
      }
      int hasPhoneNumber = cursor.getInt(3);
      if (supportDirectDial) {
        if (hasPhoneNumber > 0) {
          contact.setPhoneNumbers(loadPhoneNumbers(contentResolver, context, contactId));
        } else {
          // users want direct dial but this contact has no phone number
          return null;
        }
      }
    }
  } finally {
    loader.stopLoading();
    if (cursor != null) {
      cursor.close();
    }
  }
  return contact;
}

