private static final MmsSmsDeliveryInfo getSmsNewDeliveryInfo(Context context) {
  ContentResolver resolver = context.getContentResolver();
  Cursor cursor =
      SqliteWrapper.query(
          context,
          resolver,
          Sms.CONTENT_URI,
          SMS_STATUS_PROJECTION,
          NEW_DELIVERY_SM_CONSTRAINT,
          null,
          Sms.DATE);

  if (cursor == null) return null;

  try {
    if (!cursor.moveToLast()) return null;

    String address = cursor.getString(COLUMN_SMS_ADDRESS);

    String name = Contact.get(address, true).getName();

    long timeMillis = 3000;

    return new MmsSmsDeliveryInfo(
        String.format(
            context.getString(R.string.delivery_toast_body),
            (name == null) ? address : name.replace('\n', ' ').replace('\r', ' ')),
        timeMillis);

  } finally {
    cursor.close();
  }
}

