private int parsePingResult(InputStream is, ContentResolver cr, HashMap<String, Integer> errorMap)
    throws IOException, StaleFolderListException, IllegalHeartbeatException,
        CommandStatusException {
  PingParser pp = new PingParser(is, this);
  if (pp.parse()) {
    // True indicates some mailboxes need syncing...
    // syncList has the serverId's of the mailboxes...
    mBindArguments[0] = Long.toString(mAccount.mId);
    mPingChangeList = pp.getSyncList();
    for (String serverId : mPingChangeList) {
      mBindArguments[1] = serverId;
      Cursor c =
          cr.query(
              Mailbox.CONTENT_URI,
              Mailbox.CONTENT_PROJECTION,
              WHERE_ACCOUNT_KEY_AND_SERVER_ID,
              mBindArguments,
              null);
      if (c == null) throw new ProviderUnavailableException();
      try {
        if (c.moveToFirst()) {

          /**
           * Check the boxes reporting changes to see if there really were any... We do this
           * because bugs in various Exchange servers can put us into a looping behavior by
           * continually reporting changes in a mailbox, even when there aren't any.
           *
           * <p>This behavior is seemingly random, and therefore we must code defensively by
           * backing off of push behavior when it is detected.
           *
           * <p>One known cause, on certain Exchange 2003 servers, is acknowledged by Microsoft,
           * and the server hotfix for this case can be found at
           * http://support.microsoft.com/kb/923282
           */

          // Check the status of the last sync
          String status = c.getString(Mailbox.CONTENT_SYNC_STATUS_COLUMN);
          int type = ExchangeService.getStatusType(status);
          // This check should always be true...
          if (type == ExchangeService.SYNC_PING) {
            int changeCount = ExchangeService.getStatusChangeCount(status);
            if (changeCount > 0) {
              errorMap.remove(serverId);
            } else if (changeCount == 0) {
              // This means that a ping reported changes in error; we keep a count
              // of consecutive errors of this kind
              String name = c.getString(Mailbox.CONTENT_DISPLAY_NAME_COLUMN);
              Integer failures = errorMap.get(serverId);
              if (failures == null) {
                userLog("Last ping reported changes in error for: ", name);
                errorMap.put(serverId, 1);
              } else if (failures > MAX_PING_FAILURES) {
                // We'll back off of push for this box
                pushFallback(c.getLong(Mailbox.CONTENT_ID_COLUMN));
                continue;
              } else {
                userLog("Last ping reported changes in error for: ", name);
                errorMap.put(serverId, failures + 1);
              }
            }
          }

          // If there were no problems with previous sync, we'll start another one
          ExchangeService.startManualSync(
              c.getLong(Mailbox.CONTENT_ID_COLUMN), ExchangeService.SYNC_PING, null);
        }
      } finally {
        c.close();
      }
    }
  }
  return pp.getSyncStatus();
}

/**
 * Load select fields from cursor, used to populate the Spinners and closes the cursor
 *
 * @param cursor the cursor from the DAO, from a select query on the fields ID and NAME
 * @param intId true if the id to map from the database is an integer. If false, the id is a
 *     String (like in the country table)
 * @return the map, NAME (String)-> ID (Integer)
 */
private SelectFieldsValue loadSelectFieldsFromCursor(final Cursor cursor, final boolean intId) {
  final Map<String, Integer> mapNameId = new HashMap<String, Integer>();
  final Map<String, String> mapNameCode = new HashMap<String, String>();
  // init the map and the list with "ALL" with id = 0
  mapNameId.put(this.activity.getString(R.string.search_all), 0);
  mapNameCode.put(this.activity.getString(R.string.search_all), BasicConstants.EMPTY_STRING);

  final List<String> fieldsValues = new ArrayList<String>();

  fieldsValues.add(this.activity.getString(R.string.search_all));
  if (cursor != null) {
    final int nbResults = cursor.getCount();
    for (int i = 0; i < nbResults; i++) {
      cursor.moveToPosition(i);
      final int idIndex = cursor.getColumnIndexOrThrow(IOrnidroidDAO.ID);
      final int nameIndex = cursor.getColumnIndexOrThrow(IOrnidroidDAO.NAME_COLUMN_NAME);

      if (intId) {
        mapNameId.put(cursor.getString(nameIndex), cursor.getInt(idIndex));
      } else {
        // in this case, if the id is a string (country code)
        mapNameCode.put(cursor.getString(nameIndex), cursor.getString(idIndex));
      }
      fieldsValues.add(cursor.getString(nameIndex));
    }
    cursor.close();
  }
  final SelectFieldsValue sfv = new SelectFieldsValue(mapNameId, mapNameCode, fieldsValues);
  return sfv;
}

/*package*/ void deleteParser(ArrayList<Long> deletes, int entryTag) throws IOException {
  while (nextTag(entryTag) != END) {
    switch (tag) {
      case Tags.SYNC_SERVER_ID:
        String serverId = getValue();
        // Find the message in this mailbox with the given serverId
        Cursor c = getServerIdCursor(serverId, MESSAGE_ID_SUBJECT_PROJECTION);
        try {
          if (c.moveToFirst()) {
            deletes.add(c.getLong(MESSAGE_ID_SUBJECT_ID_COLUMN));
            if (Eas.USER_LOG) {
              userLog(
                  "Deleting ",
                  serverId + ", " + c.getString(MESSAGE_ID_SUBJECT_SUBJECT_COLUMN));
            }
          }
        } finally {
          c.close();
        }
        break;
      default:
        skipTag();
    }
  }
}

