private String getFilePathFromResourcePath(String resourcePath) {
  String filePath = "";
  // Discussion about the warning
  // http://simonmacdonald.blogspot.fr/2012/07/phonegap-android-plugins-sometimes-we.html
  ContentResolver cr = this.cordova.getContext().getContentResolver();
  String[] projection = {MediaStore.Images.Media.DATA};
  Cursor cur = cr.query(Uri.parse(resourcePath), projection, null, null, null);
  if (cur != null) {
    cur.moveToFirst();
    filePath = cur.getString(0);
  }

  return filePath;
}

/**
 * Returns the default group (e.g. "My Contacts") for the current raw contact's account. Returns
 * -1 if there is no such group.
 */
private long getDefaultGroupId() {
  String accountType = mState.getValues().getAsString(RawContacts.ACCOUNT_TYPE);
  String accountName = mState.getValues().getAsString(RawContacts.ACCOUNT_NAME);
  String accountDataSet = mState.getValues().getAsString(RawContacts.DATA_SET);
  mGroupMetaData.moveToPosition(-1);
  while (mGroupMetaData.moveToNext()) {
    String name = mGroupMetaData.getString(GroupMetaDataLoader.ACCOUNT_NAME);
    String type = mGroupMetaData.getString(GroupMetaDataLoader.ACCOUNT_TYPE);
    String dataSet = mGroupMetaData.getString(GroupMetaDataLoader.DATA_SET);
    if (name.equals(accountName)
        && type.equals(accountType)
        && Objects.equal(dataSet, accountDataSet)) {
      long groupId = mGroupMetaData.getLong(GroupMetaDataLoader.GROUP_ID);
      if (!mGroupMetaData.isNull(GroupMetaDataLoader.AUTO_ADD)
          && mGroupMetaData.getInt(GroupMetaDataLoader.AUTO_ADD) != 0) {
        return groupId;
      }
    }
  }
  return -1;
}

