public void testInsert() {
  ContentValues values = new ContentValues();

  /*
   * Test insert into uri_movie
   */
  values.put(MoviesTable.COLUMN_TITLE, "batman");
  values.put(MoviesTable.COLUMN_RATING, 1);
  values.put(MoviesTable.COLUMN_NOTE, "");
  values.put(MoviesTable.COLUMN_DATE, 0);
  values.put(MoviesTable.COLUMN_IMDB_ID, 0);
  values.put(MoviesTable.COLUMN_POSTER_LARGE, "");
  values.put(MoviesTable.COLUMN_POSTER_SMALL, "");

  Uri tmpUri = contentResolver.insert(uri_movies, values);
  long movieId = Long.parseLong(tmpUri.getLastPathSegment());
  // Check that the id of the movie was returned.
  assertTrue(movieId != 0);

  Cursor cursor =
      contentResolver.query(
          uri_movies, null, MoviesTable.COLUMN_MOVIE_ID + " = " + movieId, null, null);
  assertEquals(cursor.getCount(), 1);

  cursor.moveToFirst();
  assertTrue(cursor.getString(1).equals("batman"));

  /*
   * Test insert the movie with the same title into uri_movie
   */
  values = new ContentValues();

  values.put(MoviesTable.COLUMN_TITLE, "batman");
  values.put(MoviesTable.COLUMN_RATING, 1);
  values.put(MoviesTable.COLUMN_NOTE, "");
  values.put(MoviesTable.COLUMN_DATE, 0);
  values.put(MoviesTable.COLUMN_IMDB_ID, 0);
  values.put(MoviesTable.COLUMN_POSTER_LARGE, "");
  values.put(MoviesTable.COLUMN_POSTER_SMALL, "");

  tmpUri = contentResolver.insert(uri_movies, values);
  long zeroId = Long.parseLong(tmpUri.getLastPathSegment());

  // If the movie already existed insert should return 0.
  assertEquals(zeroId, 0);

  // Confirms that there exist one and only one movie with the title 'batman'
  cursor =
      contentResolver.query(
          uri_movies, null, MoviesTable.COLUMN_TITLE + " = 'batman'", null, null);
  assertEquals(cursor.getCount(), 1);

  /*
   * Test insert into uri_tag
   */
  values = new ContentValues();

  values.put(TagsTable.COLUMN_NAME, "tag");
  try {
    tmpUri = contentResolver.insert(uri_tags, values);
    Assert.fail("Should throw UnsupportedOperationException");
  } catch (UnsupportedOperationException e) {
  }

  /*
   * Test insert into uri_hastag
   */
  values = new ContentValues();

  values.put(MoviesTable.COLUMN_MOVIE_ID, movieId);
  values.put(TagsTable.COLUMN_NAME, "tag");
  tmpUri = contentResolver.insert(uri_hastag, values);
  long tagId = Long.parseLong(tmpUri.getLastPathSegment());

  cursor =
      contentResolver.query(
          uri_hastag,
          null,
          HasTagTable.COLUMN_MOVIE_ID
              + " = "
              + movieId
              + " AND "
              + HasTagTable.COLUMN_TAG_ID
              + " = "
              + tagId,
          null,
          null);
  assertEquals(cursor.getCount(), 1);

  /*
   * Attach a tag to a movie it is already attached to.
   */
  tmpUri = contentResolver.insert(uri_hastag, values);
  zeroId = Long.parseLong(tmpUri.getLastPathSegment());

  // If the attachment already existed insert should return 0.
  assertEquals(zeroId, 0);

  cursor =
      contentResolver.query(
          uri_hastag,
          null,
          HasTagTable.COLUMN_MOVIE_ID
              + " = "
              + movieId
              + " AND "
              + HasTagTable.COLUMN_TAG_ID
              + " = "
              + tagId,
          null,
          null);
  /* Confirms that there exist one and only one attachment between the
   * movie 'batman' and the tag 'tag'
   */
  assertEquals(cursor.getCount(), 1);
}

/**
 * Returns the mime type for a given attachment. There are three possible results: - If thumbnail
 * Uri, always returns "image/png" (even if there's no attachment) - If the attachment does not
 * exist, returns null - Returns the mime type of the attachment
 */
@Override
public String getType(Uri uri) {
  List<String> segments = uri.getPathSegments();
  String accountId = segments.get(0);
  String id = segments.get(1);
  String format = segments.get(2);
  if (FORMAT_THUMBNAIL.equals(format)) {
    return "image/png";
  } else {
    uri = ContentUris.withAppendedId(Attachment.CONTENT_URI, Long.parseLong(id));
    Cursor c =
        getContext().getContentResolver().query(uri, MIME_TYPE_PROJECTION, null, null, null);
    try {
      if (c.moveToFirst()) {
        String mimeType = c.getString(MIME_TYPE_COLUMN_MIME_TYPE);
        String fileName = c.getString(MIME_TYPE_COLUMN_FILENAME);
        mimeType = inferMimeType(fileName, mimeType);
        return mimeType;
      }
    } finally {
      c.close();
    }
    return null;
  }
}

/** Loads the profile into a MatrixCursor. */
private MatrixCursor loadProfile() {
  Cursor cursor =
      getContext().getContentResolver().query(Profile.CONTENT_URI, mProjection, null, null, null);
  try {
    MatrixCursor matrix = new MatrixCursor(mProjection);
    Object[] row = new Object[mProjection.length];
    while (cursor.moveToNext()) {
      for (int i = 0; i < row.length; i++) {
        row[i] = cursor.getString(i);
      }
      matrix.addRow(row);
    }
    return matrix;
  } finally {
    cursor.close();
  }
}

/** Constructs an "_attachments" dictionary for a revision, to be inserted in its JSON body. */
public Map<String, Object> getAttachmentsDictForSequenceWithContent(
    long sequence, boolean withContent) {
  assert (sequence > 0);

  Cursor cursor = null;

  String args[] = {Long.toString(sequence)};
  try {
    cursor =
        database.rawQuery(
            "SELECT filename, key, type, length, revpos FROM attachments WHERE sequence=?", args);

    if (!cursor.moveToFirst()) {
      return null;
    }

    Map<String, Object> result = new HashMap<String, Object>();

    while (!cursor.isAfterLast()) {

      byte[] keyData = cursor.getBlob(1);
      TDBlobKey key = new TDBlobKey(keyData);
      String digestString = "sha1-" + Base64.encodeBytes(keyData);
      String dataBase64 = null;
      if (withContent) {
        byte[] data = attachments.blobForKey(key);
        if (data != null) {
          dataBase64 = Base64.encodeBytes(data);
        } else {
          Log.w(TDDatabase.TAG, "Error loading attachment");
        }
      }

      Map<String, Object> attachment = new HashMap<String, Object>();
      if (dataBase64 == null) {
        attachment.put("stub", true);
      } else {
        attachment.put("data", dataBase64);
      }
      attachment.put("digest", digestString);
      attachment.put("content_type", cursor.getString(2));
      attachment.put("length", cursor.getInt(3));
      attachment.put("revpos", cursor.getInt(4));

      result.put(cursor.getString(0), attachment);

      cursor.moveToNext();
    }

    return result;

  } catch (SQLException e) {
    Log.e(TDDatabase.TAG, "Error getting attachments for sequence", e);
    return null;
  } finally {
    if (cursor != null) {
      cursor.close();
    }
  }
}

