/** Brings legacy settings table in sync with the new settings. */
public void copySettingsToLegacySettings() {
  SQLiteDatabase db = mDbHelper.getWritableDatabase();
  Cursor cursor = db.rawQuery(SettingsMatchQuery.SQL, null);
  try {
    while (cursor.moveToNext()) {
      String accountName = cursor.getString(SettingsMatchQuery.ACCOUNT_NAME);
      String accountType = cursor.getString(SettingsMatchQuery.ACCOUNT_TYPE);
      String value = cursor.getString(SettingsMatchQuery.SHOULD_SYNC);
      mValues.clear();
      mValues.put(android.provider.Contacts.Settings._SYNC_ACCOUNT, accountName);
      mValues.put(android.provider.Contacts.Settings._SYNC_ACCOUNT_TYPE, accountType);
      mValues.put(
          android.provider.Contacts.Settings.KEY,
          android.provider.Contacts.Settings.SYNC_EVERYTHING);
      mValues.put(android.provider.Contacts.Settings.VALUE, value);
      updateSetting(db, accountName, accountType, mValues);
    }
  } finally {
    cursor.close();
  }
}

private static String getTitle(Context context, Uri uri, boolean followSettingsUri) {
  Cursor cursor = null;
  ContentResolver res = context.getContentResolver();

  String title = null;

  if (uri != null) {
    String authority = uri.getAuthority();

    if (Settings.AUTHORITY.equals(authority)) {
      if (followSettingsUri) {
        Uri actualUri =
            RingtoneManager.getActualDefaultRingtoneUri(
                context, RingtoneManager.getDefaultType(uri));
        String actualTitle = getTitle(context, actualUri, false);
        title =
            context.getString(
                com.android.internal.R.string.ringtone_default_with_actual, actualTitle);
      }
    } else if (RingtoneManager.THEME_AUTHORITY.equals(authority)) {
      Uri themes = Uri.parse("content://com.tmobile.thememanager.themes/themes");
      title =
          stringForQuery(
              res.query(
                  themes,
                  new String[] {"ringtone_name"},
                  "ringtone_uri = ?",
                  new String[] {uri.toString()},
                  null));
      if (title == null) {
        title =
            stringForQuery(
                res.query(
                    themes,
                    new String[] {"notif_ringtone_name"},
                    "notif_ringtone_uri = ?",
                    new String[] {uri.toString()},
                    null));
      }
    } else {
      if (DrmStore.AUTHORITY.equals(authority)) {
        cursor = res.query(uri, DRM_COLUMNS, null, null, null);
      } else if (MediaStore.AUTHORITY.equals(authority)) {
        cursor = res.query(uri, MEDIA_COLUMNS, null, null, null);
      }

      try {
        if (cursor != null && cursor.getCount() == 1) {
          cursor.moveToFirst();
          return cursor.getString(2);
        } else {
          title = uri.getLastPathSegment();
        }
      } finally {
        if (cursor != null) {
          cursor.close();
        }
      }
    }
  }

  if (title == null) {
    title = context.getString(com.android.internal.R.string.ringtone_unknown);

    if (title == null) {
      title = "";
    }
  }

  return title;
}

/**
 * Builds a text representation of the scanning history. Each scan is encoded on one line,
 * terminated by a line break (\r\n). The values in each line are comma-separated, and
 * double-quoted. Double-quotes within values are escaped with a sequence of two double-quotes.
 * The fields output are:
 *
 * <ul>
 *   <li>Raw text
 *   <li>Display text
 *   <li>Format (e.g. QR_CODE)
 *   <li>Timestamp
 *   <li>Formatted version of timestamp
 * </ul>
 */
CharSequence buildHistory() {
  StringBuilder historyText = new StringBuilder(1000);
  SQLiteOpenHelper helper = new DBHelper(activity);
  SQLiteDatabase db = null;
  Cursor cursor = null;
  try {
    db = helper.getWritableDatabase();
    cursor =
        db.query(
            DBHelper.TABLE_NAME,
            COLUMNS,
            null,
            null,
            null,
            null,
            DBHelper.TIMESTAMP_COL + " DESC");

    while (cursor.moveToNext()) {

      historyText.append('"').append(massageHistoryField(cursor.getString(0))).append("\",");
      historyText.append('"').append(massageHistoryField(cursor.getString(1))).append("\",");
      historyText.append('"').append(massageHistoryField(cursor.getString(2))).append("\",");
      historyText.append('"').append(massageHistoryField(cursor.getString(3))).append("\",");

      // Add timestamp again, formatted
      long timestamp = cursor.getLong(3);
      historyText
          .append('"')
          .append(massageHistoryField(EXPORT_DATE_TIME_FORMAT.format(new Date(timestamp))))
          .append("\",");

      // Above we're preserving the old ordering of columns which had formatted data in position 5

      historyText.append('"').append(massageHistoryField(cursor.getString(4))).append("\"\r\n");
    }
    return historyText;
  } finally {
    close(cursor, db);
  }
}

public ArrayList<HashMap<String, String>> query(String sql, String[] args) {
  ArrayList<HashMap<String, String>> rowList = new ArrayList<HashMap<String, String>>();
  try {
    db = dbh.getReadableDatabase();
    cursor = db.rawQuery(sql, args);
    while (cursor.moveToNext()) {
      HashMap<String, String> colList = new HashMap<String, String>();
      int len = cursor.getColumnCount();
      for (int i = 0; i < len; i++) {
        colList.put(cursor.getColumnName(i), cursor.getString(i));
      }
      rowList.add(colList);
    }
  } catch (Exception e) {
    e.printStackTrace();
  } finally {
    cursor.close();
    db.close();
  }
  return rowList;
}

private String getStringFromColumn(Cursor c, String columnName) {
  int index = c.getColumnIndexOrThrow(columnName);
  String value = c.getString(index);
  if (value != null && value.length() > 0) {
    return value;
  } else {
    return null;
  }
}

