private void updateTables5() {
  myDatabase.execSQL(
      "CREATE TABLE IF NOT EXISTS Bookmarks("
          + "bookmark_id INTEGER PRIMARY KEY,"
          + "book_id INTEGER NOT NULL REFERENCES Books(book_id),"
          + "bookmark_text TEXT NOT NULL,"
          + "creation_time INTEGER NOT NULL,"
          + "modification_time INTEGER,"
          + "access_time INTEGER,"
          + "access_counter INTEGER NOT NULL,"
          + "paragraph INTEGER NOT NULL,"
          + "word INTEGER NOT NULL,"
          + "char INTEGER NOT NULL)");

  myDatabase.execSQL(
      "CREATE TABLE IF NOT EXISTS BookState("
          + "book_id INTEGER UNIQUE NOT NULL REFERENCES Books(book_id),"
          + "paragraph INTEGER NOT NULL,"
          + "word INTEGER NOT NULL,"
          + "char INTEGER NOT NULL)");
  Cursor cursor = myDatabase.rawQuery("SELECT book_id,file_name FROM Books", null);
  final SQLiteStatement statement =
      myDatabase.compileStatement(
          "INSERT INTO BookState (book_id,paragraph,word,char) VALUES (?,?,?,?)");
  while (cursor.moveToNext()) {
    final long bookId = cursor.getLong(0);
    final String fileName = cursor.getString(1);
    final int position = new ZLIntegerOption(fileName, "PositionInBuffer", 0).getValue();
    final int paragraph = new ZLIntegerOption(fileName, "Paragraph_" + position, 0).getValue();
    final int word = new ZLIntegerOption(fileName, "Word_" + position, 0).getValue();
    final int chr = new ZLIntegerOption(fileName, "Char_" + position, 0).getValue();
    if ((paragraph != 0) || (word != 0) || (chr != 0)) {
      statement.bindLong(1, bookId);
      statement.bindLong(2, paragraph);
      statement.bindLong(3, word);
      statement.bindLong(4, chr);
      statement.execute();
    }
    ZLConfig.Instance().removeGroup(fileName);
  }
  cursor.close();
}

/**
 * Moves music cursor to next valid position
 *
 * @return true if we looped in this move
 */
public boolean navigateTracks(int dir) {
  String currentKey = mMusicCursor.getString(TITLE_KEY);
  boolean valid = true;
  boolean hasLooped = false;
  do {

    boolean success = true;
    // Move according to direction
    if (dir > 0) {
      if (!mMusicCursor.moveToNext()) {
        mMusicCursor.moveToFirst();
        success = false;
      }
    } else {
      if (!mMusicCursor.moveToPrevious()) {
        mMusicCursor.moveToLast();
        success = false;
      }
    }
    // Make sure we are not looping in search of valid files
    if (!success) {
      if (hasLooped) {
        return false;
      }
      hasLooped = true;
    }
    File test = new File(mMusicCursor.getString(FILEPATH));
    valid = !currentKey.equals(mMusicCursor.getString(TITLE_KEY)) && test.exists();
  } while (!valid);
  return !hasLooped;
}

/**
 * Called by JNI. Open a file chooser to upload a file.
 *
 * @param acceptType The value of the 'accept' attribute of the input tag associated with this
 *     file picker.
 * @return String version of the URI.
 */
private String openFileChooser(String acceptType) {
  Uri uri = mCallbackProxy.openFileChooser(acceptType);
  if (uri != null) {
    String filePath = "";
    // Note - querying for MediaStore.Images.Media.DATA
    // seems to work for all content URIs, not just images
    Cursor cursor =
        mContext
            .getContentResolver()
            .query(uri, new String[] {MediaStore.Images.Media.DATA}, null, null, null);
    if (cursor != null) {
      try {
        if (cursor.moveToNext()) {
          filePath = cursor.getString(0);
        }
      } finally {
        cursor.close();
      }
    } else {
      filePath = uri.getLastPathSegment();
    }
    String uriString = uri.toString();
    BrowserFrame.sJavaBridge.storeFilePathForContentUri(filePath, uriString);
    return uriString;
  }
  return "";
}

