public void testContentProviderAcquisition() throws RemoteException {
  // given
  Account account1 = new Account(TEST_NAME_1);
  Account account2 = new Account(TEST_NAME_2);
  RuntimeExceptionDao<Account, Integer> simpleDao =
      getHelper().getRuntimeExceptionDao(Account.class);
  simpleDao.create(account1);
  simpleDao.create(account2);
  List<Account> accountList = simpleDao.queryForAll();
  assertEquals(2, accountList.size());

  // when
  ContentProviderClient contentProviderClient =
      this.resolver.acquireContentProviderClient(AccountContract.CONTENT_URI);
  Cursor cursor =
      contentProviderClient.query(AccountContract.CONTENT_URI, null, null, null, null);

  // then
  accountList = new ArrayList<Account>();
  while (cursor.moveToNext()) {
    Account account = new Account(cursor.getString(1));
    accountList.add(account);
  }
  cursor.close();
  contentProviderClient.release();

  assertEquals(2, accountList.size());
  assertEquals(TEST_NAME_1, accountList.get(0).getName());
  assertEquals(TEST_NAME_2, accountList.get(1).getName());
}

public List<GameSummary> findAllGameSummaries() {
  synchronized (GameDBHelper.class) {
    String[] columns = {
      "g." + COLUMN_ID,
      "g." + COLUMN_NAME,
      "g." + COLUMN_DATE_SAVED,
      // player names; the "separator" is a trick to ensure that we can cleanly separate the
      // response,
      // and put it into the proper order, since group_concat is always unordered in sqlite
      "group_concat((ps.name || '"
          + GROUP_CONCAT_INNER_SEPARATOR
          + "' || ps.playerNumber), '"
          + GROUP_CONCAT_SEPARATOR
          + "')",
      "max(length(ps.history) - length(replace(ps.history, ',', '')) + 1)" // num rounds
    };

    String table =
        TABLE_GAMES
            + " g join "
            + TABLE_PLAYER_SCORES
            + " ps "
            + " on g."
            + COLUMN_ID
            + " = ps."
            + COLUMN_GAME_ID;
    String groupBy = "g." + COLUMN_ID;

    Cursor cursor = null;

    try {

      cursor = db.query(table, columns, null, null, groupBy, null, null);

      List<GameSummary> result = new ArrayList<GameSummary>();

      // re-use sparse array for performance
      SparseArray<String> playerNumbersToNames = new SparseArray<String>();

      while (cursor.moveToNext()) {
        GameSummary gameSummary = new GameSummary();

        gameSummary.setId(cursor.getInt(0));
        gameSummary.setName(cursor.getString(1));
        gameSummary.setDateSaved(cursor.getLong(2));

        String playerNumbersAndNames = cursor.getString(3);
        // sort by player number, get player names in order (no way to do this in sqlite,
        // unfortunately)

        playerNumbersToNames.clear();
        for (String playerNumberAndName :
            StringUtil.split(playerNumbersAndNames, GROUP_CONCAT_SEPARATOR)) {
          int idx = playerNumberAndName.indexOf(GROUP_CONCAT_INNER_SEPARATOR);
          String playerName = playerNumberAndName.substring(0, idx);
          int playerNumber =
              Integer.parseInt(
                  playerNumberAndName.substring(idx + GROUP_CONCAT_INNER_SEPARATOR.length()));
          playerNumbersToNames.put(playerNumber, playerName);
        }
        List<String> playerNames = new ArrayList<String>(playerNumbersToNames.size());
        for (int i = 0, len = playerNumbersToNames.size(); i < len; i++) {
          int playerNumber = playerNumbersToNames.keyAt(i);
          playerNames.add(playerNumbersToNames.get(playerNumber));
        }
        gameSummary.setPlayerNames(playerNames);

        gameSummary.setNumRounds(cursor.getInt(4));

        result.add(gameSummary);
      }

      return result;

    } finally {
      if (cursor != null) {
        cursor.close();
      }
    }
  }
}

@Override
protected void onActivityResult(int requestCode, int resultCode, Intent data) {
  // there are cases where the contact picker may end up sending us more than one
  // request. We want to ignore the request if we're not in the correct state.
  if (requestCode == VOICEMAIL_PROVIDER_CFG_ID) {
    boolean failure = false;

    // No matter how the processing of result goes lets clear the flag
    if (DBG) log("mVMProviderSettingsForced: " + mVMProviderSettingsForced);
    final boolean isVMProviderSettingsForced = mVMProviderSettingsForced;
    mVMProviderSettingsForced = false;

    String vmNum = null;
    if (resultCode != RESULT_OK) {
      if (DBG) log("onActivityResult: vm provider cfg result not OK.");
      failure = true;
    } else {
      if (data == null) {
        if (DBG) log("onActivityResult: vm provider cfg result has no data");
        failure = true;
      } else {
        if (data.getBooleanExtra(SIGNOUT_EXTRA, false)) {
          if (DBG) log("Provider requested signout");
          if (isVMProviderSettingsForced) {
            if (DBG) log("Going back to previous provider on signout");
            switchToPreviousVoicemailProvider();
          } else {
            final String victim = getCurrentVoicemailProviderKey();
            if (DBG) log("Relaunching activity and ignoring " + victim);
            Intent i = new Intent(ACTION_ADD_VOICEMAIL);
            i.putExtra(IGNORE_PROVIDER_EXTRA, victim);
            i.setFlags(Intent.FLAG_ACTIVITY_CLEAR_TOP);
            this.startActivity(i);
          }
          return;
        }
        vmNum = data.getStringExtra(VM_NUMBER_EXTRA);
        if (vmNum == null || vmNum.length() == 0) {
          if (DBG) log("onActivityResult: vm provider cfg result has no vmnum");
          failure = true;
        }
      }
    }
    if (failure) {
      if (DBG) log("Failure in return from voicemail provider");
      if (isVMProviderSettingsForced) {
        switchToPreviousVoicemailProvider();
      } else {
        if (DBG) log("Not switching back the provider since this is not forced config");
      }
      return;
    }
    mChangingVMorFwdDueToProviderChange = isVMProviderSettingsForced;
    final String fwdNum = data.getStringExtra(FWD_NUMBER_EXTRA);

    // TODO(iliat): It would be nice to load the current network setting for this and
    // send it to the provider when it's config is invoked so it can use this as default
    final int fwdNumTime = data.getIntExtra(FWD_NUMBER_TIME_EXTRA, 20);

    if (DBG)
      log(
          "onActivityResult: vm provider cfg result "
              + (fwdNum != null ? "has" : " does not have")
              + " forwarding number");
    saveVoiceMailAndForwardingNumber(
        getCurrentVoicemailProviderKey(),
        new VoiceMailProviderSettings(vmNum, fwdNum, fwdNumTime));
    return;
  }

  if (resultCode != RESULT_OK) {
    if (DBG) log("onActivityResult: contact picker result not OK.");
    return;
  }

  Cursor cursor = getContentResolver().query(data.getData(), NUM_PROJECTION, null, null, null);
  if ((cursor == null) || (!cursor.moveToFirst())) {
    if (DBG) log("onActivityResult: bad contact data, no results found.");
    return;
  }

  switch (requestCode) {
    case VOICEMAIL_PREF_ID:
      mSubMenuVoicemailSettings.onPickActivityResult(cursor.getString(0));
      break;
    default:
      // TODO: may need exception here.
  }
}

