public static final MmsSmsNotificationInfo getSmsNewMessageNotificationInfo(
    Context context, Set<Long> threads) {
  ContentResolver resolver = context.getContentResolver();
  Cursor cursor =
      SqliteWrapper.query(
          context,
          resolver,
          Sms.CONTENT_URI,
          SMS_STATUS_PROJECTION,
          NEW_INCOMING_SM_CONSTRAINT,
          null,
          Sms.DATE + " desc");

  if (cursor == null) {
    return null;
  }

  try {
    if (!cursor.moveToFirst()) {
      return null;
    }

    String address = cursor.getString(COLUMN_SMS_ADDRESS);
    String body = cursor.getString(COLUMN_SMS_BODY);
    long threadId = cursor.getLong(COLUMN_THREAD_ID);
    long timeMillis = cursor.getLong(COLUMN_DATE);

    MmsSmsNotificationInfo info =
        getNewMessageNotificationInfo(
            address,
            body,
            context,
            R.drawable.stat_notify_sms,
            null,
            threadId,
            timeMillis,
            cursor.getCount());

    threads.add(threadId);
    while (cursor.moveToNext()) {
      threads.add(cursor.getLong(COLUMN_THREAD_ID));
    }

    return info;
  } finally {
    cursor.close();
  }
}

public String goNextArtist() {
  if (!taggedMusicAvailable) {
    return "";
  }
  int initialPosition = musicCursor.getPosition();
  while (musicCursor.moveToNext()) {
    if (!musicCursor.getString(ARTIST).equals(currentArtist)) {
      currentArtist = musicCursor.getString(ARTIST);
      return currentArtist;
    }
  }
  musicCursor.moveToFirst();
  while (musicCursor.getPosition() < initialPosition) {
    if (!musicCursor.getString(ARTIST).equals(currentArtist)) {
      currentArtist = musicCursor.getString(ARTIST);
      return currentArtist;
    }
    musicCursor.moveToNext();
  }
  currentArtist = musicCursor.getString(ARTIST);
  return currentArtist;
}

