  return str;
}

@Override
public View getView(int position, View v, ViewGroup parent) {
  ActivityWithAccount activity = (ActivityWithAccount) m_ctx.getActivity();
  if (v == null) {
    LayoutInflater vi = LayoutInflater.from(m_ctx.getActivity());
    v = vi.inflate(R.layout.view_comment, null);
  }

  if (m_cursor == null) {
    return v;
  }

  m_cursor.moveToPosition(position);
  JSONObject comment = null;
  try {
    comment = new JSONObject(m_cursor.getString(0));
  } catch (JSONException e) {
    throw new RuntimeException(e);
  }

  AvatarView authorAvatar = (AvatarView) v.findViewById(R.id.commentAuthorAvatar);
  TextView commentBody = (TextView) v.findViewById(R.id.commentBody);
  TextView commentMeta = (TextView) v.findViewById(R.id.commentMeta);
  ImageView image = (ImageView) v.findViewById(R.id.image);

  JSONObject author = comment.optJSONObject("author");
  if (author != null) {
    JSONObject imageObj = author.optJSONObject("image");
    if (imageObj != null)
      activity.getImageLoader().setImage(authorAvatar, Utils.getImageUrl(activity, imageObj));

    commentMeta.setText(
        m_ctx.getResources().getString(R.string.comment_meta_by)
            + " "
            + author.optString("displayName")
            + ", "
            + Utils.humanDate(comment.optString("published")));
  }

  JSONObject imageObj = comment.optJSONObject("image");
  if (imageObj != null) {
    image.setVisibility(View.VISIBLE);
    activity.getImageLoader().setImage(image, Utils.getImageUrl(activity, imageObj));
  } else {
    image.setVisibility(View.GONE);
  }

  Utils.updateStatebar(v, m_cursor.getInt(1), m_cursor.getInt(2), m_cursor.getInt(3));
  PumpHtml.setFromHtml(activity, commentBody, comment.optString("content"));

  return v;
}

public String peekNextTrack() {
  if (!taggedMusicAvailable) {
    return "";
  }
  int initialPosition = musicCursor.getPosition();
  while (musicCursor.moveToNext()) {
    if (musicCursor.getString(ARTIST).equals(currentArtist)
        && musicCursor.getString(ALBUM).equals(currentAlbum)
        && !musicCursor.getString(TRACK).equals(currentTrack)) {
      String track = musicCursor.getString(TRACK);
      musicCursor.moveToPosition(initialPosition);
      Log.e("peek end 0", musicCursor.getPosition() + " " + getCurrentSongInfo());
      return track;
    }
  }
  musicCursor.moveToFirst();
  while (musicCursor.getPosition() < initialPosition) {
    if (musicCursor.getString(ARTIST).equals(currentArtist)
        && musicCursor.getString(ALBUM).equals(currentAlbum)
        && !musicCursor.getString(TRACK).equals(currentTrack)) {
      String track = musicCursor.getString(TRACK);
      musicCursor.moveToPosition(initialPosition);
      Log.e("peek end 1", musicCursor.getPosition() + " " + getCurrentSongInfo());
      return track;
    }
    musicCursor.moveToNext();
  }
  Log.e("peek end 2", musicCursor.getPosition() + " " + getCurrentSongInfo());
  return musicCursor.getString(TRACK);
}

/**
 * Stores a custom dictionary for a given deck.
 *
 * @param dictionary integer number of dictionary, -1 if not set (standard dictionary will be
 *     used)
 */
public static void storeLookupDictionary(Context context, long did, int dictionary) {
  openDBIfClosed(context);
  Cursor cur = null;
  try {
    cur = mMetaDb.rawQuery("SELECT _id FROM customDictionary" + " WHERE did = " + did, null);
    if (cur.moveToNext()) {
      mMetaDb.execSQL(
          "UPDATE customDictionary "
              + "SET did = "
              + did
              + ", "
              + "dictionary="
              + Integer.toString(dictionary)
              + " "
              + "WHERE _id="
              + cur.getString(0)
              + ";");
      Log.i(AnkiDroidApp.TAG, "Store custom dictionary (" + dictionary + ") for deck " + did);
    } else {
      mMetaDb.execSQL(
          "INSERT INTO customDictionary (did, dictionary) VALUES (?, ?)",
          new Object[] {did, dictionary});
      Log.i(AnkiDroidApp.TAG, "Store custom dictionary (" + dictionary + ") for deck " + did);
    }
  } catch (Exception e) {
    Log.e("Error", "Error storing custom dictionary to MetaDB ", e);
  } finally {
    if (cur != null && !cur.isClosed()) {
      cur.close();
    }
  }
}

