private static Map<String, Integer> retrieveAllRows(Context context) {
  Map<String, Integer> list = new HashMap<String, Integer>();

  // Form an array specifying which columns to return.
  String[] projection = new String[] {Media.MediaColumns._ID, Media.MediaColumns.URI};

  // Get the base URI for the Media Files table in the Media content provider.
  Uri mediaFile = Media.MediaColumns.CONTENT_URI;

  // Make the query.
  Cursor cursor = context.getContentResolver().query(mediaFile, projection, null, null, null);

  while (cursor.moveToNext()) {
    int uriColumn = cursor.getColumnIndex(Media.MediaColumns.URI);
    int idColumn = cursor.getColumnIndex(Media.MediaColumns._ID);
    String uri = cursor.getString(uriColumn);
    int id = cursor.getInt(idColumn);
    list.put(uri, id);
  }

  cursor.close();

  return list;
}

/**
 * Get a uri's user-friendly display name
 *
 * @param context the application context
 * @param uri the uri to query
 * @return a user-friendly display name
 */
public static String getUriDisplayName(Context context, Uri uri) {
  String displayName = null;

  String scheme = uri.getScheme();

  if (scheme.startsWith("content")) {
    String[] proj = {OpenableColumns.DISPLAY_NAME};
    Cursor cursor = context.getContentResolver().query(uri, proj, null, null, null);

    if (cursor != null) {
      int columnIndex = cursor.getColumnIndexOrThrow(OpenableColumns.DISPLAY_NAME);
      cursor.moveToFirst();
      displayName = cursor.getString(columnIndex);

      cursor.close();
    }
  } else if (scheme.startsWith("file")) {
    displayName = uri.getLastPathSegment();
  }

  return displayName;
}

@Override
public void onLoadFinished(Loader<Cursor> loader, Cursor data) {
  // Remove the "Loading..." view
  mPullToRefreshView.setEmptyView(null);

  // Enable pull-to-refresh if allowed
  if (isPullToRefreshAllowed()) {
    setPullToRefreshEnabled(true);
  }

  Cursor cursor;
  if (mCursors.length > 1) {
    mCursors[loader.getId()] = data;
    cursor = new MergeCursorWithUniqueId(mCursors, getComparator());
    mUniqueIdColumn = cursor.getColumnIndex("_id");
  } else {
    cursor = data;
    mUniqueIdColumn = ID_COLUMN;
  }

  if (mIsThreadDisplay) {
    cursor.moveToFirst();
    mTitle = cursor.getString(SUBJECT_COLUMN);
    mTitle = Utility.stripSubject(mTitle);
    if (StringUtils.isNullOrEmpty(mTitle)) {
      mTitle = getString(R.string.general_no_subject);
    }
    refreshTitle();
  }

  cleanupSelected(cursor);

  mAdapter.swapCursor(cursor);

  resetActionMode();
  computeBatchDirection();
}

