public List<EditableEvent> getEvents(Context ctx, long rawContactId) {
  Log.i("Birthday", "Going to get events for " + rawContactId);

  String[] projection =
      new String[] {
        ContactsContract.CommonDataKinds.Event._ID,
        ContactsContract.CommonDataKinds.Event.START_DATE,
        ContactsContract.CommonDataKinds.Event.TYPE,
        ContactsContract.CommonDataKinds.Event.LABEL,
      };

  Cursor c =
      ctx.getContentResolver()
          .query(
              ContactsContract.Data.CONTENT_URI,
              projection,
              ContactsContract.Data.RAW_CONTACT_ID
                  + "= ? AND "
                  + ContactsContract.Data.MIMETYPE
                  + "= ? AND "
                  + "("
                  + ContactsContract.CommonDataKinds.Event.TYPE
                  + "="
                  + ContactsContract.CommonDataKinds.Event.TYPE_BIRTHDAY
                  + " OR "
                  + ContactsContract.CommonDataKinds.Event.TYPE
                  + "="
                  + ContactsContract.CommonDataKinds.Event.TYPE_ANNIVERSARY
                  + " OR "
                  + ContactsContract.CommonDataKinds.Event.TYPE
                  + "="
                  + ContactsContract.CommonDataKinds.Event.TYPE_CUSTOM
                  + " OR "
                  + ContactsContract.CommonDataKinds.Event.TYPE
                  + "="
                  + ContactsContract.CommonDataKinds.Event.TYPE_OTHER
                  + ")",
              new String[] {
                String.valueOf(rawContactId),
                ContactsContract.CommonDataKinds.Event.CONTENT_ITEM_TYPE
              },
              null);

  List<EditableEvent> events = new ArrayList<EditableEvent>();
  while (c != null && c.moveToNext()) {
    try {
      ParseResult parseResult = tryParseBDay(c.getString(1));

      EditableEvent evt =
          new EditableEvent(
              c.getLong(0),
              EventType.getEventType(c.getInt(2)),
              parseResult.date,
              parseResult.integrity,
              c.getString(3));

      events.add(evt);

    } catch (ParseException e) {
      Log.i(
          "Birthday",
          "Skipping "
              + c.getString(0)
              + " due to unparseable bday date ("
              + c.getString(2)
              + ")");
    } catch (IllegalArgumentException e) {
      Log.i(
          "Birthday",
          "Skipping "
              + c.getString(0)
              + " due to unparseable bday date ("
              + c.getString(2)
              + ")");
    } catch (NullPointerException e) {
      Log.i("Birthday", "Skipping contact id: " + c.getString(1) + " due to NPE.");
    }
  }

  if (c != null) {
    c.close();
  }
  Log.i("Birthday", "Returning " + events);
  return events;
}

private Bundle insertComment(String accountName, Bundle extras) {
  String body = getBodyExtra(extras);
  String parentThingId = getParentThingIdExtra(extras);
  String thingId = getThingIdExtra(extras);

  SQLiteDatabase db = helper.getWritableDatabase();
  SQLiteStatement updateSequence = db.compileStatement(UPDATE_SEQUENCE_STATEMENT);
  db.beginTransaction();
  try {
    // Queue an action to sync back the comment to the server.
    ContentValues values = new ContentValues(7);
    values.put(CommentActions.COLUMN_ACCOUNT, accountName);
    values.put(CommentActions.COLUMN_ACTION, CommentActions.ACTION_INSERT);
    values.put(CommentActions.COLUMN_PARENT_THING_ID, parentThingId);
    values.put(CommentActions.COLUMN_TEXT, body);
    values.put(CommentActions.COLUMN_THING_ID, thingId);

    long actionId = db.insert(CommentActions.TABLE_NAME, null, values);
    if (actionId == -1) {
      return null;
    }

    Cursor cursor =
        db.query(
            Sessions.TABLE_NAME,
            SESSION_ID_PROJECTION,
            Sessions.SELECT_BY_TYPE_AND_THING_ID,
            Array.of(Sessions.TYPE_COMMENTS, parentThingId),
            null,
            null,
            null);
    try {
      while (cursor.moveToNext()) {
        long sessionId = cursor.getLong(SESSION_INDEX_ID);

        // Get information from the session to figure out here to insert the comment.
        long headerDbId = -1;
        int position = -1;
        int nesting = -1;
        int sequence = -1;
        Cursor c =
            db.query(
                Comments.TABLE_NAME,
                INSERT_COMMENT_PROJECTION,
                Comments.SELECT_BY_SESSION_ID,
                Array.of(sessionId),
                null,
                null,
                Comments.SORT_BY_SEQUENCE_AND_ID);
        try {
          while (c.moveToNext()) {
            if (c.getPosition() == 0) {
              headerDbId = c.getLong(INSERT_COMMENT_INDEX_ID);
            }
            String rowThingId = c.getString(INSERT_COMMENT_INDEX_THING);
            if (thingId.equals(rowThingId)) {
              position = c.getPosition();
              break;
            }
          }
          if (headerDbId == -1 || position == -1) {
            continue;
          }

          CursorCommentList cl =
              new CursorCommentList(
                  c,
                  INSERT_COMMENT_INDEX_ID,
                  INSERT_COMMENT_INDEX_NESTING,
                  INSERT_COMMENT_INDEX_SEQUENCE);
          nesting = CommentLogic.getInsertNesting(cl, position);
          sequence = CommentLogic.getInsertSequence(cl, position);
        } finally {
          c.close();
        }

        // Increment the sequence numbers to make room for our comment
        updateSequence.bindLong(1, sessionId);
        updateSequence.bindLong(2, sequence);
        updateSequence.executeUpdateDelete();

        // Insert the placeholder comment with the proper sequence number.
        values.clear();
        values.put(Comments.COLUMN_ACCOUNT, accountName);
        values.put(Comments.COLUMN_AUTHOR, accountName);
        values.put(Comments.COLUMN_BODY, body);
        values.put(Comments.COLUMN_COMMENT_ACTION_ID, actionId);
        values.put(Comments.COLUMN_KIND, Kinds.KIND_COMMENT);
        values.put(Comments.COLUMN_NESTING, nesting);
        values.put(Comments.COLUMN_SEQUENCE, sequence);
        values.put(Comments.COLUMN_SESSION_ID, sessionId);
        long commentId = db.insert(Comments.TABLE_NAME, null, values);
        if (commentId == -1) {
          return null;
        }
      }
    } finally {
      cursor.close();
    }

    db.setTransactionSuccessful();
  } finally {
    db.endTransaction();
  }

  ContentResolver cr = getContext().getContentResolver();
  cr.notifyChange(COMMENT_ACTIONS_URI, null, SYNC);
  cr.notifyChange(COMMENTS_URI, null, NO_SYNC);
  return Bundle.EMPTY;
}

/** @see StringKeyValueStore#get(java.lang.String) */
public String get(String key) {

  String result = null;
  if (dbStore == null) {
    open();
  }
  StringBuffer where = new StringBuffer(KEY_COLUMN_NAME);
  where.append("=\"").append(key).append("\"");
  Cursor resultCursor =
      dbStore.query(
          true, tableName, QUERY_VALUE_COLUMN, where.toString(), null, null, null, null, null);

  if (resultCursor.getCount() > 0) {
    int colIndex = resultCursor.getColumnIndexOrThrow(VALUE_COLUMN_NAME);
    resultCursor.moveToFirst();
    result = resultCursor.getString(colIndex);
  }
  resultCursor.close();
  return result;
}

private static String getTextMessageDetails(Context context, Cursor cursor) {
  Log.d(TAG, "getTextMessageDetails");

  StringBuilder details = new StringBuilder();
  Resources res = context.getResources();

  // Message Type: Text message.
  details.append(res.getString(R.string.message_type_label));
  details.append(res.getString(R.string.text_message));

  // Address: ***
  details.append('\n');
  int smsType = cursor.getInt(MessageListAdapter.COLUMN_SMS_TYPE);
  if (Sms.isOutgoingFolder(smsType)) {
    details.append(res.getString(R.string.to_address_label));
  } else {
    details.append(res.getString(R.string.from_label));
  }
  details.append(cursor.getString(MessageListAdapter.COLUMN_SMS_ADDRESS));

  // Sent: ***
  if (smsType == Sms.MESSAGE_TYPE_INBOX) {
    long date_sent = cursor.getLong(MessageListAdapter.COLUMN_SMS_DATE_SENT);
    if (date_sent > 0) {
      details.append('\n');
      details.append(res.getString(R.string.sent_label));
      details.append(MessageUtils.formatTimeStampString(context, date_sent, true));
    }
  }

  // Received: ***
  details.append('\n');
  if (smsType == Sms.MESSAGE_TYPE_DRAFT) {
    details.append(res.getString(R.string.saved_label));
  } else if (smsType == Sms.MESSAGE_TYPE_INBOX) {
    details.append(res.getString(R.string.received_label));
  } else {
    details.append(res.getString(R.string.sent_label));
  }

  long date = cursor.getLong(MessageListAdapter.COLUMN_SMS_DATE);
  details.append(MessageUtils.formatTimeStampString(context, date, true));

  // Delivered: ***
  if (smsType == Sms.MESSAGE_TYPE_SENT) {
    // For sent messages with delivery reports, we stick the delivery time in the
    // date_sent column (see MessageStatusReceiver).
    long dateDelivered = cursor.getLong(MessageListAdapter.COLUMN_SMS_DATE_SENT);
    if (dateDelivered > 0) {
      details.append('\n');
      details.append(res.getString(R.string.delivered_label));
      details.append(MessageUtils.formatTimeStampString(context, dateDelivered, true));
    }
  }

  // Error code: ***
  int errorCode = cursor.getInt(MessageListAdapter.COLUMN_SMS_ERROR_CODE);
  if (errorCode != 0) {
    details.append('\n').append(res.getString(R.string.error_code_label)).append(errorCode);
  }

  return details.toString();
}

private void prescan(String filePath, boolean prescanFiles) throws RemoteException {
  Cursor c = null;
  String where = null;
  String[] selectionArgs = null;

  if (mFileCache == null) {
    mFileCache = new HashMap<String, FileCacheEntry>();
  } else {
    mFileCache.clear();
  }
  if (mPlayLists == null) {
    mPlayLists = new ArrayList<FileCacheEntry>();
  } else {
    mPlayLists.clear();
  }

  if (filePath != null) {
    // query for only one file
    where = Files.FileColumns.DATA + "=?";
    selectionArgs = new String[] {filePath};
  }

  // Build the list of files from the content provider
  try {
    if (prescanFiles) {
      // First read existing files from the files table

      c = mMediaProvider.query(mFilesUri, FILES_PRESCAN_PROJECTION, where, selectionArgs, null);

      if (c != null) {
        mWasEmptyPriorToScan = c.getCount() == 0;
        while (c.moveToNext()) {
          long rowId = c.getLong(FILES_PRESCAN_ID_COLUMN_INDEX);
          String path = c.getString(FILES_PRESCAN_PATH_COLUMN_INDEX);
          int format = c.getInt(FILES_PRESCAN_FORMAT_COLUMN_INDEX);
          long lastModified = c.getLong(FILES_PRESCAN_DATE_MODIFIED_COLUMN_INDEX);

          // Only consider entries with absolute path names.
          // This allows storing URIs in the database without the
          // media scanner removing them.
          if (path != null && path.startsWith("/")) {
            String key = path;
            if (mCaseInsensitivePaths) {
              key = path.toLowerCase();
            }

            FileCacheEntry entry = new FileCacheEntry(rowId, path, lastModified, format);
            mFileCache.put(key, entry);
          }
        }
        c.close();
        c = null;
      }
    }
  } finally {
    if (c != null) {
      c.close();
    }
  }

  // compute original size of images
  mOriginalCount = 0;
  c = mMediaProvider.query(mImagesUri, ID_PROJECTION, null, null, null);
  if (c != null) {
    mOriginalCount = c.getCount();
    c.close();
  }
}

