/**
 * Retrieve a list of lookup values for selection lists
 *
 * @param foreignKey
 * @return
 */
public ForeignKeyHolder getFKList2(String foreignKey) {
  // TODO must be changed to handle lookup tables with code - values
  // must the return both the foreign key "code" and describing text
  ForeignKeyHolder lists = new ForeignKeyHolder();
  String[] ids = new String[0];
  try {
    Cursor cursor = _db.rawQuery(foreignKey, null);
    ids = new String[cursor.getCount()];
    int i = 0;
    while (cursor.moveToNext()) {
      ids[i++] = cursor.getString(0);
    }
    cursor.close();
  } catch (Exception e) {
    Utils.showMessage("Error", e.getLocalizedMessage(), _cont);
    Utils.logE("getFKList", logging);
    Utils.printStackTrace(e, logging);
  }
  // select [id] from [foreign]
  // TODO replace field name with *
  String sql =
      foreignKey.substring(0, foreignKey.indexOf('['))
          + "*"
          + foreignKey.substring(foreignKey.indexOf(']') + 1);
  Utils.logD("SQL: " + sql, logging);
  String[] texts = new String[0];
  try {
    Cursor cursor = _db.rawQuery(sql, null);
    texts = new String[cursor.getCount()];
    int i = 0;
    int cols = cursor.getColumnCount();
    while (cursor.moveToNext()) {
      int j = 0;
      String rowText = "";
      for (j = 0; j < cols; j++) {
        rowText += cursor.getString(j);
        if (j < cols - 1) rowText += " | ";
      }
      texts[i++] = rowText;
    }
    cursor.close();
  } catch (Exception e) {
    Utils.showMessage("Error", e.getLocalizedMessage(), _cont);
    Utils.logE("getFKList", logging);
    Utils.printStackTrace(e, logging);
  }
  lists.setId(ids);
  lists.setText(texts);
  return lists;
}

@Override
protected void onActivityResult(int requestCode, int resultCode, Intent data) {
  // there are cases where the contact picker may end up sending us more than one
  // request. We want to ignore the request if we're not in the correct state.
  if (requestCode == VOICEMAIL_PROVIDER_CFG_ID) {
    boolean failure = false;

    // No matter how the processing of result goes lets clear the flag
    if (DBG) log("mVMProviderSettingsForced: " + mVMProviderSettingsForced);
    final boolean isVMProviderSettingsForced = mVMProviderSettingsForced;
    mVMProviderSettingsForced = false;

    String vmNum = null;
    if (resultCode != RESULT_OK) {
      if (DBG) log("onActivityResult: vm provider cfg result not OK.");
      failure = true;
    } else {
      if (data == null) {
        if (DBG) log("onActivityResult: vm provider cfg result has no data");
        failure = true;
      } else {
        if (data.getBooleanExtra(SIGNOUT_EXTRA, false)) {
          if (DBG) log("Provider requested signout");
          if (isVMProviderSettingsForced) {
            if (DBG) log("Going back to previous provider on signout");
            switchToPreviousVoicemailProvider();
          } else {
            final String victim = getCurrentVoicemailProviderKey();
            if (DBG) log("Relaunching activity and ignoring " + victim);
            Intent i = new Intent(ACTION_ADD_VOICEMAIL);
            i.putExtra(IGNORE_PROVIDER_EXTRA, victim);
            i.setFlags(Intent.FLAG_ACTIVITY_CLEAR_TOP);
            this.startActivity(i);
          }
          return;
        }
        vmNum = data.getStringExtra(VM_NUMBER_EXTRA);
        if (vmNum == null || vmNum.length() == 0) {
          if (DBG) log("onActivityResult: vm provider cfg result has no vmnum");
          failure = true;
        }
      }
    }
    if (failure) {
      if (DBG) log("Failure in return from voicemail provider");
      if (isVMProviderSettingsForced) {
        switchToPreviousVoicemailProvider();
      } else {
        if (DBG) log("Not switching back the provider since this is not forced config");
      }
      return;
    }
    mChangingVMorFwdDueToProviderChange = isVMProviderSettingsForced;
    final String fwdNum = data.getStringExtra(FWD_NUMBER_EXTRA);

    // TODO(iliat): It would be nice to load the current network setting for this and
    // send it to the provider when it's config is invoked so it can use this as default
    final int fwdNumTime = data.getIntExtra(FWD_NUMBER_TIME_EXTRA, 20);

    if (DBG)
      log(
          "onActivityResult: vm provider cfg result "
              + (fwdNum != null ? "has" : " does not have")
              + " forwarding number");
    saveVoiceMailAndForwardingNumber(
        getCurrentVoicemailProviderKey(),
        new VoiceMailProviderSettings(vmNum, fwdNum, fwdNumTime));
    return;
  }

  if (resultCode != RESULT_OK) {
    if (DBG) log("onActivityResult: contact picker result not OK.");
    return;
  }

  Cursor cursor = getContentResolver().query(data.getData(), NUM_PROJECTION, null, null, null);
  if ((cursor == null) || (!cursor.moveToFirst())) {
    if (DBG) log("onActivityResult: bad contact data, no results found.");
    return;
  }

  switch (requestCode) {
    case VOICEMAIL_PREF_ID:
      mSubMenuVoicemailSettings.onPickActivityResult(cursor.getString(0));
      break;
    default:
      // TODO: may need exception here.
  }
}

/**
 * Given a cursor and a set of keys, extract the required keys and values and write them to a byte
 * array.
 *
 * @param cursor A cursor with settings data.
 * @param settings The settings to extract.
 * @return The byte array of extracted values.
 */
private byte[] extractRelevantValues(Cursor cursor, String[] settings) {
  final int settingsCount = settings.length;
  byte[][] values = new byte[settingsCount * 2][]; // keys and values
  if (!cursor.moveToFirst()) {
    Log.e(TAG, "Couldn't read from the cursor");
    return new byte[0];
  }

  // Obtain the relevant data in a temporary array.
  int totalSize = 0;
  int backedUpSettingIndex = 0;
  Map<String, String> cachedEntries = new HashMap<String, String>();
  for (int i = 0; i < settingsCount; i++) {
    String key = settings[i];
    String value = cachedEntries.remove(key);

    // If the value not cached, let us look it up.
    if (value == null) {
      while (!cursor.isAfterLast()) {
        String cursorKey = cursor.getString(COLUMN_NAME);
        String cursorValue = cursor.getString(COLUMN_VALUE);
        cursor.moveToNext();
        if (key.equals(cursorKey)) {
          value = cursorValue;
          break;
        }
        cachedEntries.put(cursorKey, cursorValue);
      }
    }

    if (value == null) {
      continue;
    }

    // Write the key and value in the intermediary array.
    byte[] keyBytes = key.getBytes();
    totalSize += INTEGER_BYTE_COUNT + keyBytes.length;
    values[backedUpSettingIndex * 2] = keyBytes;

    byte[] valueBytes = value.getBytes();
    totalSize += INTEGER_BYTE_COUNT + valueBytes.length;
    values[backedUpSettingIndex * 2 + 1] = valueBytes;

    backedUpSettingIndex++;

    if (DEBUG) {
      Log.d(TAG, "Backed up setting: " + key + "=" + value);
    }
  }

  // Aggregate the result.
  byte[] result = new byte[totalSize];
  int pos = 0;
  final int keyValuePairCount = backedUpSettingIndex * 2;
  for (int i = 0; i < keyValuePairCount; i++) {
    pos = writeInt(result, pos, values[i].length);
    pos = writeBytes(result, pos, values[i]);
  }
  return result;
}

