private void fillPhoneTypeContact(final Contact contact, final Cursor cursor) {
  synchronized (contact) {
    contact.mContactMethodType = CONTACT_METHOD_TYPE_PHONE;
    contact.mContactMethodId = cursor.getLong(PHONE_ID_COLUMN);
    contact.mLabel = cursor.getString(PHONE_LABEL_COLUMN);
    contact.mName = cursor.getString(CONTACT_NAME_COLUMN);
    contact.mPersonId = cursor.getLong(CONTACT_ID_COLUMN);
    contact.mPresenceResId = getPresenceIconResourceId(cursor.getInt(CONTACT_PRESENCE_COLUMN));
    contact.mPresenceText = cursor.getString(CONTACT_STATUS_COLUMN);
    contact.mNumberE164 = cursor.getString(PHONE_NORMALIZED_NUMBER);
    contact.mSendToVoicemail = cursor.getInt(SEND_TO_VOICEMAIL) == 1;
    if (Log.isLoggable(LogTag.CONTACT, Log.DEBUG)) {
      log(
          "fillPhoneTypeContact: name="
              + contact.mName
              + ", number="
              + contact.mNumber
              + ", presence="
              + contact.mPresenceResId
              + " SendToVoicemail: "
              + contact.mSendToVoicemail);
    }
  }
  byte[] data = loadAvatarData(contact);

  synchronized (contact) {
    contact.mAvatarData = data;
  }
}

private void updateTwitter(
    String token,
    String secret,
    String accountEsid,
    int appWidgetId,
    String widget,
    long account,
    int service,
    int status_count,
    boolean time24hr,
    boolean display_profile,
    int notifications,
    HttpClient httpClient) {
  String response;
  JSONArray statusesArray;
  ArrayList<String[]> links = new ArrayList<String[]>();
  final ArrayList<String> notificationSids = new ArrayList<String>();
  mSimpleDateFormat = null;
  JSONObject statusObj;
  JSONObject friendObj;
  Cursor currentNotifications;
  String sid;
  String friend;
  MyfeedleOAuth myfeedleOAuth = new MyfeedleOAuth(TWITTER_KEY, TWITTER_SECRET, token, secret);
  // parse the response
  if ((response =
          MyfeedleHttpClient.httpResponse(
              httpClient,
              myfeedleOAuth.getSignedRequest(
                  new HttpGet(
                      String.format(TWITTER_URL_FEED, TWITTER_BASE_URL, status_count)))))
      != null) {
    // if not a full_refresh, only update the status_bg and icons
    try {
      statusesArray = new JSONArray(response);
      // if there are updates, clear the cache
      int e2 = statusesArray.length();
      if (e2 > 0) {
        removeOldStatuses(widget, Long.toString(account));
        for (int e = 0; (e < e2) && (e < status_count); e++) {
          links.clear();
          statusObj = statusesArray.getJSONObject(e);
          friendObj = statusObj.getJSONObject(Suser);
          addStatusItem(
              parseDate(statusObj.getString(Screated_at), TWITTER_DATE_FORMAT),
              friendObj.getString(Sname),
              display_profile ? friendObj.getString(Sprofile_image_url) : null,
              statusObj.getString(Stext),
              service,
              time24hr,
              appWidgetId,
              account,
              statusObj.getString(Sid),
              friendObj.getString(Sid),
              links,
              httpClient);
        }
      }
    } catch (JSONException e) {
      Log.e(TAG, service + ":" + e.toString());
    }
  }
  // notifications
  if (notifications != 0) {
    currentNotifications =
        getContentResolver()
            .query(
                Notifications.getContentUri(MyfeedleService.this),
                new String[] {Notifications.SID},
                Notifications.ACCOUNT + "=?",
                new String[] {Long.toString(account)},
                null);
    // loop over notifications
    if (currentNotifications.moveToFirst()) {
      // store sids, to avoid duplicates when requesting the latest feed
      sid = mMyfeedleCrypto.Decrypt(currentNotifications.getString(0));
      if (!notificationSids.contains(sid)) notificationSids.add(sid);
    }
    currentNotifications.close();
    // limit to oldest status
    String last_sid = null;
    Cursor last_status =
        getContentResolver()
            .query(
                Statuses.getContentUri(MyfeedleService.this),
                new String[] {Statuses.SID},
                Statuses.ACCOUNT + "=?",
                new String[] {Long.toString(account)},
                Statuses.CREATED + " ASC LIMIT 1");
    if (last_status.moveToFirst()) last_sid = mMyfeedleCrypto.Decrypt(last_status.getString(0));
    last_status.close();
    // get all mentions since the oldest status for this account
    if ((response =
            MyfeedleHttpClient.httpResponse(
                httpClient,
                myfeedleOAuth.getSignedRequest(
                    new HttpGet(
                        String.format(
                            TWITTER_MENTIONS,
                            TWITTER_BASE_URL,
                            last_sid != null
                                ? String.format(TWITTER_SINCE_ID, last_sid)
                                : "")))))
        != null) {
      try {
        statusesArray = new JSONArray(response);
        for (int i = 0, i2 = statusesArray.length(); i < i2; i++) {
          statusObj = statusesArray.getJSONObject(i);
          friendObj = statusObj.getJSONObject(Suser);
          if (!friendObj.getString(Sid).equals(accountEsid)
              && !notificationSids.contains(statusObj.getString(Sid))) {
            friend = friendObj.getString(Sname);
            addNotification(
                statusObj.getString(Sid),
                friendObj.getString(Sid),
                friend,
                statusObj.getString(Stext),
                parseDate(statusObj.getString(Screated_at), TWITTER_DATE_FORMAT),
                account,
                friend + " mentioned you on Twitter");
          }
        }
      } catch (JSONException e) {
        Log.e(TAG, service + ":" + e.toString());
      }
    }
  }
}

