/**
 * Upload a local file to this file on the distributed file system
 *
 * @param monitor
 * @param file
 */
public void upload(IProgressMonitor monitor, File file) {

  final int taskSize = 1024;

  monitor.setTaskName("Upload file " + this.path);

  BufferedInputStream istream = null;
  DataOutputStream ostream = null;

  try {
    istream = new BufferedInputStream(new FileInputStream(file));
    ostream = getDFS().create(this.path);

    int bytes;
    byte[] buffer = new byte[taskSize];

    while ((bytes = istream.read(buffer)) >= 0) {
      if (monitor.isCanceled()) return;
      ostream.write(buffer, 0, bytes);
      monitor.worked(1);
    }

  } catch (Exception e) {
    ErrorMessageDialog.display(
        String.format("Unable to uploade file %s to %s", file, this.path),
        e.getLocalizedMessage());

  } finally {
    try {
      if (istream != null) istream.close();
    } catch (IOException e) {
      e.printStackTrace();
      // nothing we can do here
    }
    try {
      if (ostream != null) ostream.close();
    } catch (IOException e) {
      e.printStackTrace();
      // nothing we can do here
    }
  }
}

private static String _runCommand(String command, OUTPUT o) throws IOException {
  DataOutputStream os = null;
  Process process = null;
  try {
    process = Runtime.getRuntime().exec(shell);
    os = new DataOutputStream(process.getOutputStream());
    InputStreamHandler sh = sinkProcessOutput(process, o);
    os.writeBytes(command + '\n');
    os.flush();
    os.writeBytes(EXIT);
    os.flush();
    process.waitFor();
    if (sh != null) {
      String output = sh.getOutput();
      Log.d(TAG, command + " output: " + output);
      return output;
    } else {
      return null;
    }
  } catch (Exception e) {
    final String msg = e.getMessage();
    Log.e(TAG, "runCommand error: " + msg);
    throw new IOException(msg);
  } finally {
    try {
      if (os != null) {
        os.close();
      }
      if (process != null) {
        process.destroy();
      }
    } catch (Exception ignored) {
    }
  }
}

/** @see org.newdawn.slick.muffin.Muffin#saveFile(java.util.HashMap, java.lang.String) */
public void saveFile(HashMap scoreMap, String fileName) throws IOException {

  PersistenceService ps;
  BasicService bs;
  URL configURL;

  try {
    ps = (PersistenceService) ServiceManager.lookup("javax.jnlp.PersistenceService");
    bs = (BasicService) ServiceManager.lookup("javax.jnlp.BasicService");
    URL baseURL = bs.getCodeBase();
    // System.out.println("CodeBase was " + baseURL);
    configURL = new URL(baseURL, fileName);
  } catch (Exception e) {
    Log.error(e);
    throw new IOException("Failed to save state: ");
  }

  try {
    ps.delete(configURL);
  } catch (Exception e) {
    Log.info("No exisiting Muffin Found - First Save");
  }

  try {
    ps.create(configURL, 1024); // 1024 bytes for our data

    FileContents fc = ps.get(configURL);
    DataOutputStream oos = new DataOutputStream(fc.getOutputStream(false));

    // scroll through hashMap and write key and value to file
    Set keys = scoreMap.keySet(); // get the keys

    // get values using keys
    for (Iterator i = keys.iterator(); i.hasNext(); ) {
      String key = (String) i.next();

      oos.writeUTF(key);

      if (fileName.endsWith("Number")) {
        double value = ((Double) scoreMap.get(key)).doubleValue();
        oos.writeDouble(value);
      } else {
        String value = (String) scoreMap.get(key);
        oos.writeUTF(value);
      }
    }

    oos.flush();
    oos.close();
  } catch (Exception e) {
    Log.error(e);
    throw new IOException("Failed to store map of state data");
  }
}

private static void addBodyIfExists(HttpURLConnection connection, Request<?> request)
    throws IOException, AuthFailureError {
  byte[] body = request.getBody();
  if (body != null) {
    connection.setDoOutput(true);
    connection.addRequestProperty(HEADER_CONTENT_TYPE, request.getBodyContentType());
    DataOutputStream out = new DataOutputStream(connection.getOutputStream());
    out.write(body);
    out.close();
  }
}

