      }
    }
  }
}

int postData(String repository, String mime) {
  try {
    URL url = new URL(repository + "/cgi-bin/upload.cgi");
    HttpURLConnection conn = (HttpURLConnection) url.openConnection();
    conn.setDoInput(true);
    conn.setDoOutput(true);
    conn.setUseCaches(false);
    conn.setRequestMethod("POST");
    conn.setRequestProperty("Connection", "Close");
    conn.setRequestProperty(
        "Content-Type",
        "multipart/form-data;boundary=-----------------------------18042893838469308861681692777");
    DataOutputStream os = new DataOutputStream(conn.getOutputStream());
    os.writeBytes(mime);
    os.flush();
    os.close();
    BufferedReader in = new BufferedReader(new InputStreamReader(conn.getInputStream()));
    String str;
    while ((str = in.readLine()) != null) {
      // ErrorMsg.reportStatus("Server: "+str);

      if (str.indexOf("<h2") > -1) {
        int st = str.indexOf(">");
        int en = str.indexOf("</h");
        if (st < 0 || en < 0)
          ErrorMsg.reportError("Error", "Can not Understand Server Response: " + str);
        else {
          if (str.indexOf("Success") > -1) {
            ErrorMsg.reportError("Info", "Patch Uploaded Successfully");
            return 1;
          } else ErrorMsg.reportError("Error", str.substring(st + 1, en));
        }
      }
    }
    in.close();
  } catch (Exception e) {
    ErrorMsg.reportStatus(e);
  }

  return 0;
}

private void createInput(Path inDir, Configuration conf) throws IOException {
  String input =
      "Hadoop is framework for data intensive distributed "
          + "applications.\n"
          + "Hadoop enables applications to work with thousands of nodes.";
  FileSystem fs = inDir.getFileSystem(conf);
  if (!fs.mkdirs(inDir)) {
    throw new IOException("Failed to create the input directory:" + inDir.toString());
  }
  fs.setPermission(inDir, new FsPermission(FsAction.ALL, FsAction.ALL, FsAction.ALL));
  DataOutputStream file = fs.create(new Path(inDir, "data.txt"));
  int i = 0;
  while (i < 1000 * 3000) {
    file.writeBytes(input);
    i++;
  }
  file.close();
}

/**
 * @param xid <description>
 * @return <description>
 * @exception javax.transaction.xa.XAException <description>
 */
public synchronized int prepare(Xid xid) throws XAException {
  System.out.println("******: " + this.getClass().getName() + ": " + "PREPARE " + xid);

  File prepared = new File(DATA_DIR + this.getClass().getName() + ".xid_");
  try {
    prepared.createNewFile();
    final int formatId = xid.getFormatId();
    final byte[] gtrid = xid.getGlobalTransactionId();
    final int gtrid_length = gtrid.length;
    final byte[] bqual = xid.getBranchQualifier();
    final int bqual_length = bqual.length;

    DataOutputStream fos = new DataOutputStream(new FileOutputStream(prepared));
    fos.writeInt(formatId);
    fos.writeInt(gtrid_length);
    fos.write(gtrid, 0, gtrid_length);
    fos.writeInt(bqual_length);
    fos.write(bqual, 0, bqual_length);
    fos.flush();
    fos.close();
  } catch (IOException e) {
    throw new XAException(XAException.XAER_RMERR);
  }
  return XA_OK;
}

