/**
 * Adding a new key-value pair to TFile.
 *
 * @param key buffer for key.
 * @param koff offset in key buffer.
 * @param klen length of key.
 * @param value buffer for value.
 * @param voff offset in value buffer.
 * @param vlen length of value.
 * @throws IOException Upon IO errors.
 *     <p>If an exception is thrown, the TFile will be in an inconsistent state. The only
 *     legitimate call after that would be close
 */
public void append(byte[] key, int koff, int klen, byte[] value, int voff, int vlen)
    throws IOException {
  if ((koff | klen | (koff + klen) | (key.length - (koff + klen))) < 0) {
    throw new IndexOutOfBoundsException("Bad key buffer offset-length combination.");
  }

  if ((voff | vlen | (voff + vlen) | (value.length - (voff + vlen))) < 0) {
    throw new IndexOutOfBoundsException("Bad value buffer offset-length combination.");
  }

  try {
    DataOutputStream dosKey = prepareAppendKey(klen);
    try {
      ++errorCount;
      dosKey.write(key, koff, klen);
      --errorCount;
    } finally {
      dosKey.close();
    }

    DataOutputStream dosValue = prepareAppendValue(vlen);
    try {
      ++errorCount;
      dosValue.write(value, voff, vlen);
      --errorCount;
    } finally {
      dosValue.close();
    }
  } finally {
    state = State.READY;
  }
}

static void configureWordCount(
    FileSystem fs,
    String jobTracker,
    JobConf conf,
    String input,
    int numMaps,
    int numReduces,
    Path inDir,
    Path outDir)
    throws IOException {
  fs.delete(outDir, true);
