private void copyBinaryFileWithDataStream(File file, String targetDirName) throws IOException {

  String fileName = file.getName();
  System.out.println("Copy file (binary) : " + targetDirName + fileName);
  DataInputStream distr = null;
  FileInputStream fis = null;

  FileOutputStream fos = null;
  DataOutputStream dos2 = null;
  try {
    fis = new FileInputStream(file);
    distr = new DataInputStream(fis);

    fos = new FileOutputStream(targetDirName + fileName);
    dos2 = new DataOutputStream(fos);

    byte nn = 0;
    boolean endSig = false;
    byte[] bytes = new byte[fis.available()];
    int n = 0;
    while (true) {
      int q = -1;
      for (int i = 0; i < bytes.length; i++) {
        try {

          bytes[i] = distr.readByte();
        } catch (EOFException ie) {
          endSig = true;
        }

        if (endSig) break;
        q = i;
      }
      if (q == (bytes.length - 1)) dos2.write(bytes);
      else for (int i = 0; i < q; i++) dos2.writeByte(bytes[i]);

      // dos2.writeByte(nn);
      if (endSig) break;
    }
  } catch (IOException cse) {
    throw new IOException("Binary File Input failure... (IOException) " + cse.getMessage());
  } catch (Exception ne) {
    throw new IOException("Binary File Input failure... (Excep) " + ne.getMessage());
  } finally {
    if (fis != null) fis.close();
    if (distr != null) distr.close();
    if (fos != null) fos.close();
    if (dos2 != null) dos2.close();
  }
}

public void serialize(OutputStream os) throws Exception {

  Queue<Node> queue = new LinkedList<Node>();
  queue.add(root);

  List<Node> nodes = new ArrayList<Node>();

  int pos = 0;
  while (queue.size() > 0) {
    Node node = queue.remove();
    nodes.add(node);

    node.pos = pos;

    // eight bytes for id length
    pos += 8;

    // eight bytes per id
    if (node.ids != null) {
      pos += node.ids.size() * 8;
    }

    // two bytes for children length
    pos += 2;

    // 10 bytes for each child (2 for the char, 8 for the pointer)
    pos += node.children.size() * 10;

    queue.addAll(node.children.values());
  }

  BufferedOutputStream bos = new BufferedOutputStream(os, 4096);
  DataOutputStream dos = new DataOutputStream(bos);

  int bytes = 0;
  for (Node node : nodes) {

    // System.out.print("pos: " + node.pos + " (" + bytes + ")");

    if (node.ids == null) {
      dos.writeLong(0);
      bytes += 8;
    } else {
      dos.writeLong(node.ids.size());
      bytes += 8;
      for (int id : node.ids) {
        dos.writeLong(id);
        bytes += 8;
      }
    }
    // System.out.print(" ids: " + node.ids);

    // System.out.print(" children: ");
    dos.writeShort(node.children.size());
    bytes += 2;
    for (char c : node.children.keySet()) {
      Node child = node.children.get(c);
      dos.writeChar(c);
      bytes += 2;
      dos.writeLong(child.pos);
      bytes += 8;
      // System.out.print(c + " " + child.pos + " ");
    }
    // System.out.println("");
  }
  bos.close();
  dos.close();
}

