/**
 * Saves or update new properties in the given file. If the file doesn't exists, a new one is
 * created.
 *
 * @param file The file to SAVE data in.
 * @param prop Data to SAVE.
 * @return true - Data have been correctly saved.\n false - A problem has occurred during the
 *     saving process.
 */
public static boolean saveProperties(final File file, final Properties prop) {
  boolean saveOk = true;
  DataOutputStream dos;
  try {
    dos = new DataOutputStream(new BufferedOutputStream(new FileOutputStream(file)));
    try {
      prop.store(dos, null); // 2nd parameter allows to store comments
    } catch (IOException e) {
      saveOk = false;
      System.err.println("IO exception during storage process: " + e.getMessage());
    }
    try {
      dos.close();
    } catch (IOException e) {
      System.err.println("IO exeption while closing the stream: " + e.getMessage());
    }
  } catch (FileNotFoundException e1) {
    saveOk = false;
    System.err.println("File wasn't found: " + e1.getMessage());
  }

  return saveOk;
}

/**
 * Connects to server and sends the json string 'outputData'
 *
 * @param path The path connecting to
 * @param gsonClientPack The json formated string to be sent to the server
 * @return String The data from the server
 */
public static String connect(String path, String gsonClientPack) {

  System.out.println("Client attempting a connection to : " + ADDRESS + path); // Debugging

  HttpURLConnection connection;
  URL link;
  InputStream input;
  String data = "";
  DataOutputStream output;

  try {
    link = new URL(ADDRESS + path);
    connection = (HttpURLConnection) link.openConnection();
    connection.setAllowUserInteraction(true);
    connection.setRequestMethod("POST");
    connection.setDoOutput(true);
    connection.setDoInput(true);
    connection.setInstanceFollowRedirects(false);

    output = new DataOutputStream(connection.getOutputStream());
    output.writeBytes(gsonClientPack);
    output.flush();
    output.close();

    try { // Try to read the data from server
      input = connection.getInputStream();
      while (input.available() <= 0) ; // no data from server, waiting...
      while (input.available() > 0) {
        int number = input.read();
        data += (char) number;
      }
      input.close();
    } catch (Exception e) {
      e.printStackTrace();
      data = gsonClientPack; // in the case of an exception, return the original pack
    }

    connection.disconnect();
  } catch (Exception e) {
    e.printStackTrace();
  }

  // DEBUGGING //

  System.out.println("Client succesfully connected to : " + ADDRESS + path); // Debugging
  System.out.println("Data To Server: " + gsonClientPack);
  System.out.println("Data From Server: " + data);
  System.out.println("---------------------------------------");

  // END //

  return data;
}

