private void check() {
  if (aos == null) aos = new AuxOutputStream();
  if (ais == null) ais = new AuxInputStream();

  byte[] aux = new byte[24];
  aos.setBuffer(aux, 0, aux.length);
  DataOutputStream dos = new DataOutputStream(aos);
  ais.setBuffer(aux, 0, aux.length);
  DataInputStream dis = new DataInputStream(ais);

  try {
    aos.setBuffer(aux, 0, aux.length);
    dos.writeShort((short) -514);
    pushShort((short) -514);
    mbuf.len = length();
    if (mbuf.len != aos.length()) throw new Error("Size mismatch in type Short");
    peek(mbuf);
    if (!equalBytes(mbuf.data, mbuf.off, aux, 0, mbuf.len))
      throw new Error("Raw contents mismatch in type Short");
    if (popShort() != -514) throw new Error("Value mismatch in type Short");

    aos.setBuffer(aux, 0, aux.length);
    dos.writeInt(-134480386);
    pushInt(-134480386);
    mbuf.len = length();
    if (mbuf.len != aos.length()) throw new Error("Size mismatch in type Int");
    peek(mbuf);
    if (!equalBytes(mbuf.data, mbuf.off, aux, 0, mbuf.len))
      throw new Error("Raw contents mismatch in type Int");
    if (popInt() != -134480386) throw new Error("Value mismatch in type Int");

    aos.setBuffer(aux, 0, aux.length);
    dos.writeLong(-119247870);
    pushLong(-119247870);
    mbuf.len = length();
    if (mbuf.len != aos.length()) throw new Error("Size mismatch in type Long");
    peek(mbuf);
    if (!equalBytes(mbuf.data, mbuf.off, aux, 0, mbuf.len))
      throw new Error("Raw contents mismatch in type Long");
    if (popLong() != -119247870) throw new Error("Value mismatch in type Long");

    aos.setBuffer(aux, 0, aux.length);
    dos.writeFloat(-134480386);
    pushFloat(-134480386);
    mbuf.len = length();
    if (mbuf.len != aos.length()) throw new Error("Size mismatch in type Float");
    peek(mbuf);
    if (!equalBytes(mbuf.data, mbuf.off, aux, 0, mbuf.len))
      throw new Error("Raw contents mismatch in type Float");
    if (popFloat() != -134480386) throw new Error("Value mismatch in type Float");

    aos.setBuffer(aux, 0, aux.length);
    dos.writeDouble(-134480386);
    pushDouble(-134480386);
    mbuf.len = length();
    if (mbuf.len != aos.length()) throw new Error("Size mismatch in type Double");
    peek(mbuf);
    if (!equalBytes(mbuf.data, mbuf.off, aux, 0, mbuf.len))
      throw new Error("Raw contents mismatch in type Double");
    if (popDouble() != -134480386) throw new Error("Value mismatch in type Double");

    aos.setBuffer(aux, 0, aux.length);
    dos.writeBoolean(true);
    pushBoolean(true);
    mbuf.len = length();
    if (mbuf.len != aos.length()) throw new Error("Size mismatch in type Boolean");
    if (popBoolean() != true) throw new Error("Value mismatch in type Boolean");

    // TODO: test String

    dos.close();
    dis.close();
  } catch (Exception ex) {
    ex.printStackTrace();
    throw new Error("DHHH");
  }

  discard(length());
  checked = true;
